pragma solidity >=0.4.16 <0.9.0;//限定solidity编译器版本

// 开发自己的代币
contract Bingo {
    // 规则1 代表币名称 需要构建 function name() public view returns (string)的方法 但是当状态变量加上public后等效
    string public name = "Bingo";
    // 规则2 代表符号 function symbol() public view returns (string) 
    string public symbol = "BVX";
    // 规则3 代表位数 function decimals() public view returns (uint8) 
    uint8 public decimals = 18;//跟以太币一样的方式 最小单位wei和ETH也是18位差别
    // 规则4 代表总量 function totalSupply() public view returns (uint256) 
    uint public totalSupply; //为了可以运算 **decimals放到构造函数里执行
    // 规则5 查询用户余额 function balanceOf(address account) public view returns (uint256)
    mapping(address => uint256) public balanceOf;//mapping类似js中的对象 address是健名 键值类型uint256 这样外界就可以 balanceOf('账户地址')获取余额
    // 规则8中授权信息的存储变量
    // 大概结构 {"某某某":{"机构A":1000,"机构B":2000},"某某某":{"机构A":1000,"机构B":2000}}
    mapping(address => mapping(address => uint256)) public allowance;//allowance代表授权信息 授权信息的存储变量
    
    // 构造函数
    constructor() {
        totalSupply = 1000000 * 10**decimals;//代表总发行量100万个BVX 后面10**decimals属于小数部分
        // 给创建者设置代币数量
        balanceOf[msg.sender] = totalSupply;
    }
    // 规则6 转账交易 function transfer(address _to,uint256 _value) public returns (bool success) 
    function transfer(address _to, uint256 _value) public returns (bool) {
        require(_to!=address(0));//require函数中条件为真就继续 否则就报错 address(0)代表空账号
        // require(balanceOf[msg.sender] >= _value);
        // balanceOf[_to] += _value;
        // balanceOf[msg.sender] -= _value;
        _transfer(msg.sender,_to,_value);
        return true;
    }


    // 封装一个内部转账函数 因为transfer transferFrom都会用到
    function _transfer(address _from, address _to, uint256 _value) internal{
        require(balanceOf[_from] >= _value);
        balanceOf[_from] -= _value;
        balanceOf[_to] += _value;

        // 触发事件
        emit Transfer(_from,_to,_value);
    }


    // 规则7 转账交易 事件 在transfer等发生调用的时候需要触发事件 记录日志 event Transfer(address indexed _from, address indexed _to, uint256 _value);
    event Transfer(address indexed _from, address indexed _to, uint256 _value);
    // 规则9 approve函数调用后必须触发Approval事件
    event Approval(address indexed _from, address indexed _spender, uint256 _value);
    // 规则8 授权给第三方机构一定数额的代币
    function approve(address _spender, uint256 _value) public returns (bool success){
        // msg.sender 当前网页登录的账号 也就是发送授权消息的账户
        // _spender 第三方的交易所账户地址
        // _value 授权的代币数量
        require(_spender!=address(0));
        allowance[msg.sender][_spender] = _value;//给当前用户中添加授权给_spender一定金额的代币
        // 触发Approval
        emit Approval(msg.sender,_spender,_value);
        return true;
    }


    // 规则9 交易所转账授权金额
    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success){
        // _from 放款账号 当初授权给当前交易所的账号
        // _to 收款账号
        // msg.sender 当前交易所地址
        require(balanceOf[_from]>=_value);//保证放款账号有钱
        require(allowance[_from][msg.sender]>=_value);//保证授权给交易所的余额足够

        allowance[_from][msg.sender] -= _value;
        // 进行转账
        _transfer(_from,_to,_value);
        return true;
    }
}
