contract FanbasePayment is Ownable {

    using SafeERC20 for IERC20;

    address ZERO_ADDRESS = address(0);

    uint256 public BONUS_PLAN_VALUE = 0;
    uint256 public MONTHLY_PROFESSIONAL_PLAN_VALUE = 19;
    uint256 public ANNUAL_PROFESSIONAL_PLAN_VALUE = 99;

    uint256 public MONTHLY_ADVANCED_PLAN_VALUE = 39;
    uint256 public ANNUAL_ADVANCED_PLAN_VALUE = 249;

    uint256 public MONTHLY_ENTERPRISE_PLAN_VALUE = 59;
    uint256 public ANNUAL_ENTERPRISE_PLAN_VALUE = 499;

    uint256 public MONTHLY_PLAN_DEADLINE = 30 days;
    uint256 public ANNUAL_PLAN_DEADLINE = 365 days;

    enum Plan { Bonus, MonthlyProfessional, MonthlyAdvanced, MonthlyEnterprise, AnnualProfessional, AnnualAdvanced, AnnualEnterprise }

    address public collector;
    address[] private payableTokens;

    mapping(Plan => uint256) public planToValue;
    mapping(Plan => uint256) public planToDeadline;

    mapping(address => Plan) public userToPlan;
    mapping(address => address) public userToPayableToken;
    mapping(address => uint8) public payableTokenToDecimal;

    mapping(bytes32 => Plan) private hashTokenToPlan;
    mapping(bytes32 => string) private hashTokenToDomain;
    mapping(address => uint256) private userToLastCollectedTime;
    mapping(address => bytes32) private userToHashToken;
    mapping(bytes32 => address) private hashTokenToUser;

    constructor(address _collector) Ownable() {
        collector = _collector;

        planToValue[Plan.Bonus] = BONUS_PLAN_VALUE;
        planToValue[Plan.MonthlyProfessional] = MONTHLY_PROFESSIONAL_PLAN_VALUE;
        planToValue[Plan.MonthlyAdvanced] = MONTHLY_ADVANCED_PLAN_VALUE;
        planToValue[Plan.MonthlyEnterprise] = MONTHLY_ENTERPRISE_PLAN_VALUE;
        planToValue[Plan.AnnualProfessional] = ANNUAL_PROFESSIONAL_PLAN_VALUE;
        planToValue[Plan.AnnualAdvanced] = ANNUAL_ADVANCED_PLAN_VALUE;
        planToValue[Plan.AnnualEnterprise] = ANNUAL_ENTERPRISE_PLAN_VALUE;

        planToDeadline[Plan.Bonus] = ANNUAL_PLAN_DEADLINE;
        planToDeadline[Plan.MonthlyProfessional] = MONTHLY_PLAN_DEADLINE;
        planToDeadline[Plan.MonthlyAdvanced] = MONTHLY_PLAN_DEADLINE;
        planToDeadline[Plan.MonthlyEnterprise] = MONTHLY_PLAN_DEADLINE;
        planToDeadline[Plan.AnnualProfessional] = ANNUAL_PLAN_DEADLINE;
        planToDeadline[Plan.AnnualAdvanced] = ANNUAL_PLAN_DEADLINE;
        planToDeadline[Plan.AnnualEnterprise] = ANNUAL_PLAN_DEADLINE;
    }

    ///////////////////////
    /////User Function/////
    ///////////////////////
    function subscribe(Plan _plan, address _payableToken) external returns(bytes32) {
        require(payableTokenToDecimal[_payableToken] > 0, "unregistered token");

        userToPlan[msg.sender] = _plan;
        userToPayableToken[msg.sender] = _payableToken;

        uint256 planValue = planToValue[_plan];
        uint256 decimals = payableTokenToDecimal[_payableToken];
        uint256 amount = planValue * 10 ** decimals;

        if(planValue > 0) {
            IERC20 token = IERC20(_payableToken);
            token.safeTransferFrom(msg.sender, address(this), amount);
        }
        
        bytes32 hashToken = generateHashToken(msg.sender);
        userToHashToken[msg.sender] = hashToken;
        hashTokenToUser[hashToken] = msg.sender;
        hashTokenToPlan[hashToken] = _plan;

        userToLastCollectedTime[msg.sender] = block.timestamp;

        return hashToken;
    }
    
    function pay() external {
        require(userToHashToken[msg.sender] != 0x00, "unsubscribed user");

        address tokenAddress = userToPayableToken[msg.sender];

        Plan userPlan = userToPlan[msg.sender];

        uint256 planValue = planToValue[userPlan];
        uint256 decimals = payableTokenToDecimal[tokenAddress];
        uint256 amount = planValue * 10 ** decimals;

        if(planValue > 0) {
            IERC20 token = IERC20(tokenAddress);
            token.safeTransferFrom(msg.sender, address(this), amount);
        }

        userToLastCollectedTime[msg.sender] = block.timestamp;
    }

    function changePayableToken(address _tokenAddress) external {
        require(userToHashToken[msg.sender] != 0x00, "unsubscribed user");
        require(userToPayableToken[msg.sender] != _tokenAddress, "can't change to same token");

        userToPayableToken[msg.sender] = _tokenAddress;
    }

    function changePlan(Plan _plan) external {
        require(userToHashToken[msg.sender] != 0x00, "unsubscribed user");
        require(userToPlan[msg.sender] != _plan, "can't change to current plan");

        uint256 planValue = planToValue[_plan];
        address tokenAddress = userToPayableToken[msg.sender];
        uint256 decimals = payableTokenToDecimal[tokenAddress];
        uint256 amount = planValue * 10 ** decimals;

        if(planValue > 0) {
            IERC20 token = IERC20(tokenAddress);
            token.safeTransferFrom(msg.sender, address(this), amount);
        }

        userToPlan[msg.sender] = _plan;
        userToLastCollectedTime[msg.sender] = block.timestamp;
    }

    function registerDomain(string memory _domain) external {
        require(userToHashToken[msg.sender] != 0x00, "unsubscribed user");

        bytes32 hashToken = userToHashToken[msg.sender];

        require(bytes32(bytes(hashTokenToDomain[hashToken])) == 0x00, "domain already registered");

        hashTokenToDomain[hashToken] = _domain;
    }

    function changeDomain(string memory _domain) external {
        require(userToHashToken[msg.sender] != 0x00, "unsubscribed user");

        bytes32 hashToken = userToHashToken[msg.sender];

        require(bytes32(bytes(hashTokenToDomain[hashToken])) != 0x00, "domain is not registered");
        require(compareString(hashTokenToDomain[hashToken], _domain) == false, "can't change to same domain");

        hashTokenToDomain[hashToken] = _domain;
    }

    function verifyHashKey(bytes32 _hashToken) external view returns (bool) {
        require(hashTokenToUser[_hashToken] != ZERO_ADDRESS, "unregistered hash token");

        address user = hashTokenToUser[_hashToken];
        Plan userPlan = userToPlan[user];
        uint256 deadline = planToDeadline[userPlan];
        uint256 lastCollectedTime = userToLastCollectedTime[user];

        bool isSubscriptionValid;

        if(block.timestamp < lastCollectedTime + deadline) {
            isSubscriptionValid = true;
        }
        else {
            isSubscriptionValid = false;
        }

        return isSubscriptionValid;
    }

    function getUserHashToken(
        bytes32 _messageHash,
        bytes memory _signature,
        address _signer
    ) external view returns (bytes32) {
        require(isValidSignature(_messageHash, _signature, _signer), "verification failed");
        
        return userToHashToken[_signer];
    }

    function getHashTokenToPlan(bytes32 _hashToken) external view returns (Plan) {
        return hashTokenToPlan[_hashToken];
    }

    function getUserLastPayedTimestamp(address _user) external view returns(uint256) {
        return userToLastCollectedTime[_user];
    }

    function gethashTokenToDomain(bytes32 _hashToken) external view returns(string memory) {
        return hashTokenToDomain[_hashToken];
    }

    function getPayableTokens() external view returns(address[] memory) {
        return payableTokens;
    }

    function getUserToDomain(address _address) external view returns (string memory) {
        bytes32 hashToken = userToHashToken[_address];

        return hashTokenToDomain[hashToken];
    }

    /////////////////////////
    /////Owner Function//////
    /////////////////////////
    function collect(address _tokenAddress) external onlyOwner {
        uint256 tokenBalance = IERC20(_tokenAddress).balanceOf(address(this));
        IERC20(_tokenAddress).transfer(collector, tokenBalance);
    }

    function addPayableToken(address _address) external onlyOwner {
        require(payableTokenToDecimal[_address] == 0, "already added");

        uint8 decimals = ERC20Detailed(_address).decimals();
        payableTokenToDecimal[_address] = decimals;

        payableTokens.push(_address);
    }

    function removePayableToken(address _address) external onlyOwner {
        require(payableTokenToDecimal[_address] > 0, "unregisgered token");

        payableTokenToDecimal[_address] = 0;

        for (uint i = 0; i < payableTokens.length; i++) {
            if (payableTokens[i] == _address) {
                payableTokens[i] = payableTokens[payableTokens.length - 1];
                payableTokens.pop();
                break;
            }
        }
    }

    function setCollector(address _address) external onlyOwner {
        require(collector != _address, "can't change to same address");
        collector = _address;
    }

    ///////////////////////////
    /////Internal Function/////
    ///////////////////////////
    function generateHashToken(
        address _user
    ) internal view returns (bytes32) {
        uint256 randomNum = uint256(keccak256(abi.encodePacked(block.timestamp, _user, block.timestamp)));
        bytes32 hashToken = keccak256(abi.encodePacked(randomNum));

        return hashToken;
    }

    function isValidSignature(
        bytes32 _messageHash,
        bytes memory _signature,
        address _signer
    ) internal pure returns (bool) {
        require(_signature.length == 65, "Invalid signature length");

        bytes32 r;
        bytes32 s;
        uint8 v;

        assembly {
            r := mload(add(_signature, 0x20))
            s := mload(add(_signature, 0x40))
            v := byte(0, mload(add(_signature, 0x60)))
        }

        bytes memory prefix = "\x19Ethereum Signed Message:\n32";
        bytes32 prefixedHash = keccak256(abi.encodePacked(prefix, _messageHash));

        return ecrecover(prefixedHash, v, r, s) == _signer;
    }

    function compareString(string memory _str1, string memory _str2) internal pure returns (bool) {
        return keccak256(abi.encodePacked(_str1)) == keccak256(abi.encodePacked(_str2));
    }
