contract Wolfies is ERC20, Ownable {
    using SafeMath for uint256;

    IUniswapV2Router02 public uniswapV2Router;
    address public  uniswapV2Pair;
    bool private swapping;
    
    address public deadWallet = 0x000000000000000000000000000000000000dEaD;
    address public GUARDIAN = address(0xF606bd19b1E61574ED625d9ea96C841D4E247A32); 
    address public constant BUSD = address(0xe9e7CEA3DedcA5984780Bafc599bD69ADd087D56); 
    uint256 public swapTokensAtAmount = 500000 * (10**18);
    mapping(address => bool) public _isBlacklisted;

    uint256 public GuardianBuyBack = 1;
    uint256 public BUSDBuyBack = 1;
    uint public BurnFee = 1;
    uint256 public totalFees = GuardianBuyBack.add(BUSDBuyBack);

    address public _treasuryWalletAddress = 0x3E422AA13fA3058FD265Eef026a8C692170556ed;
    address public _developmentWalletAddress = 0x7BcA6625e4F1B154216d622ca5702823eee750Cf;

    mapping (address => bool) private _isExcludedFromFees;
    mapping (address => bool) public automatedMarketMakerPairs;

    event UpdateUniswapV2Router(address indexed newAddress, address indexed oldAddress);
    event ExcludeFromFees(address indexed account, bool isExcluded);
    event ExcludeMultipleAccountsFromFees(address[] accounts, bool isExcluded);
    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);
    event LiquidityWalletUpdated(address indexed newLiquidityWallet, address indexed oldLiquidityWallet);

    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );

    event SendDividends(
    	uint256 tokensSwapped,
    	uint256 amount
    );


    constructor() public ERC20("Wolf Pups", "Wolfies") {

    	IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x05E61E0cDcD2170a76F9568a110CEe3AFdD6c46f);
        address _uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());

        uniswapV2Router = _uniswapV2Router;
        uniswapV2Pair = _uniswapV2Pair;

        _setAutomatedMarketMakerPair(_uniswapV2Pair, true);

        // excludeFromFees(owner(), true);
        excludeFromFees(_treasuryWalletAddress, true);
        excludeFromFees(_developmentWalletAddress, true);
        excludeFromFees(address(this), true);
        _mint(owner(), 250000000000 * (10**18));   // 50,000,000
    }

    receive() external payable {
  	}

    function updateUniswapV2Router(address newAddress) public onlyOwner {
        require(newAddress != address(uniswapV2Router), "The router already has that address");
        emit UpdateUniswapV2Router(newAddress, address(uniswapV2Router));
        uniswapV2Router = IUniswapV2Router02(newAddress);
        address _uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory())
            .createPair(address(this), uniswapV2Router.WETH());
        uniswapV2Pair = _uniswapV2Pair;
    }

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        require(_isExcludedFromFees[account] != excluded, "Account is already the value of 'excluded'");
        _isExcludedFromFees[account] = excluded;
        emit ExcludeFromFees(account, excluded);
    }

    function excludeMultipleAccountsFromFees(address[] memory accounts, bool excluded) public onlyOwner {
        for(uint256 i = 0; i < accounts.length; i++) {
            _isExcludedFromFees[accounts[i]] = excluded;
        }

        emit ExcludeMultipleAccountsFromFees(accounts, excluded);
    }

    function setSwapTokensAtAmount(uint256 amount) external onlyOwner{
        swapTokensAtAmount = amount;
    }

    function setTreasuryWallet(address payable wallet) external onlyOwner{
        _treasuryWalletAddress = wallet;
    }

    function setDevelopmentWallet(address payable wallet) external onlyOwner{
        _developmentWalletAddress = wallet;
    }

    function setGuardianBuyBack(uint256 value) external onlyOwner{
        GuardianBuyBack = value;
        totalFees = GuardianBuyBack.add(BUSDBuyBack);
    }

    function setBUSDBuyBackFee(uint256 value) external onlyOwner{
        BUSDBuyBack = value;
        totalFees = GuardianBuyBack.add(BUSDBuyBack);
    }

    function setBurnFee(uint256 value) external onlyOwner{
        BurnFee = value;
    }

    function setAutomatedMarketMakerPair(address pair, bool value) public onlyOwner {
        require(pair != uniswapV2Pair, "The PancakeSwap pair cannot be removed from automatedMarketMakerPairs");

        _setAutomatedMarketMakerPair(pair, value);
    }
    
    function blacklistAddress(address account, bool value) external onlyOwner{
        _isBlacklisted[account] = value;
    }

    function _setAutomatedMarketMakerPair(address pair, bool value) private {
        require(automatedMarketMakerPairs[pair] != value, "Automated market maker pair is already set to that value");
        automatedMarketMakerPairs[pair] = value;

        emit SetAutomatedMarketMakerPair(pair, value);
    }

    function swapOnDemand(bool _GuardianBuyBack, bool _BUSDBuyBack) external onlyOwner {
        swapping = true;

        uint256 contractTokenBalance = balanceOf(address(this));

        if (_BUSDBuyBack) {
            uint256 busdbuybackTokens = contractTokenBalance.mul(BUSDBuyBack).div(totalFees);
            sellForBUSDBuyback(busdbuybackTokens);
        }

        if (_GuardianBuyBack) {
            uint256 guardianTokens = balanceOf(address(this));
            swapAndSendToTreasury(guardianTokens);
        }

        swapping = false;
    }

    function isExcludedFromFees(address account) public view returns(bool) {
        return _isExcludedFromFees[account];
    }


    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(!_isBlacklisted[from] && !_isBlacklisted[to], 'Blacklisted address');

        if(amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

		uint256 contractTokenBalance = balanceOf(address(this));
        bool canSwap = contractTokenBalance >= swapTokensAtAmount;

        if( canSwap &&
            !swapping &&
            !automatedMarketMakerPairs[from] &&
            from != owner() &&
            to != owner()
        ) 
        {    
           swapping = true;
           uint256 busdbuybackTokens = contractTokenBalance.mul(BUSDBuyBack).div(totalFees);
           sellForBUSDBuyback(busdbuybackTokens);
           uint256 sellForGuardianBuyback = balanceOf(address(this));
           swapAndSendToTreasury(sellForGuardianBuyback);
           swapping = false;
        }

        bool takeFee = !swapping;
        if(_isExcludedFromFees[from] || _isExcludedFromFees[to]) {
            takeFee = false;
        }

        if(takeFee) {
        	uint256 fees = amount.mul(totalFees).div(100);
        	if(automatedMarketMakerPairs[to]){
        	    fees += amount.mul(1).div(100);
        	}
            uint burnamount = amount.mul(BurnFee).div(100);
            super._transfer(from, deadWallet, burnamount);
        	amount = amount.sub(fees).sub(burnamount);
            super._transfer(from, address(this), fees);
        }

        super._transfer(from, to, amount);
    }

    function swapAndSendToTreasury(uint256 tokens) private {
        swapTokens(tokens,GUARDIAN);
        uint256 newBalance = IERC20(GUARDIAN).balanceOf(address(this));
        IERC20(GUARDIAN).transfer(_treasuryWalletAddress, newBalance);
    }

    function sellForBUSDBuyback(uint256 tokens) private {
        // swapTokensForBNB(tokens);
        // uint256 balance = address(this).balance;
        // payable(_developmentWalletAddress).transfer(balance);
        swapTokens(tokens,BUSD);
        uint256 newBalance = IERC20(BUSD).balanceOf(address(this));
        IERC20(BUSD).transfer(_developmentWalletAddress, newBalance);

    }    
 
    function swapTokensForBNB(uint256 tokenAmount) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, 
            path,
            address(this),
            block.timestamp
        );
    }


    function swapTokens(uint256 tokenAmount, address _to) private {

        address[] memory path = new address[](3);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();
        path[2] = _to;

        _approve(address(this), address(uniswapV2Router), tokenAmount);
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(this),
            block.timestamp
        );
    }


    function updateGuardianToken(address _erc20Token) external onlyOwner {
       IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x05E61E0cDcD2170a76F9568a110CEe3AFdD6c46f);
        address getV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .getPair(address(_erc20Token), _uniswapV2Router.WETH());

        require(getV2Pair!=0x0000000000000000000000000000000000000000, "Invalid Token"); 
        GUARDIAN = _erc20Token;
    } 


    function recoverWrongTokens(address _tokenAddress, uint256 _tokenAmount) external onlyOwner {
        IERC20(_tokenAddress).transfer(address(msg.sender), _tokenAmount);
    }

    function migrateBnb(address payable _newadd,uint256 amount) public onlyOwner {
        (bool success, ) = address(_newadd).call{ value: amount }("");
        require(success, "Address: unable to extract value, tx may have reverted");    
    }

