pragma solidity 0.8.17;

import { IERC20 } from "./IERC20.sol";
import { SafeERC20 } from "./SafeERC20.sol";
import { ReentrancyGuard } from "./ReentrancyGuard.sol";
import { Ownable } from "./Ownable.sol";

import { ITokenPresale } from "./ITokenPresale.sol";
import { PaymentToken } from "./PaymentToken.sol";
import { ChainlinkPriceOracle } from "./ChainlinkPriceOracle.sol";

/**
 * @title SuperManicTokenPresale
 */
contract SuperManicTokenPresale is ITokenPresale, PaymentToken, Ownable, ReentrancyGuard, ChainlinkPriceOracle {
    using SafeERC20 for IERC20;

    uint256 public tokenPrice;
    uint256 public tokensSold;
    uint256 public minPurchase;
    uint256 public raised;
    address public immutable presaleToken;
    address public beneficiary;
    bool public isFinalized;

    string private constant ZERO_PRICE = "Presale: zero price";
    string private constant ZERO_ADDRESS = "Presale: zero address";
    string private constant NOT_FINALIZE = "Presale: not finalize yet";
    string private constant ZERO_CONTRIBUTION = "Presale: zero contribution";
    string private constant MINIMUM_PURCHASE = "Presale: smaller than minimum";
    string private constant INSUFFICIENT_BALANCE = "Presale: insufficient balance";
    string private constant INSUFFICIENT_TOKEN = "Presale: insufficient tokens left";
    string private constant INSUFFICIENT_FUNDS = "Presale: not enough withdrawable funds";

    mapping(address => uint256) public contributions; // user => bought tokens
    mapping(address => uint256) public tokenRaised; // token => amount
    mapping(address => mapping(address => uint256)) public tokenBought; // user => token => amount

    modifier onlyFinalized() {
        require(isFinalized, NOT_FINALIZE);
        _;
    }

    receive() external payable {}

    fallback() external payable {}

    /**
     * @dev Creates a presale contract.
     * @param presaleToken_ address of the ERC20 token contract
     * @param tokenPrice_ presale token price
     * @param beneficiary_ address of the presale beneficiary
     */
    constructor(address presaleToken_, uint256 tokenPrice_, address beneficiary_) {
        require(presaleToken_ != address(0), ZERO_ADDRESS);
        require(beneficiary_ != address(0), ZERO_ADDRESS);
        require(tokenPrice_ > 0, ZERO_PRICE);
        presaleToken = presaleToken_;
        tokenPrice = tokenPrice_;
        beneficiary = beneficiary_;
        address[] memory tokens = new address[](2);
        tokens[0] = address(0);
        tokens[1] = 0xdAC17F958D2ee523a2206206994597C13D831ec7;
        _acceptPaymentTokens(tokens);
    }

    function acceptPaymentTokens(address[] calldata addresses_) external onlyOwner {
        _acceptPaymentTokens(addresses_);
    }

    function cancelPaymentTokens(address[] calldata addresses_) external onlyOwner {
        _cancelPaymentTokens(addresses_);
    }

    function finalize(bool status_) external onlyOwner {
        isFinalized = status_;
    }

    function setBeneficiary(address beneficiary_) external onlyOwner {
        require(beneficiary_ != address(0), ZERO_ADDRESS);
        beneficiary = beneficiary_;
        emit SetBeneficiary(beneficiary_);
    }

    function setMinPursechase(uint256 minPurchase_) external onlyOwner {
        require(minPurchase_ > 0, ZERO_PRICE);
        minPurchase = minPurchase_;
        emit SetMinPurchase(minPurchase_);
    }

    function setTokenPrice(uint256 newPrice_) external onlyOwner {
        require(newPrice_ > 0, ZERO_PRICE);
        tokenPrice = newPrice_;
        emit SetPrice(newPrice_);
    }

    function setPriceFeeds(address token_, address priceFeed_) external onlyOwner {
        _setPriceFeeds(token_, priceFeed_);
    }

    function buy(
        address paymentToken_,
        uint256 paymentAmount_
    ) external payable onlyAcceptedToken(paymentToken_) nonReentrant {
        address sender = _msgSender();
        uint256 usdAmount = _getUsdAmount(paymentToken_, paymentAmount_);
        uint256 tokenAmount = _getTokenPresaleAmount(usdAmount);

        _preValidate(sender, paymentToken_, paymentAmount_, usdAmount);

        _buy(sender, tokenAmount, paymentToken_, paymentAmount_);

        emit TokensPurchased(sender, beneficiary, paymentToken_, paymentAmount_, tokenAmount);
    }

    function buyExactTokens(
        address paymentToken_,
        uint256 tokenAmount_
    ) external payable onlyAcceptedToken(paymentToken_) nonReentrant {
        address sender = _msgSender();
        uint256 usdAmount = _getTokenPresalePrice(tokenAmount_);
        uint256 paymentAmount = _getTokenAmount(paymentToken_, usdAmount);

        _preValidate(sender, paymentToken_, paymentAmount, usdAmount);
        _buy(sender, tokenAmount_, paymentToken_, paymentAmount);

        emit TokensPurchased(sender, beneficiary, paymentToken_, paymentAmount, tokenAmount_);
    }

    function claim() external nonReentrant onlyFinalized {
        _claim(msg.sender);
    }

    function ownerClaim(address[] calldata beneficiaries_) external nonReentrant onlyFinalized onlyOwner {
        uint256 length = beneficiaries_.length;

        for (uint256 i = 0; i < length; ) {
            _claim(beneficiaries_[i]);
            unchecked {
                ++i;
            }
        }
    }

    function withdrawToken(address token_, uint256 amount_) external onlyOwner {
        if (token_ == presaleToken) {
            require(this.getWithdrawableAmount() >= amount_, INSUFFICIENT_FUNDS);
        }
        __transfer(token_, _msgSender(), amount_);
        emit Withdraw(token_, amount_);
    }

    function getWithdrawableAmount() public view returns (uint256) {
        return IERC20(presaleToken).balanceOf(address(this)) - tokensSold;
    }

    function getRaised() external view returns (uint256, uint256) {
        return (raised, raised + (getWithdrawableAmount() * tokenPrice) / 1 ether);
    }

    function getTokenPresaleAmount(address paymentToken, uint256 paymentAmount) external view returns (uint256) {
        uint256 usdAmount = _getUsdAmount(paymentToken, paymentAmount);
        uint256 tokenAmount = _getTokenPresaleAmount(usdAmount);
        return tokenAmount;
    }

    function getTokenPresalePrice(address paymentToken, uint256 tokenReceiveAmount) external view returns (uint256) {
        uint256 usdAmount = _getTokenPresalePrice(tokenReceiveAmount);
        uint256 paymentAmount = _getTokenAmount(paymentToken, usdAmount);
        return paymentAmount;
    }

    function _getTokenPresaleAmount(uint256 usdAmount) private view returns (uint256) {
        return (usdAmount * 1 ether) / tokenPrice;
    }

    function _getTokenPresalePrice(uint256 tokenAmount) private view returns (uint256) {
        return (tokenAmount * tokenPrice) / 1 ether;
    }

    function _preValidate(address buyer_, address paymentToken_, uint256 paymentAmount_, uint256 usdtAmount_) private {
        require(usdtAmount_ >= minPurchase, MINIMUM_PURCHASE);
        if (paymentToken_ == address(0)) {
            require(msg.value >= paymentAmount_, INSUFFICIENT_BALANCE);
            __transfer(address(0), beneficiary, paymentAmount_);
        } else {
            IERC20(paymentToken_).safeTransferFrom(buyer_, beneficiary, paymentAmount_);
        }
        raised += usdtAmount_;
    }

    function _buy(address buyer_, uint256 tokenAmount_, address paymentToken_, uint256 paymentAmount_) private {
        uint256 tokenBalance = IERC20(presaleToken).balanceOf(address(this));
        tokensSold += tokenAmount_;
        require(tokensSold <= tokenBalance, INSUFFICIENT_TOKEN);
        contributions[buyer_] += tokenAmount_;
        tokenRaised[paymentToken_] += paymentAmount_;
        tokenBought[buyer_][paymentToken_] += paymentAmount_;
    }

    function _claim(address receiver) private {
        uint256 contributionAmount = contributions[receiver];
        require(contributionAmount > 0, ZERO_CONTRIBUTION);
        contributions[receiver] = 0;
        IERC20(presaleToken).safeTransfer(receiver, contributionAmount);
        emit Released(receiver, contributionAmount);
    }

    function __transfer(address asset_, address to_, uint256 amount_) private {
        if (amount_ == 0) return;
        if (asset_ == address(0)) {
            // solhint-disable-next-line
            (bool ok, ) = payable(to_).call{ value: amount_ }("");
            require(ok, "INSUFICIENT_BALANCE");
        } else IERC20(asset_).safeTransfer(to_, amount_);
    }
}"}}
