contract KaBoSu is ERC20, Ownable {

    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;
    bool private swapping;
    bool public enabelAddLp;

    address public constant ROUTER = 0x10ED43C718714eb63d5aA57B78B54704E256024E;
    address public constant USDT = 0x55d398326f99059fF775485246999027B3197955;
    address public constant BTS = 0xd6d2F886f8064ec538ef18ca2B4Ad61342B99999;
    address public marketingAddr = 0x40E77a2a2cfb9555706C4A2D7CD36630697B68E4;
    address public tokenOwner = 0x54d9153066FA4923cE4693227BB96c6b2Db96f88;

    uint256 public numTokensSellToSwap = 21000 * 1e18;
    uint public airdropNum = 5;
    uint public maxToken = 30000 * 1e18;
    uint256 public startTime;
    
    uint public minUsdt = 45 * 1e18;
    uint public lastBuyTime;
    address public curWinner;
    address public lastWinner;
    uint public lastRewards;
    
    uint256 public buyBTSFee = 1;
    uint256 public buyMarketingFee = 1;
    uint256 public buyVaultFee = 3;
    
    uint256 public sellBTSFee = 1;
    uint256 public sellMarketingFee = 1;
    uint256 public sellVaultFee = 3;


    // exlcude from fees and max transaction amount
    mapping (address => bool) private isExcludedFromFees;
    mapping (address => bool) public isBlclist;
    mapping (address => bool) public isExemptMaxToken;
    mapping (address => bool) public isWhitelist;

    modifier lockTheSwap {
        swapping = true;
        _;
        swapping = false;
    }

    constructor() ERC20("KaBoSu", "KaBoSu") {
        
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(ROUTER);
        address _WETH = _uniswapV2Router.WETH();
         // Create a uniswap pair for this new token
        address _uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _WETH);

        uniswapV2Router = _uniswapV2Router;
        uniswapV2Pair = _uniswapV2Pair;

        // exclude from paying fees or having max transaction amount
        isExcludedFromFees[owner()] = true;
        isExcludedFromFees[marketingAddr] = true;
        isExcludedFromFees[tokenOwner] = true;
        isExcludedFromFees[address(this)] = true;

        isExemptMaxToken[address(this)] = true;
        isExemptMaxToken[address(0)] = true;
        isExemptMaxToken[address(0xdead)] = true;
        isExemptMaxToken[_uniswapV2Pair] = true;
        isExemptMaxToken[ROUTER] = true;
        isExemptMaxToken[tokenOwner] = true;
        isExemptMaxToken[marketingAddr] = true;

        isWhitelist[tokenOwner] = true;
        isWhitelist[marketingAddr] = true;

        _approve(address(this), ROUTER, type(uint).max);
        IERC20(USDT).approve(ROUTER, type(uint).max);
        lastBuyTime = block.timestamp;
        /*
            _mint is an internal function in ERC20.sol that is only called here,
            and CANNOT be called ever again
        */
        _mint(tokenOwner, 21000000 * 1e18);
    }

    receive() external payable {}

    function startTrade() external onlyOwner {
        startTime = block.timestamp;
    }

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        isExcludedFromFees[account] = excluded;
    }

    function excludeMultipleAccountsFromFees(address[] calldata accounts, bool excluded) public onlyOwner {
        for(uint256 i = 0; i < accounts.length; i++) {
            isExcludedFromFees[accounts[i]] = excluded;
        }
    }

    function setWhitelist(address account, bool flag) external onlyOwner {
        isWhitelist[account] = flag;
    }

    function setMultipleWhitelist(address[] calldata accounts, bool flag) public onlyOwner {
        for(uint256 i = 0; i < accounts.length; i++) {
            isWhitelist[accounts[i]] = flag;
        }
    }

    function setEnabelAddLp(bool _enabelAddLp) external onlyOwner {
        enabelAddLp = _enabelAddLp;
    }

    function setExemptMaxToken(address account, bool excluded) public onlyOwner {
        isExemptMaxToken[account] = excluded;
    }

    function setMinUsdt(uint256 _minUsdt) external onlyOwner {
        minUsdt = _minUsdt;
    }

    function setMaxToken(uint256 _maxToken) external onlyOwner {
        maxToken = _maxToken;
    }

    function setBlcList(address account, bool flag) external onlyOwner {
        isBlclist[account] = flag;
    }

    function setMultipleBlcList(address[] calldata accounts, bool excluded) external onlyOwner {
        for(uint256 i = 0; i < accounts.length; i++) {
            isBlclist[accounts[i]] = excluded;
        }
    }

    function setAirdropNum(uint _airdropNum) external onlyOwner {
        airdropNum = _airdropNum;
    }

    function setMarketingAddr(address _marketWallet) external onlyOwner {
        marketingAddr = _marketWallet;
    }

    function setBuyBTSFee(uint256 _buyNFTFee) external onlyOwner {
        buyBTSFee = _buyNFTFee;
    }

    function setBuyMarketingFee(uint256 _buyMarketingFee) external onlyOwner {
        buyMarketingFee = _buyMarketingFee;
    }

    function setBuyVaultFee(uint256 _buyVaultFee) external onlyOwner {
        buyVaultFee = _buyVaultFee;
    }

    function setSellBTSFee(uint256 _sellNFTFee) external onlyOwner {
        sellBTSFee = _sellNFTFee;
    }

    function setSellMarketingFee(uint256 _sellMarketingFee) external onlyOwner {
        sellMarketingFee = _sellMarketingFee;
    }

    function setSellVaultFee(uint256 _sellVaultFee) external onlyOwner {
        sellVaultFee = _sellVaultFee;
    }

    function setNumTokensSellToSwap(uint256 value) external onlyOwner {
        numTokensSellToSwap = value;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(!isBlclist[from], "blclist");

        if (amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        if (!swapping) {
            processVault(from, to, amount);
        }
        
        uint256 contractTokenBalance = balanceOf(address(this));
        bool overMinTokenBalance = contractTokenBalance >= numTokensSellToSwap;
        if( overMinTokenBalance &&
            !swapping &&
            from != uniswapV2Pair
        ) {
            swapAndDividend(numTokensSellToSwap);
        } 

        bool takeFee = !swapping;

        // if any account belongs to _isExcludedFromFee account then remove the fee
        if(isExcludedFromFees[from] || isExcludedFromFees[to]) {
            takeFee = false;
        } else{
            if (isAddLp(to) || (from != uniswapV2Pair && to != uniswapV2Pair)) {
                require(enabelAddLp, "not time");
            }
        }

        //transfer amount, it will take tax, burn, liquidity fee
        _tokenTransfer(from,to,amount,takeFee); 

        if (!isExemptMaxToken[to]) {
            require(balanceOf(to) <= maxToken, "max token limit");
        }

        if (from == uniswapV2Pair && !isWhitelist[to]) {
            require(startTime > 0, "not time");
        }
    }

    function processVault(address from, address to, uint amount) private {
        if (block.timestamp - lastBuyTime >= 30 minutes && curWinner != address(0)) {
            uint reward = IERC20(USDT).balanceOf(address(this)) / 2;
            IERC20(USDT).transfer(curWinner, reward);
            lastWinner = curWinner;
            lastRewards = reward;
            curWinner = address(0);
        }
        if (from == uniswapV2Pair && to != ROUTER) {
            address[] memory path = new address[](3);
            path[0] = USDT;
            path[1] = uniswapV2Router.WETH();
            path[2] = address(this);
            uint[] memory amounts = uniswapV2Router.getAmountsIn(amount, path);
            if (amounts[0] >= minUsdt) {
                curWinner = to;
                lastBuyTime = block.timestamp;
            }
        }    
    }

    //this method is responsible for taking all fee, if takeFee is true
    function _tokenTransfer(address sender, address recipient, uint256 amount, bool takeFee) private {
        if(takeFee) {
            if (airdropNum > 0 && balanceOf(address(this)) >= airdropNum * 1e12) {       
                for(uint i; i < airdropNum ; i++){
                    super._transfer(address(this), address(uint160(uint(keccak256(abi.encodePacked(i, balanceOf(address(this)), block.timestamp))))), 1e12);
                }
            }
            uint256 totalFee;
            if(sender == uniswapV2Pair) { //buy
                totalFee = buyBTSFee + buyMarketingFee + buyVaultFee;
            } else if (recipient == uniswapV2Pair) {
                totalFee = sellBTSFee + sellMarketingFee + sellVaultFee;
            } 

            if(totalFee > 0) {
                uint256 feeAmount = amount * totalFee / 100;
                super._transfer(sender, address(this), feeAmount);
                amount -= feeAmount;
            }
        }
        super._transfer(sender, recipient, amount);
    }

    function swapAndDividend(uint256 tokenAmount) private lockTheSwap {
        uint totalBuyShare = buyBTSFee + buyMarketingFee + buyVaultFee;
        uint totalSellShare = sellBTSFee + sellMarketingFee + sellVaultFee;

        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](3);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();
        path[2] = USDT;

        uint256 initialBalance = IERC20(USDT).balanceOf(address(this));
        // make the swap
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of USDT
            path,
            address(this),
            block.timestamp
        );
        uint256 newBalance = IERC20(USDT).balanceOf(address(this)) - initialBalance;
        uint256 bToM = newBalance * (buyMarketingFee + sellMarketingFee) / (totalBuyShare + totalSellShare);
        uint256 bToBTS = newBalance * (buyBTSFee + sellBTSFee) / (totalBuyShare + totalSellShare);
        IERC20(USDT).transfer(marketingAddr, bToM);
        
        swapBTS(bToBTS);
    }

    function swapBTS(uint usdtAmount) private {
        address[] memory path = new address[](3);
        path[0] = USDT;
        path[1] = uniswapV2Router.WETH();
        path[2] = BTS;

        // make the swap
