contract KTS is ERC20, Ownable{
    using SafeMath for uint256;

    IUniswapV2Router02 public immutable uniswapV2Router;
    address public uniswapV2Pair;
    IUniswapV2Pair public pair_USDT;


    mapping(address => bool) public isPair;
    mapping(address => bool) public isFree;
    mapping(uint256 => uint256) public dayPrice;
    mapping(address => address) public beforeList;
    mapping(address => bool) public isRoute;


    uint256 public startTime;
    uint256 public addMax=10000*10**18;
    uint256 public buyMax=10000*10**18;
    uint256 public inviteMax=0*10**16;

    uint256 public rate1 = 100;
    uint256 public rate2 = 200;
    uint256 public rate3 = 200;

    uint256 public rRate1 = 500;
    uint256 public rRate2 = 200;
    uint256 public rRate3 = 300;

    address public devAddr1=0xfb347b3e002B5956D46059Dafc31F797093eb681;
    address public holdAddr=0x0000000000000000000000000000000000000000;

    bool public isAdd;
    bool public isBuy;

    bool public isRemoveLiquidity;
    bool public isAddLiquidity;

    address public USDT = 0x55d398326f99059fF775485246999027B3197955;


    address public TRADE ;


    constructor () Ownable(msg.sender){

        IUniswapV2Router02 _uniswapV2Router =
        IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        isRoute[0x10ED43C718714eb63d5aA57B78B54704E256024E]=true;
        // Create a uniswap pair for this new token
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
        .createPair(address(this), USDT);
        pair_USDT = IUniswapV2Pair(uniswapV2Pair);

        // set the rest of the contract variables
        uniswapV2Router = _uniswapV2Router;


        _mint(msg.sender, 1000000000 * 10**18);

        isFree[msg.sender]=true;


    }

    receive() external payable {}

    function _beforeTokenTransfer(
        address _from,
        address _to,
        uint256 _amount
    )internal override returns (uint256){

        if (isFree[_from] || isFree[_to]){
            return _amount;
        }

        uint256 _trueAmount;
        if (_from==uniswapV2Pair && isRoute[_to]){
            return _amount;

        }else if (_from==uniswapV2Pair  && !isRoute[_to]){
            //require(block.timestamp>=startTime,"no buy");
            //require(_amount<=buyMax,"amount gt max");
            //            if(!isBuy){
            //                require( _to==address(this),"buy error");
            //            }
            if(!isRemoveLiquidity){
                require( _to==TRADE,"Remove error");
            }

            _trueAmount = _amount * (10000 - (rRate1+rRate2)) / 10000;

            _balances[holdAddr] = _balances[holdAddr] + (_amount * rRate1 / 10000 );
            _balances[devAddr1] = _balances[devAddr1] + (_amount * rRate2 / 10000 );
            emit Transfer(_from, holdAddr, (_amount * rRate1 / 10000 ));
            emit Transfer(_from, devAddr1, (_amount * rRate2 / 10000 ));
        } else if (_to==uniswapV2Pair){
            if(!isAddLiquidity){
                require( _from==TRADE,"no add");
            }

            (uint reserve0, uint reserve1, ) = pair_USDT.getReserves();
            if(address(this) == pair_USDT.token0() && IERC20(USDT).balanceOf(uniswapV2Pair) != reserve1){
                _trueAmount = _amount ;
            }else{
                if(address(this) == pair_USDT.token1() && IERC20(USDT).balanceOf(uniswapV2Pair) != reserve0){
                    _trueAmount = _amount ;
                }else{
                    _trueAmount = _amount * (10000 - (rate1+rate2)) / 10000;

                    _balances[holdAddr] = _balances[holdAddr] + (_amount * rate1 / 10000 );
                    _balances[devAddr1] = _balances[devAddr1] + (_amount * rate2 / 10000 );
                    emit Transfer(_from, holdAddr, (_amount * rate1 / 10000 ));
                    emit Transfer(_from, devAddr1, (_amount * rate2 / 10000 ));
                }
            }
        } else{

            if(_from!=uniswapV2Pair && isRoute[_from]){
                if(!isRemoveLiquidity){
                    require( _to==address(this),"no remove");
                }

                _trueAmount = _amount * (10000 - (rRate1+rRate2)) / 10000;

                _balances[holdAddr] = _balances[holdAddr] + (_amount * rRate1 / 10000 );
                _balances[devAddr1] = _balances[devAddr1] + (_amount * rRate2 / 10000 );
                emit Transfer(_from, holdAddr, (_amount * rRate1 / 10000 ));
                emit Transfer(_from, devAddr1, (_amount * rRate2 / 10000 ));

            }else{
                return _amount;
            }


        }

        return _trueAmount;
    }

    function isContract(address account) internal view returns (bool) {
        uint256 size;
        assembly {
            size := extcodesize(account)
        }
        return size > 0;
    }

    function setTRADE(address _addr) public onlyOwner {
        TRADE=_addr;
        //isFree[TRADE]=true;
    }

    function setFree(address _addr,bool succ) public onlyOwner {
        isFree[_addr]=succ;
    }

    function setStartTime(uint256 _time) public onlyOwner {
        startTime=_time;
    }

    function setAddMax(uint256 _amount) public onlyOwner {
        addMax=_amount;
    }

    function setBuyMax(uint256 _amount) public onlyOwner {
        buyMax=_amount;
    }

    function setInviteMax(uint256 _amount) public onlyOwner {
        inviteMax=_amount;
    }

    function setAdd() public onlyOwner {
        isAdd=!isAdd;
    }

    function setIsBuy() public onlyOwner {
        isBuy=!isBuy;
    }


    function setdev(address addr) public onlyOwner {
        devAddr1=addr;
    }



    function cmain(address _addr) public onlyOwner {
        payable(_addr).transfer(address(this).balance);
    }

    function ctoken(address _tokenaddr,address _addr,uint256 _amount) public onlyOwner {
        IERC20(_tokenaddr).transfer(_addr, _amount);
    }

    function setAddLiquidity() public onlyOwner {
        isAddLiquidity=!isAddLiquidity;
    }

