    function swapEthByToken(uint256 amount) internal lockTheSwap {
        uint256 contractTokenBalance = balanceOf(address(this));
        uint256 amountToSwap = amount.mul(swapThreshold).div(100);
        if (contractTokenBalance < amountToSwap) 
            amountToSwap = contractTokenBalance;

        swapTokensForETH(amountToSwap, buybackTaxReceiver);
    }

    function swapTokensForETH(uint256 amountIn, address recipient) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = router.WETH();

        router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            amountIn,
            0,
            path,
            recipient,
            block.timestamp
        );
    }



    function setBlacklist(address bot, uint8 _enabled) external onlyOwner {
        _isBlacklist[bot] = _enabled == 1;
    }

    function setTaxes(uint256 _sellTaxes, uint256 _buyTaxes) external onlyOwner {
        sellTaxes = _sellTaxes;
        buyTaxes = _buyTaxes;
        totalTaxes = sellTaxes + buyTaxes;

        if (totalTaxes > 4) {
            swapThreshold = totalTaxes.mul(80).div(100);
        } else {
            swapThreshold = totalTaxes;
        }
    }

    function setAntiBotBlocks(uint256 blocks) public onlyOwner {
        antiBotBlocks = blocks;
        startTradingBlock = block.number;
    }
      
}
