pragma solidity 0.8.7;





contract HeroNFT is ERC721, ERC721Burnable, ERC721Enumerable {

    mapping(address => bool) public operators;
    string public baseUri;

    uint256 public currentId;
    mapping(uint256 => Info) public tokenInfos;
    uint256 maxTimeSummon = 7;

    struct Info {
        uint256 raceId;
        uint256 rarity;
        uint256[4] genes;
        uint256 timeOfSummon;
        uint256 lastTimeSummon;
        uint256 parentA;
        uint256 parentB; 
        uint256 birthTime;
        uint256 generation;
        uint256 star;
    }

    modifier onlyOperator {
        require(operators[_msgSender()], "Require permission");
        _;
    }

    event HeroMint(address to, uint256 tokenId);
    // event HeroInfoUpdated(uint256 tokenId, Info info);
    event UpdateBaseUri(string baseUri);
    event HeroInfoUpdated(uint256 tokenId, Info info);

    constructor(string memory _baseUri) ERC721("Hero NFT in Heroes of the lands", "HERO"){
       operators[_msgSender()] = true;
       baseUri = _baseUri;
    }

    function setOperatorPermission(address _operators, bool _isOperator) external onlyOperator {
        operators[_operators] = _isOperator;
    }

    function setBaseUri(string calldata _baseUri) external onlyOperator {
        baseUri = _baseUri;
        emit UpdateBaseUri(_baseUri);
    }

    function mint(address _to) external onlyOperator returns(uint256) {
       _safeMint(_to, ++currentId);
       emit HeroMint(_to, currentId);
       return currentId;
    }

    function updateRarityHeroInfo(uint256 _tokenId, uint256 _rarity) external onlyOperator{
        Info storage info = tokenInfos[_tokenId];
        info.rarity = _rarity;

        emit HeroInfoUpdated(_tokenId, info);
    }

    function updateStarHeroInfo(uint256 _tokenId, uint256 _star) external onlyOperator{
        Info storage info = tokenInfos[_tokenId];
        info.star = _star;

        emit HeroInfoUpdated(_tokenId, info);
    }

    function setInfo(uint256 _tokenId, uint256 _rarity, uint256 _raceId, uint256[4] calldata _genes, uint256 _timeOfSummon, uint256 _lastTimeSummon, uint256 _parentA, uint256 _parentB, uint256 _birthTime, uint256 _generation, uint256 _star) external onlyOperator {
        Info memory info = Info({
            raceId: _raceId,
            rarity: _rarity,
            genes: _genes,
            timeOfSummon: _timeOfSummon,
            lastTimeSummon: _lastTimeSummon,
            parentA: _parentA,
            parentB: _parentB,
            birthTime: _birthTime,
            generation: _generation,
            star: _star
        });

        tokenInfos[_tokenId] = info;

        emit HeroInfoUpdated(_tokenId, info);
    }

    function getGenesHero(uint256 _tokenId) external view returns(uint256[4] memory){
        return tokenInfos[_tokenId].genes;
    }

    function getInfo(uint256 _tokenId) external view returns(Info memory){
        return tokenInfos[_tokenId];
    }

    function getOwnedHeroes(address user) public view returns(uint256[] memory){
        uint256 balance = balanceOf(user);
        uint256[] memory heroeIds = new uint256[](balance);
        for(uint256 i = 0; i < balance; i++){
            heroeIds[i] = tokenOfOwnerByIndex(user,i);
        }

        return heroeIds;
    }

    function tokenURI(uint256 _tokenId) public view virtual override returns (string memory){
        require(_exists(_tokenId), "");
        if(bytes(baseUri).length > 0){
            return string(abi.encodePacked(baseUri, Strings.toString(_tokenId)));
        }else{
            return "";
        }
    }

    function _beforeTokenTransfer(address from, address to, uint256 tokenId) internal override(ERC721, ERC721Enumerable)
    {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    function supportsInterface(bytes4 interfaceId) public view override(ERC721, ERC721Enumerable) returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}
