        uint256 b,
        string memory errorMessage
    ) internal pure returns (uint256) {
        unchecked {
            require(b > 0, errorMessage);
            return a % b;
        }
    }
}

contract TCA is BEP20 {
    uint256 private initialSupply;
    uint256 public max_supply = 40000000 * (10**9);

    using SafeMath for uint256;
    uint256 public totalMint;

    constructor() BEP20("Telecom Company Alliance", "TCA", 9) {
        initialSupply = 110000000 * (10**9);
        _mint(owner(), initialSupply);
    }

    function mint(uint256 amount) public onlyOwner {
        // require(totalMint < 40000000 * (10**9), "Error: Limit has reached");
        _mint(owner(), amount);
        totalMint += amount;
        initialSupply += amount;
    }

