{{
  "language": "Solidity",
  "sources": {
    "contracts/XVGApes.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/interfaces/IERC2981.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/introspection/ERC165Storage.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableMap.sol";

// coded by Crypto Tester: https://twitter.com/crypto_tester_

contract XVGApes is ERC721,
  ERC721Enumerable,
  ERC721URIStorage,
  Ownable,
  AccessControl,
  ERC165Storage,
  IERC2981 {

  using EnumerableMap for EnumerableMap.UintToAddressMap;

  uint256 public mintCount;
  uint256 public standardMintCount;
  uint256 public personalizedMintCount;
  uint256 public adminMintCount;
  uint256 public adminStandardMintCount;
  uint256 public adminPersonalizedMintCount;
  uint256 public xvgStandardMintCount;
  uint256 public xvgPersonalizedMintCount;
  uint256 public airdropMintCount;
  uint256 public supply = 3210;
  uint256 public standardSupply = 3000;
  uint256 public personalizedSupply = 210;
  uint256 public stopMintingAt = 3001;
  uint256 public royaltyFee = 70; // 7%
  uint256 public airdropEligibility = 4;
  uint256 public maxRandomAttempts = 500;
  address public paymentAddress;
  address public royaltyAddress;
  bool public mintingEnabled;
  string public baseURI;
  string public defaultPersonalizedTokenURI;
  enum MintType { STANDARD, PERSONALIZED, ADMIN, AIRDROP, XVG }

  mapping(uint256 => string) public personalizedTokenURIs;
  mapping(uint256 => string) public personalizedImageURIs;
  mapping(uint256 => bool) public mintedTokenIds;
  uint256[] public mintedPersonalizedTokenIds;
  uint256[] public availableTokenIds;
  uint256 private nonce = 0;
  bool public safeRandomTokenIdSelection;

  address[] public erc20Addresses;
  mapping(address => PaymentInfo) public paymentInfo;
  EnumerableMap.UintToAddressMap private _tokenOwners;

  struct NftInfo {
    uint256 tokenId;
    address owner;
  }

  struct Metadata {
    uint256 tokenId;
    string tokenURI;
    string imageURI;
    address owner;
  }

  struct PaymentInfo {
    address erc20Address;
    string symbol;
    uint256 decimals;
    uint256 price;
    uint256 personalizedPrice;
  }

  bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

  event Mint(address addr, uint256 tokenId);
  event Airdrop(address addr, uint256 tokenId);
  event PersonalizedMint(address addr, uint256 tokenId);
  event UpdateBaseURI(string uri);
  event UpdateDefaultPersonalizedTokenURI(string uri);
  event UpdateTokenURI(uint256 id, string uri);
  event UintPropertyChange(string param, uint256 value);
  event BoolPropertyChange(string param, bool value);
  event AddressPropertyChange(string param, address value);
  event MintingEnabled();
  event MintingDisabled();

  error FailedRandomAttempt();

  bytes4 private constant _INTERFACE_ID_ERC721 = 0x80ac58cd;
  bytes4 private constant _INTERFACE_ID_ERC721_METADATA = 0x5b5e139f;
  bytes4 private constant _INTERFACE_ID_ERC721_ENUMERABLE = 0x780e9d63;
  bytes4 private constant _INTERFACE_ID_ERC2981 = 0x2a55205a;

  constructor() ERC721("XVG Apes", "XVGA") {
    // ERC721 interface
    _registerInterface(_INTERFACE_ID_ERC721);
    _registerInterface(_INTERFACE_ID_ERC721_METADATA);
    _registerInterface(_INTERFACE_ID_ERC721_ENUMERABLE);

    // Royalties interface
    _registerInterface(_INTERFACE_ID_ERC2981);

    _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
    _setupRole(ADMIN_ROLE, msg.sender);
  }

  modifier onlyAdmin() {
    require(hasRole(ADMIN_ROLE, msg.sender), "ADMIN_ROLE_REQUIRED");
    _;
  }

  modifier publicMint(uint256 quantity, IERC20 token, MintType mintType) {
    require(mintingEnabled, "MINTING_DISABLED");
    require(paymentAddress != address(0), "PAYMENT_ADDRESS_NOT_SET");

    PaymentInfo memory t = paymentInfo[address(token)];
    require(t.decimals > 0, "INVALID_TOKEN");
    if (mintType == MintType.STANDARD) {
      if (t.price > 0) {
        token.transferFrom(msg.sender, paymentAddress, quantity * t.price);
      }
    } else {
      if (t.personalizedPrice > 0) {
        token.transferFrom(msg.sender, paymentAddress, t.personalizedPrice);
      }
    }
    _;
  }

  /// @notice Mints a random NFT.
  function mint(uint256 quantity, IERC20 token) external publicMint(quantity, token, MintType.STANDARD) {
    require(standardMintCount + quantity <= stopMintingAt, "QUANTITY_OVER_PLANNED_STOP");
    _randomMint(quantity, msg.sender, MintType.STANDARD);
    _airdrop(quantity, msg.sender);
  }

  /// @dev Aridrops NFTs, depending on quantity purchased and airdropEligibility
  function _airdrop(uint256 quantity, address to) private {
    uint256 airdropAmount;
    if (airdropEligibility > 0 && quantity >= airdropEligibility) {
      airdropAmount = quantity / airdropEligibility;
      bool canAirdrop = (standardMintCount + airdropAmount <= standardSupply);
      while (airdropAmount > 1 && !canAirdrop) {
        airdropAmount--;
        canAirdrop = (standardMintCount + airdropAmount <= standardSupply);
      }
      if (canAirdrop) _randomMint(airdropAmount, to, MintType.AIRDROP);
    }
  }

  /// @notice Mints an NFT with the chosen ID, that will be manually created and have its metadata updated once the image is ready.
  function personalizedMint(uint256 tokenId, IERC20 token) external publicMint(1, token, MintType.PERSONALIZED) {
    require(tokenId < personalizedSupply, "TOKEN_ID_OUT_OF_RANGE");
    _mintSpecificTokenId(tokenId, msg.sender);
  }

  /// @dev Mints a random token within the 210 - 3209 range (ADMIN).
  function adminMint(uint256 quantity, address to) external onlyAdmin {
    _randomMint(quantity, to, MintType.ADMIN);
  }

  /// @dev Mints a token with the given ID, can be a standard or personalized (ADMIN).
  function specificMint(uint256 tokenId, address to) external onlyAdmin {
    _mintSpecificTokenId(tokenId, to);
    adminMintCount++;
    if (tokenId < personalizedSupply) {
      adminPersonalizedMintCount++;
    } else {
      adminStandardMintCount++;
    }
  }

  /// @dev Mints a random token within the 210 - 3209 range for users who paid in XVG (ADMIN).
  function xvgMint(uint256 quantity, address to) external onlyAdmin {
    _randomMint(quantity, to, MintType.XVG);
  }

  /// @dev Mints a token with the given ID, can be a standard or personalized (ADMIN).
  function xvgPersonalizedMint(uint256 tokenId, address to) external onlyAdmin {
    require(tokenId < personalizedSupply, "TOKEN_ID_OUT_OF_RANGE");
    _mintSpecificTokenId(tokenId, to);
    xvgPersonalizedMintCount++;
  }

  /// @dev Mints a random token within the standard range (210 - 3209).
  function _randomMint(uint256 quantity, address to, MintType mintType) private {
    require(standardMintCount < standardSupply, "MINTED_OUT");
    require(quantity > 0, "QUANTITY_TOO_LOW");
    require(standardMintCount + quantity <= standardSupply, "QUANTITY_TOO_HIGH");
    for (uint256 i = 0; i < quantity; i++) {
      uint256 tokenId = _getRandomTokenId(personalizedSupply, supply);
      _mint(to, tokenId);
      _setTokenURI(tokenId, _endOfURI(tokenId));
      mintedTokenIds[tokenId] = true;
      standardMintCount++;
      mintCount++;
      if (mintType == MintType.ADMIN) {
        adminMintCount++;
        adminStandardMintCount++;
      }
      if (mintType == MintType.XVG) {
        xvgStandardMintCount++;
      }
      if (mintType == MintType.AIRDROP) {
        airdropMintCount++;
        emit Airdrop(to, tokenId);
      } else {
        emit Mint(to, tokenId);
      }
      _handleStopMintingAt();
    }
  }

  /// @dev Mints a token with the given ID (can be a standard or personalized).
  function _mintSpecificTokenId(uint256 tokenId, address to) private {
    require(mintCount < supply, "MINTED_OUT");
    require(tokenId < supply, "ID_TOO_HIGH");
    require(!mintedTokenIds[tokenId], "TOKEN_ALREADY_MINTED");
    _mint(to, tokenId);
    mintedTokenIds[tokenId] = true;
    mintCount++;
    if (tokenId >= personalizedSupply) {
      _setTokenURI(tokenId, _endOfURI(tokenId));
      standardMintCount++;
      emit Mint(to, tokenId);
    } else {
      mintedPersonalizedTokenIds.push(tokenId);
      personalizedMintCount++;
      emit PersonalizedMint(to, tokenId);
    }
  }

  /// @dev Returns a random tokenId between offset and amount (personalizedSupply 210 - supply 3210).
  function _getRandomTokenId(uint256 offset, uint256 amount) private returns (uint256) {
    if (!safeRandomTokenIdSelection) {
      uint256 _maxTokenId = amount - offset;
      uint256 random = uint256(keccak256(abi.encodePacked(nonce, msg.sender, block.timestamp))) % _maxTokenId;
      nonce++;
      uint256 tokenId = random + offset;
      uint256 attempts = 1;
      while (mintedTokenIds[tokenId]) {
        random = uint256(keccak256(abi.encodePacked(random, nonce, msg.sender, block.timestamp))) % _maxTokenId;
        nonce++;
        tokenId = random + offset;
        attempts++;
        if (attempts > maxRandomAttempts) {
          revert FailedRandomAttempt();
        }
      }
      mintedTokenIds[tokenId] = true;
      return tokenId;
    } else {
      return _getRandomAvailableTokenId();
    }
  }

  /// @dev Returns a random tokenId from availableTokenIds and removes it from the list.
  function _getRandomAvailableTokenId() private returns (uint256) {
    uint256 randomIndex = _getRandomNumber(availableTokenIds.length);
    uint256 tokenId = availableTokenIds[randomIndex];

    // swap the tokenId at randomIndex with the last one of the array
    availableTokenIds[randomIndex] = availableTokenIds[availableTokenIds.length - 1];
    // the tokenId can now be removed with pop(), as it's the last element
    availableTokenIds.pop();

    return tokenId;
  }

  /// @dev Returns a random number up to maxNumber.
  function _getRandomNumber(uint256 maxNumber) private returns (uint256 random) {
    random = uint256(keccak256(abi.encodePacked(nonce, msg.sender, block.timestamp))) % maxNumber;
    nonce++;
  }

  /// @dev Pause minting when stopMintingAt is reached.
  function _handleStopMintingAt() private {
    if (standardMintCount == stopMintingAt) {
      mintingEnabled = false;
      emit MintingDisabled();
    }
  }

  /// @dev Fills availableTokenIds with unminted tokenIds and switches to using this list for random selection.
  function enableSafeRandomTokenIdSelection() external onlyAdmin {
    require(availableTokenIds.length == 0, "SAFE_RANDOM_SELECTION_ALREADY_ENABLED");
    // initialize the availableTokenIds array with all tokenIds that have not been minted yet
    uint256 availableAmount = standardSupply - standardMintCount;
    availableTokenIds = new uint256[](availableAmount);
    uint256 addedTokenIds = 0;
    for (uint256 id = personalizedSupply; id < supply; id++) {
      if (!mintedTokenIds[id]) {
        availableTokenIds[addedTokenIds] = id;
        addedTokenIds++;
      }
      if (addedTokenIds == availableAmount) {
        break;
      }
    }
    safeRandomTokenIdSelection = true;
  }

  /// @notice Returns a list of all available tokenIds for personalizedMint.
  function getAvailablePersonalizedTokenIds() view external returns (uint256[] memory) {
    uint256 availableAmount = personalizedSupply - personalizedMintCount;
    uint256[] memory result = new uint256[](availableAmount);
    uint256 addedTokenIds = 0;
    for (uint256 id = 0; id < personalizedSupply; id++) {
      if (!mintedTokenIds[id]) {
        result[addedTokenIds] = id;
        addedTokenIds++;
      }
      if (addedTokenIds == availableAmount) {
        break;
      }
    }
    return result;
  }

  /// @notice Returns a list of Metadata of all minted personalized tokens.
  function getAllPersonalizedMetadata() view external returns (Metadata[] memory) {
    Metadata[] memory result = new Metadata[](personalizedMintCount);
    for (uint256 i = 0; i < personalizedMintCount; i++) {
      uint256 id = mintedPersonalizedTokenIds[i];
      result[i] = Metadata(id, tokenURI(id), personalizedImageURIs[id], ownerOf(id));
    }
    return result;
  }

  /// @notice Returns all tokenIds owned by the given address.
  function tokensOf(address owner) external view returns (uint256[] memory) {
    uint256 amountOwned = ERC721.balanceOf(owner);
    uint256[] memory result = new uint256[](amountOwned);
    for (uint256 i = 0; i < amountOwned; i++) {
      uint256 tokenId = ERC721Enumerable.tokenOfOwnerByIndex(owner, i);
      result[i] = tokenId;
    }
    return result;
  }

  /// @notice Returns a snapshot of all existing tokenIds and their respective owner.
  function snapshot() external view returns(NftInfo[] memory) {
    NftInfo[] memory result = new NftInfo[](_tokenOwners.length());
    for (uint256 i = 0; i < _tokenOwners.length(); i++) {
      (uint256 tokenId, address owner) = _tokenOwners.at(i);
      result[i] = NftInfo(tokenId, owner);
    }
    return result;
  }

  function getAllPaymentTokens() external view returns (PaymentInfo[] memory) {
    PaymentInfo[] memory result = new PaymentInfo[](erc20Addresses.length);
    for (uint256 i = 0; i < erc20Addresses.length; i++) {
      result[i] = paymentInfo[erc20Addresses[i]];
    }
    return result;
  }

  function setMintingEnabled(bool value) external onlyAdmin {
    mintingEnabled = value;
    if (mintingEnabled) {
      emit MintingEnabled();
    } else {
      emit MintingDisabled();
    }
  }

  function setStopMintingAt(uint256 value) external onlyAdmin {
    stopMintingAt = value;
    emit UintPropertyChange("stopMintingAt", value);
  }

  function setPaymentToken(IERC20 token, string calldata symbol, uint256 decimals, uint256 price, uint256 personalizedPrice) external onlyAdmin {
    require((address(token) != address(0) && token.totalSupply() > 0), "INVALID_TOKEN");
    uint256 index = _getErc20Index(address(token));
    if (decimals == 0 && price == 0 && personalizedPrice == 0) {
      // If all values are 0, delete the token
      delete paymentInfo[address(token)];
      // Swap last element, then pop it
      erc20Addresses[index] = erc20Addresses[erc20Addresses.length - 1];
      erc20Addresses.pop();
    } else {
      // This allows to set a price = 0 (free mint) if needed
      paymentInfo[address(token)] = PaymentInfo(address(token), symbol, decimals, price, personalizedPrice);
      if (index == 404) {
        erc20Addresses.push(address(token));
      }
    }
  }

  function setPaymentAddress(address addr) external onlyAdmin {
    paymentAddress = addr;
    emit AddressPropertyChange("paymentAddress", addr);
  }

  function setRoyaltyAddress(address addr) external onlyAdmin {
    royaltyAddress = addr;
    emit AddressPropertyChange("royaltyAddress", addr);
  }

  function setRoyaltyFee(uint256 value) external onlyAdmin {
    royaltyFee = value;
    emit UintPropertyChange("royaltyFee", value);
  }

  function setAirdropEligibility(uint256 value) external onlyAdmin {
    airdropEligibility = value;
    emit UintPropertyChange("airdropEligibility", value);
  }

  function setSupply(uint256 value) external onlyAdmin {
    supply = value;
    standardSupply = supply - personalizedSupply;
    emit UintPropertyChange("supply", supply);
    emit UintPropertyChange("personalizedSupply", personalizedSupply);
  }

  function setMaxRandomAttempts(uint256 value) external onlyAdmin {
    maxRandomAttempts = value;
    emit UintPropertyChange("maxRandomAttempts", value);
  }

  function setBaseURI(string calldata uri) external onlyAdmin {
    baseURI = uri;
    emit UpdateBaseURI(uri);
  }

  function setTokenURI(uint256 tokenId, string calldata uri) external onlyAdmin {
    require(tokenId >= personalizedSupply && tokenId < supply, "ID_OUT_OF_RANGE");
    _setTokenURI(tokenId, uri);
    emit UpdateTokenURI(tokenId, uri);
  }

  function setPersonalizedMetadata(uint256 tokenId, string calldata jsonURI, string calldata imageURI) external onlyAdmin {
    require(tokenId < personalizedSupply, "ID_TOO_HIGH");
    require(mintedTokenIds[tokenId], "ID_NOT_YET_MINTED");
    personalizedTokenURIs[tokenId] = jsonURI;
    personalizedImageURIs[tokenId] = imageURI;
  }

  /// @dev Sets the full URI for the generic metadata that will be displayed before the personalized NFT is created.
  function setDefaultPersonalizedTokenURI(string calldata uri) external onlyAdmin {
    defaultPersonalizedTokenURI = uri;
    emit UpdateDefaultPersonalizedTokenURI(uri);
  }

  function _afterTokenTransfer(address from, address to, uint256 tokenId) internal virtual override {
    super._afterTokenTransfer(from, to, tokenId);
    _tokenOwners.set(tokenId, to);
  }

  function _baseURI() internal view override returns (string memory) {
    return baseURI;
  }

  function _uint2str(uint256 nr) internal pure returns (string memory str) {
    if (nr == 0) {
      return "0";
    }
    uint256 j = nr;
    uint256 length;
    while (j != 0) {
      length++;
      j /= 10;
    }
    bytes memory bstr = new bytes(length);
    uint256 k = length;
    j = nr;
    while (j != 0) {
      bstr[--k] = bytes1(uint8(48 + j % 10));
      j /= 10;
    }
    str = string(bstr);
  }

  function _endOfURI(uint256 nr) internal pure returns (string memory jsonString) {
    string memory number = _uint2str(nr);
    string memory dotJson = ".json";
    jsonString = string(abi.encodePacked(number, dotJson));
  }

  function _beforeTokenTransfer(address from, address to, uint256 tokenId) internal override(ERC721, ERC721Enumerable) {
    super._beforeTokenTransfer(from, to, tokenId);
  }

  function _burn(uint256 tokenId) internal override(ERC721, ERC721URIStorage) {
    super._burn(tokenId);
  }

  function tokenURI(uint256 tokenId) public view override(ERC721, ERC721URIStorage) returns (string memory) {
    if (_isPersonalizedUriSet(tokenId)) {
      return personalizedTokenURIs[tokenId];
    } else if (tokenId < personalizedSupply) {
      return defaultPersonalizedTokenURI;
    } else {
      return super.tokenURI(tokenId);
    }
  }

  function _isPersonalizedUriSet(uint256 tokenId) private view returns (bool) {
    bytes memory valueBytes = bytes(personalizedTokenURIs[tokenId]);
    return valueBytes.length > 0;
  }

  function royaltyInfo(uint256, uint256 salePrice) external view override(IERC2981) returns (address receiver, uint256 royaltyAmount) {
    receiver = royaltyAddress;
    royaltyAmount = salePrice * royaltyFee / 1000; // royalty is set with 2 decimals, thus divide by 1000 instead of 100
  }

  function supportsInterface(bytes4 interfaceId) public view override(ERC721, ERC721Enumerable, ERC165Storage, IERC165, AccessControl) returns (bool) {
    return super.supportsInterface(interfaceId);
  }

  function _getErc20Index(address _address) public view returns (uint256) {
    for (uint256 i = 0; i < erc20Addresses.length; i++) {
      if (erc20Addresses[i] == _address) {
          return i;
      }
    }
    return 404; // (if the address is not found)
  }

  function sweepEth() external onlyAdmin {
    uint256 balance = address(this).balance;
    require(balance > 0, "NO_FUNDS_TO_WITHDRAW");
    (bool sent, ) = msg.sender.call{value: balance}("");
    require(sent, "FAILED_SENDING_FUNDS");
  }

  function sweepErc20(IERC20 token) external onlyAdmin {
    uint256 balance = token.balanceOf(address(this));
    require(balance > 0, "NO_FUNDS_TO_WITHDRAW");
    token.transfer(msg.sender, balance);
  }

  receive() external payable {}

  fallback() external payable {}
}
"
    },
