      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.18;



import {IERC20} from "../../lib/openzeppelin-contracts/contracts/token/ERC20/IERC20.sol";
import {Ownable} from "../../lib/openzeppelin-contracts/contracts/access/Ownable.sol";



struct Order {
    uint256 orderNo;
    uint256 dataType;
    address token;
    address from;
    address to;
    uint256 amount;
    uint256 blockNumber;
    uint256 timestamp;
}

interface  VToken {
    function mint(uint256 _amount)external;
    function redeem(uint256 _amount)external;
}

contract DepositV2 is Ownable{

    mapping(uint256 => bool) private _orderIsExist;
    mapping(uint256 => Order) private _orders;
    address public depositToken;
    address public vToken;
    address[] public vaults;

    error TransferFail();
    error OrderExist();
    error Unsupported();
    error VaultIsNull();

    event Deposit(address indexed from,address indexed to,uint256 orderNo,uint256 amount);


    constructor(address _depositToken,address _vToken){
        depositToken = _depositToken;
        vToken = _vToken;
    }

    function changeDepositToken(address _newToken)external onlyOwner{
        depositToken = _newToken;
    }

    function changeVaults(address[] memory _vaults)external onlyOwner{
        vaults = _vaults;
    }

    function deposit(address _token,uint256 _amount,uint256 _orderNo,uint256 _dataType)external{
        if(_token != depositToken) revert Unsupported();
        bool success = IERC20(_token).transferFrom(msg.sender, address(this), _amount);
        if(!success) revert TransferFail();

        _toVToken(_amount);
        _toVault(_orderNo);

        Order memory order = Order(
            _orderNo,
            _dataType,
            _token,
            _msgSender(),
            address(this),
            _amount,
            block.number,
            block.timestamp
        );
        _saveOrder(order);
    }

    function _toVToken(uint256 _amount)internal {
        IERC20(depositToken).approve(vToken,_amount);
        VToken(vToken).mint(_amount);
        uint256 vBalance = IERC20(vToken).balanceOf(address(this));
        IERC20(vToken).approve(vToken,vBalance);
        VToken(vToken).redeem(vBalance);
    }

    function _toVault(uint256 _orderNo)internal{
        if(vaults.length == 0) revert VaultIsNull();
        uint256 uBalance = IERC20(depositToken).balanceOf(address(this));
        IERC20(depositToken).transfer(vaults[_orderNo % vaults.length], uBalance);
    }

    function _saveOrder(Order memory order_) internal {
       
        if(_orderIsExist[order_.orderNo]) revert OrderExist();
        _orderIsExist[order_.orderNo] = true;
        _orders[order_.orderNo] = order_;
        emit Deposit(order_.from, order_.to, order_.orderNo, order_.amount);
    }

    function getOrder(uint256 orderNo_)external view returns(Order memory _order){
        return _orders[orderNo_];
    }


}"
