      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "./BEP20.sol";
import "./SafeMath.sol";
import "./Ownable.sol";
import "./IERC721Enumerable.sol";

contract MekaMiners is BEP20 {
    using SafeMath for uint256;

    mapping(address => bool) private banBlackList;
    uint8 private _buyFee = 0;
    uint8 private _sellFee = 0;
    address private _feeReceiveAddress;
    bool private _applyFee = true;
    bool _isLockTransferForAntiBot = true;
    address private _pancakeRouterAddress;
    mapping(address => bool) _lpAddresses;
    mapping(address => mapping(address => uint256)) private _lastTrade;

    uint8 private _tradeCooldown;
    event Deposit(address from, uint256 amount);

    constructor(address feeReceiveAddress) BEP20("MekaMiners", "MEKA") {
        _tradeCooldown = 60;
        _feeReceiveAddress = feeReceiveAddress;
        _pancakeRouterAddress = 0x10ED43C718714eb63d5aA57B78B54704E256024E;
        _mint(_msgSender(), 200000000 * 10**decimals());
    }

    function setCooldownForTrades(uint8 tradeCooldown) external onlyOwner {
        _tradeCooldown = tradeCooldown;
    }

    function setBuyFee(uint8 _fee) public onlyOwner {
        _buyFee = _fee;
    }

    function setSellFee(uint8 _fee) public onlyOwner {
        _sellFee = _fee;
    }

    function setFeeReceiveAddress(address feeReceiveAddress)
        external
        onlyOwner
    {
        require(feeReceiveAddress != address(0), "ERC20: Invalid lp address");
        _feeReceiveAddress = feeReceiveAddress;
    }

    function setPCSRouterdAddress(address newAddress) public onlyOwner {
        require(newAddress != address(0), "ERC20: Invalid pcs router address");
        _pancakeRouterAddress = newAddress;
    }

    function setLPAddress(address _addr, bool enable) external onlyOwner {
        require(_addr != address(0), "ERC20: Invalid lp address");
        _lpAddresses[_addr] = enable;
    }

    function getFees() external view returns (uint256 buyFee, uint256 sellFee) {
        return (_buyFee, _sellFee);
    }

    function isAddressLP(address _addr) public view returns (bool) {
        return _lpAddresses[_addr];
    }

    function getFeeAddress() external view returns (address fee) {
        return _feeReceiveAddress;
    }

    function isBlacklisted(address addr)
        external
        view
        onlyOwner
        returns (bool)
    {
        return banBlackList[addr];
    }

    function updateTokenLock() external onlyOwner {
        _isLockTransferForAntiBot = !_isLockTransferForAntiBot;
    }

    function updateApplyFee() external onlyOwner {
        _applyFee = !_applyFee;
    }

    function bulkBlackList(address[] calldata addr) external onlyOwner {
        for (uint256 i = 0; i < addr.length; i++) {
            banBlackList[addr[i]] = true;
        }
    }

    function updateBlackList(address addr, bool blacklisted)
        external
        onlyOwner
    {
        banBlackList[addr] = blacklisted;
    }

    function transfer(address _receiver, uint256 _amount)
        public
        override
        whenNotPaused
        returns (bool success)
    {
        require(!_isLockTransferForAntiBot, "transfer locked");
        require(!banBlackList[_msgSender()], "address-blacklisted");
        require(!banBlackList[_receiver], "address-blacklisted");
        uint256 feeAmount = 0;
        if (
            _applyFee && (isAddressLP(_msgSender()) || isAddressLP(_receiver))
        ) {
            if (_receiver != owner() && _receiver != _pancakeRouterAddress) {
                feeAmount = isAddressLP(_receiver)
                    ? computeCut(_amount, _sellFee)
                    : computeCut(_amount, _buyFee);

                require(
                    (_lastTrade[_msgSender()][_receiver] <
                        (block.timestamp - _tradeCooldown)) &&
                        (_lastTrade[_receiver][_msgSender()] <
                            (block.timestamp - _tradeCooldown)),
                    "No consecutive sells allowed. Please wait."
                );
            }

            _transfer(_msgSender(), _feeReceiveAddress, feeAmount);

            _lastTrade[_msgSender()][_receiver] = block.timestamp;
            _lastTrade[_receiver][_msgSender()] = block.timestamp;
        }

        _transfer(_msgSender(), _receiver, _amount - feeAmount);

        return true;
    }

    function transferFrom(
        address _from,
        address _receiver,
        uint256 _amount
    ) public override whenNotPaused returns (bool) {
        require(!_isLockTransferForAntiBot, "transfer locked");
        require(!banBlackList[_from], "address-blacklisted");
        require(!banBlackList[_receiver], "address-blacklisted");
        uint256 _fee = 0;

        if (isAddressLP(_receiver) && _from != owner()) {
            require(
                (_lastTrade[_from][_receiver] <
                    (block.timestamp - _tradeCooldown)) &&
                    (_lastTrade[_receiver][_from] <
                        (block.timestamp - _tradeCooldown)),
                "No consecutive sells allowed. Please wait."
            );
            _fee = computeCut(_amount, _sellFee);
            super._transferFrom(_from, _feeReceiveAddress, _fee);
            _lastTrade[_from][_receiver] = block.timestamp;
            _lastTrade[_receiver][_from] = block.timestamp;
        }

        super._transferFrom(_from, _receiver, _amount - _fee);

        return true;
    }

    function computeCut(uint256 amount, uint8 _fee)
        private
        pure
        returns (uint256)
    {
        return (amount * _fee) / 100;
    }

    function isContract(address _address) private view returns (bool) {
        uint32 size;
        assembly {
            size := extcodesize(_address)
        }

        return (size > 0);
    }

    function _beforeTokenTransfer(
        address _from,
        address _to,
        uint256 _amount
    ) internal override {
        require(
            _to != address(this),
            string("No transfers to contract allowed.")
        );
        super._beforeTokenTransfer(_from, _to, _amount);
    }
}
"
