      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract HeroTDHoldInGameV2 is Ownable, ReentrancyGuard {
    using Counters for Counters.Counter;
    using SafeERC20 for IERC20;

    Counters.Counter private _eventIdCounter;

    IERC20 public HTD_TOKEN;
    uint256 public balanceFee;
    bool public chargeFeeHTD;
    address public ownerFee;

    // Time
    uint256 public timeBegin;

    struct NFTInfo {
        address userAddress; // address user deposit
        address contractAddress; // address nft
        uint256 tokenId; // NFT id
        uint256 status; // 0 - In game ( new deposit ) # 1 - Widthdraw before # 2 - Cancel
    }

    // Mapping
    mapping(address => bool) public supportNFTs;
    mapping(address => mapping(uint256 => NFTInfo)) public userNfts;
    mapping(address => uint256) private userTotalNFTDeposit;

    // Count
    uint256 public totalNFT;

    event EventUserDepositNFT(
        address userAddress,
        string userId,
        address nftAddress,
        uint256 tokenId,
        uint256 eventIndex,
        uint256 timestamp
    );

    event EventUserWithdrawNFT(
        address userAddress,
        string userId,
        address nftAddress,
        uint256 tokenId,
        uint256 eventIndex,
        uint256 timestamp
    );

    constructor() {}

    /**
     * check address
     */
    modifier validAddress(address addr) {
        require(addr != address(0x0));
        _;
    }

    // Set array nft support
    function addSupportNft(address[] memory nfts) public onlyOwner {
        for (uint256 i = 0; i < nfts.length; i++) {
            supportNFTs[nfts[i]] = true;
        }
    }

    // Remove nft support
    function removeSupportNft(address[] memory nfts) public onlyOwner {
        for (uint256 i = 0; i < nfts.length; i++) {
            supportNFTs[nfts[i]] = false;
        }
    }

    // Set address token check
    function setAddressTokenCheck(address _newAddress) external onlyOwner {
        HTD_TOKEN = IERC20(_newAddress);
    }

    // Set time
    function setTimeBegin(uint256 _newTime) external onlyOwner {
        timeBegin = _newTime;
    }

    // Set ownerFee
    function setOwnerFee(address _ownerFee) external onlyOwner {
        ownerFee = _ownerFee;
    }

    // Set check balance HTD
    function setCheckBalanceHTD(bool _isCheck, uint256 _balanceHTD)
        external
        onlyOwner
    {
        chargeFeeHTD = _isCheck;
        balanceFee = _balanceHTD;
    }

    // User deposit hero into game
    function depositNFTIntoGame(
        address nft,
        uint256 tokenId,
        string memory userId
    ) external {
        require(supportNFTs[nft], "This NFT not supported!");

        require(block.timestamp >= timeBegin, "Please wait start time");

        if (chargeFeeHTD == true) {
            HTD_TOKEN.safeTransferFrom(msg.sender, ownerFee, balanceFee);
        }

        // GET NFT from user
        IERC721(nft).safeTransferFrom(msg.sender, address(this), tokenId);

        // Save NFT struct
        NFTInfo memory objNFT;
        objNFT.userAddress = msg.sender;
        objNFT.contractAddress = nft;
        objNFT.tokenId = tokenId;
        objNFT.status = 0;

        // Push mapping NFT
        userNfts[msg.sender][tokenId] = objNFT;
        // Add 1
        userTotalNFTDeposit[msg.sender] += 1;
        // Increase
        totalNFT += 1;

        _eventIdCounter.increment();

        emit EventUserDepositNFT(
            msg.sender,
            userId,
            nft,
            tokenId,
            _eventIdCounter.current(),
            block.timestamp
        );
    }

    // User for Admin when user withdraw hero out the game
    function withdrawNFT(
        address nft,
        uint256 tokenId,
        address userAddress,
        string memory userId
    ) external {
        NFTInfo storage objNFT = userNfts[userAddress][tokenId];

        require(
            objNFT.userAddress == userAddress,
            "Address withdraw invalid!!!"
        );

        require(objNFT.contractAddress == nft, "NFT Address wrong!");

        require(objNFT.tokenId == tokenId, "NFT ID invalid!");

        require(objNFT.status == 0, "Status is invalid!");

        // Trans NFT to user
        IERC721(nft).safeTransferFrom(address(this), userAddress, tokenId);

        objNFT.status = 1;

        // Add 1 to index
        userTotalNFTDeposit[msg.sender] -= 1;

        // Increase
        totalNFT -= 1;

        _eventIdCounter.increment();

        emit EventUserWithdrawNFT(
            userAddress,
            userId,
            nft,
            tokenId,
            _eventIdCounter.current(),
            block.timestamp
        );
    }

    // Required function to allow receiving ERC-721 - When safeTransferFrom called auto implement this func if (to) is contract address
    function onERC721Received(
        address, /*operator*/
        address, /*from*/
        uint256, /*id*/
        bytes calldata /*data*/
    ) external pure returns (bytes4) {
        return
            bytes4(
                keccak256("onERC721Received(address,address,uint256,bytes)")
            );
    }
}
