    using SafeMath for uint256;
    using Address for address;

    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    uint256 private _totalSupply;
    string private _name = "Pi Connect";
    string private _symbol = "PiCo";
    uint8 private _decimals = 18;

    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;
    uint256 private maxSupply = 50*10**6 * 10**18;
    uint256 public marketingSellFee = 1; 
    uint256 public marketingBuyFee = 1; 
    uint256 public liquidityBuyFee = 2;   
    address public marketingAddress = 0xCa97D94a1110E7f390d172A2f35787733864B6C4;
    address public devAddress = 0x6388bdf0F05Ba270f87f42bB8bf9D51caA4E65b2;
    address public ecoSystemAddress = 0xe0b266111DCa7A06948C4E03875F3961cB295103;
    address public partnerAdvisoryAddress = 0x95f0A54780dF2B88333273be5D0Daf0c64BA61d3;
    address public eventAddress = 0x75951C9A13560F76aCDE7c692846F48059654799;
    address public contributorBonusAddress = 0xC390dc8486f016CB159f237eFD9962560E56A624;
    uint256 private lockVestingPercent = 50; // 50%
    uint256 public releaseVestingICOTime; // Date & Time of ICO
    uint256 public releaseVesting2ndTime; // 2592000 = 30 days
    uint256 public releaseVesting3rdTime; // 5184000 = 60 days
    uint256 public maxLockedHolder = 50;
    bool public taxStatus = false;
    bool public icoStatus = false;
    mapping(address => bool) public excludeFee;
    mapping(address => bool) public privateSaleHolder;
    mapping(address => uint256) private lockedAmount;

    constructor() {
        _mint(_msgSender(), maxSupply*53/100);
        _mint(devAddress, maxSupply*5/100);
        _mint(ecoSystemAddress, maxSupply*20/100);
        _mint(partnerAdvisoryAddress, maxSupply*5/100);
        _mint(eventAddress, maxSupply*9/100);
        _mint(contributorBonusAddress, maxSupply*3/100);
        _mint(marketingAddress, maxSupply*5/100);
        uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory()).createPair(address(this), uniswapV2Router.WETH());
        _approve(address(this), address(uniswapV2Router), ~uint256(0));
    }

    /**
     * @dev Returns the erc token owner.
     */
    function getOwner() external view override returns (address) {
        return owner();
    }

    /**
     * @dev Returns the token name.
     */
    function name() external view override returns (string memory) {
        return _name;
    }

    /**
     * @dev Returns the token decimals.
     */
    function decimals() external view override returns (uint8) {
        return _decimals;
    }

    /**
     * @dev Returns the token symbol.
     */
    function symbol() external view override returns (string memory) {
        return _symbol;
    }

    /**
     * @dev See {ERC20-totalSupply}.
     */
    function totalSupply() external view override returns (uint256) {
        return _totalSupply;
    }

    /**
     * @dev See {ERC20-balanceOf}.
     */
    function balanceOf(address account)
        external
        view
        override
        returns (uint256)
    {
        return _balances[account];
    }

    /**
     * @dev See {ERC20-transfer}.
     *
     * Requirements:
     *
     * - `recipient` cannot be the zero address.
     * - the caller must have a balance of at least `amount`.
     */
    function transfer(address recipient, uint256 amount)
        external
        override
        returns (bool)
    {
        if(block.timestamp < releaseVestingICOTime && privateSaleHolder[_msgSender()] == true) {
            require(_balances[_msgSender()] - amount >= lockedAmount[_msgSender()],"Your tokens are still locked");
        }
        if(block.timestamp >= releaseVestingICOTime && block.timestamp < releaseVesting2ndTime && privateSaleHolder[_msgSender()] == true) {
            require(_balances[_msgSender()] - amount >= lockedAmount[_msgSender()]*lockVestingPercent/100,"Your tokens are still locked");
        }
        if(block.timestamp >= releaseVesting2ndTime && block.timestamp < releaseVesting3rdTime && privateSaleHolder[_msgSender()] == true) {
            require(_balances[_msgSender()] - amount >= lockedAmount[_msgSender()]*lockVestingPercent/2/100,"Your tokens are still locked");
        }
        _advanceTransfer(_msgSender(), recipient, amount);
        return true;
    }

    /**
     * @dev See {ERC20-allowance}.
     */
    function allowance(address owner, address spender)
        external
        view
        override
        returns (uint256)
    {
        return _allowances[owner][spender];
    }

    /**
     * @dev See {ERC20-approve}.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     */
    function approve(address spender, uint256 amount)
        external
        override
        returns (bool)
    {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    /**
     * @dev See {ERC20-transferFrom}.
     *
     * Emits an {Approval} event indicating the updated allowance. This is not
     * required by the EIP. See the note at the beginning of {ERC20};
     *
     * Requirements:
     * - `sender` and `recipient` cannot be the zero address.
