     * `amount`.
     */
    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) external override returns (bool) {
        if(block.timestamp < releaseVestingICOTime && privateSaleHolder[sender] == true) {
            require(_balances[sender] - amount >= lockedAmount[sender],"Your tokens are still locked");
        }
        if(block.timestamp >= releaseVestingICOTime && block.timestamp < releaseVesting2ndTime && privateSaleHolder[sender] == true) {
            require(_balances[sender] - amount >= lockedAmount[sender]*lockVestingPercent/100,"Your tokens are still locked");
        }
        if(block.timestamp >= releaseVesting2ndTime && block.timestamp < releaseVesting3rdTime && privateSaleHolder[sender] == true) {
            require(_balances[sender] - amount >= lockedAmount[sender]*lockVestingPercent/2/100,"Your tokens are still locked");
        }
        _advanceTransfer(sender, recipient, amount);
        _approve(
            sender,
            _msgSender(),
            _allowances[sender][_msgSender()].sub(
                amount,
                "ERC20: transfer amount exceeds allowance"
            )
        );
        return true;
    }

    /**
