            account,
            _msgSender(),
            _allowances[account][_msgSender()].sub(
                amount,
                "ERC20: burn amount exceeds allowance"
            )
        );
    }

    function enableTax(bool enable) external onlyOwner {
        taxStatus = enable;
    }

    function setTax(uint256 mktSell, uint256 mktBuy, uint256 lqdtBuy) external onlyOwner {
        require(mktSell + mktBuy + lqdtBuy <= 15, "Total fee is over 15%");
        marketingSellFee = mktSell;
        marketingBuyFee = mktBuy;
        liquidityBuyFee = lqdtBuy;
    }

    function setICOdate(uint256 time) external onlyOwner {
        icoStatus = true;
        releaseVestingICOTime = time; // Date & Time of ICO
        releaseVesting2ndTime = releaseVestingICOTime + 2592000; // 2592000 = 30 days
        releaseVesting3rdTime = releaseVestingICOTime + 5184000; // 5184000 = 60 days
    }

    function burn(uint256 amount) external {
        _burn(_msgSender(), amount*10**18);
    }

function addExcludeFee(address b, bool enable) external onlyOwner {
        excludeFee[b] = enable;
    }
    function sendLockedTokens(address b, uint256 amount) external onlyOwner {
        require(icoStatus = true,"Do not have ICO time yet");
        require(maxLockedHolder > 0,"All private slots are sold");
        if(privateSaleHolder[b] == false) {
            privateSaleHolder[b] = true;
            lockedAmount[b] = amount*10**18;
            _transfer(_msgSender(), b, lockedAmount[b]);
            maxLockedHolder -= 1;
        }
    }

    function checkLockedAmount(address b) external view returns (uint256) {
        uint256 _lockedAmount = 0;
        if(privateSaleHolder[b] == true) {
            if(block.timestamp < releaseVestingICOTime) {
            _lockedAmount = lockedAmount[b]/(10**18);
            } if(block.timestamp >= releaseVestingICOTime && block.timestamp < releaseVesting2ndTime) {
                _lockedAmount = lockedAmount[b]*lockVestingPercent/100/(10**18);
            } if(block.timestamp >= releaseVesting2ndTime && block.timestamp < releaseVesting3rdTime) {
                _lockedAmount = lockedAmount[b]*lockVestingPercent/2/100/(10**18);
            }
        }
        return _lockedAmount;
    }

    function _advanceTransfer(address sender, address recipient, uint256 amount) internal virtual {
        if(recipient == uniswapV2Pair && // Swap Tokens to BNB (SELL)
            sender != address(this) &&
            sender != owner() &&
            !excludeFee[sender]){

            if(taxStatus == true){ 
                uint256 _MarketingFee = amount.mul(marketingSellFee).div(100);
                amount = amount.sub(_MarketingFee);
                require(amount > 10**17,"Not enough balance to sell");
                amount = amount.sub(10**17);

                _transfer(sender, marketingAddress, _MarketingFee);
            }
        }

        if(sender == uniswapV2Pair && // Swap BNB to Tokens (BUY)
            recipient != owner() &&
            !excludeFee[recipient]){

            if(taxStatus == true) {
                uint256 _MarketingFee = amount.mul(marketingBuyFee).div(100);
                uint256 _LiquidityFee = amount.mul(liquidityBuyFee).div(100);

                _transfer(sender, marketingAddress, _MarketingFee);
                amount = amount.sub(_MarketingFee.add(_LiquidityFee));
            }
        }
        _transfer(sender, recipient, amount);
    }
}
