pragma solidity ^0.6.0;

// import "@nomiclabs/buidler/console.sol";





contract LAVA is IERC20,Ownable {

  using SafeMath for uint256;
  mapping (address => uint256) private _balances;
  mapping (address => mapping (address => uint256)) private _allowed;


  string private _name = 'Lavaswap';
  string private _symbol = 'Lava';
  uint8 private _decimals = 18;


  function name() public view returns(string memory) {
    return _name;
  }

  function symbol() public view returns(string memory) {
    return _symbol;
  }

  function decimals() public view returns(uint8) {
    return _decimals;
  }

  uint256 _totalSupply = 0;
  uint256 _initSupply = 100000000 *1e18;
  uint256 private _cap = 105000000 *1e18;

  uint256 public burnPercent = 250;
  uint256 public maxBurnPercent = 300;
  uint256 public developerPercent = 250;
  uint256 public maxDeveloperPercent = 300;
  address public developerAddr;
  mapping (address => bool) public whiteList;
  mapping (address => bool) public minters;

  

  function setMinter(address account, bool set) public onlyOwner{
    minters[account] = set;
  }

  modifier onlyMinter() {
    require(minters[msg.sender], "Not minters");
    _;
  }

  function setDev(address _dev) public onlyOwner{
    developerAddr = _dev;
  }

  function setBurnFee(uint256 _fee) public onlyOwner{
    require(_fee <= maxBurnPercent, "burnPercent exceeded");
    burnPercent = _fee;
  }

  function setDevFee(uint256 _fee) public onlyOwner{
    require(_fee <= maxDeveloperPercent, "developerPercent exceeded");
    developerPercent = _fee;
  }

  function setNoFee(address account, bool set) public onlyOwner{
      whiteList[account] = set;
  }


  function cap() public view returns (uint256) {
    return _cap;
  }



  constructor(address _dev) public {
    minters[msg.sender] = true;
    _mint(msg.sender, _initSupply);
    developerAddr = _dev;
    
  }

  function totalSupply() public view override returns (uint256) {
    return _totalSupply;
  }

  function balanceOf(address owner) public view override returns (uint256) {
    return _balances[owner];
  }

  function allowance(address owner, address spender) public view override returns (uint256) {
    return _allowed[owner][spender];
  }


  function afterFee(uint256 value) public view returns (uint256,uint256,uint256)  {
    uint256 burnValue = value.mul(burnPercent).div(10000);
    uint256 developerValue = value.mul(developerPercent).div(10000);
    uint256 leftValue = value.sub(burnValue).sub(developerValue);
    return (burnValue,developerValue,leftValue);
  }

  function transfer(address to, uint256 value) public override returns (bool) {
    require(value <= _balances[msg.sender]);
    // require(to != address(0));

    uint256 burnValue = 0;
    uint256 developerValue = 0;
    uint256 leftValue = value;

     _balances[msg.sender] = _balances[msg.sender].sub(value);

    if(!whiteList[msg.sender] && !whiteList[to] && developerAddr != address(0)){
        (burnValue,developerValue,leftValue) = afterFee(value);
    }
    
    _balances[to] = _balances[to].add(leftValue);
    _balances[developerAddr] = _balances[developerAddr].add(developerValue);
    _totalSupply = _totalSupply.sub(burnValue);

    emit Transfer(msg.sender, to, leftValue);
    emit Transfer(msg.sender, developerAddr, developerValue);
    emit Transfer(msg.sender, address(0), burnValue);

    return true;
  }


  function approve(address spender, uint256 value) public override returns (bool) {
    require(spender != address(0));
    _allowed[msg.sender][spender] = value;
    emit Approval(msg.sender, spender, value);
    return true;
  }

  function transferFrom(address from, address to, uint256 value) public override returns (bool) { 
    require(value <= _balances[from]);
    require(value <= _allowed[from][msg.sender]);
    require(to != address(0));
    
    uint256 burnValue = 0;
    uint256 developerValue = 0;
    uint256 leftValue = value;

    _balances[from] = _balances[from].sub(value);
    _allowed[from][msg.sender] = _allowed[from][msg.sender].sub(value);
    
    if(!whiteList[from] && !whiteList[to] && developerAddr != address(0)){
        (burnValue,developerValue,leftValue) = afterFee(value);
    }

    
    _balances[to] = _balances[to].add(leftValue);
    _balances[developerAddr] = _balances[developerAddr].add(developerValue);
    _totalSupply = _totalSupply.sub(burnValue);
    
    emit Transfer(from, to, leftValue);
    emit Transfer(from, developerAddr, developerValue);
    emit Transfer(from, address(0), burnValue);

    return true;
  }

    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: mint to the zero address");
        require(totalSupply().add(amount) <= _cap, "ERC20Capped: cap exceeded");
        _totalSupply = _totalSupply.add(amount);
        _balances[account] = _balances[account].add(amount);
        emit Transfer(address(0), account, amount);
    }

    function mint(address account, uint256 amount) public onlyMinter returns (bool) {
        _mint(account, amount);
        return true;
    }

    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: burn from the zero address");
        _balances[account] = _balances[account].sub(amount, "ERC20: burn amount exceeds balance");
        _totalSupply = _totalSupply.sub(amount);
        emit Transfer(account, address(0), amount);
    }


    function burn(uint256 amount) public virtual {
        _burn(_msgSender(), amount);
    }
}
