      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.6;

import "openzeppelin-contracts/contracts/token/ERC20/IERC20.sol";
import "openzeppelin-contracts/contracts/access/Ownable.sol";

interface IUniswapFactory {
    function createPair(address tokenA, address tokenB) external returns (address pair);
}
interface IUniSwapRouter {
    function factory() external pure returns (address);
    function getAmountsOut(uint amountIn, address[] calldata path) external view returns (uint[] memory amounts);
}

contract UNDErc20 is IERC20, Ownable {

    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    uint256 private _totalSupply;
    string private _name;
    string private _symbol;

    address public fundAddr;
    address public treasury;
    address public undPair;
    address public usdtAddr;

    IUniSwapRouter public UniRouter;

    uint8[3] private feeSetting = [2, 2, 2];

    bool isOpenTrade;

    mapping(address => bool) private controller;
    mapping(address => bool) public isExcludeFee;

    modifier onlyController () {
        require(controller[_msgSender()] , "Erc20: only by controller.");
        _;
    }

    constructor() {
        _name = "UND Token";
        _symbol = "UND";

        treasury = 0x5246DcFBb7a6944c2B7E5BA86071C25964d9d2fa;
        fundAddr = 0x67BA67f76d6d01A7eaE3415175EAf7784270B71C;

        address _usdtAddr = 0x55d398326f99059fF775485246999027B3197955;

        address _uniRouterAddr = 0x10ED43C718714eb63d5aA57B78B54704E256024E;

        UniRouter = IUniSwapRouter(_uniRouterAddr);

        usdtAddr = _usdtAddr;
        undPair = IUniswapFactory(IUniSwapRouter(_uniRouterAddr).factory()).createPair(address(this), _usdtAddr);

        controller[msg.sender] = true;

        isExcludeFee[fundAddr] = true;
        isExcludeFee[treasury] = true;
        isExcludeFee[msg.sender] = true;
        isExcludeFee[address(this)] = true;

        _mint(msg.sender, 7_0000_0000 ether);
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public pure returns (uint8) {
        return 18;
    }

    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view virtual override returns (uint256) {
        return _balances[account];
    }

    function setFundAddress(address addr_) external onlyController {
        fundAddr = addr_;
    }

    function setTreasuryAddress(address addr_) external  onlyController {
        treasury = addr_;
    }

    function setUNDPairAddress(address addr_) external onlyController {
        undPair = addr_;
    }

    function setUSDTAddress(address addr_) external  onlyController {
        usdtAddr = addr_;
    }

    function setExcludeFee(address addr_, bool _switch) external onlyController {
        isExcludeFee[addr_] = _switch;
    }

    function setOpenTrade(bool isOpen) external  onlyController {
        isOpenTrade = isOpen;
    }

    function setExcludeFeeBatch(address[] calldata addrs_, bool _switch ) external  onlyController {

        for(uint256 i = 0; i < addrs_.length; i ++) {
            isExcludeFee[addrs_[i]] = _switch;
        }
    }

    function setFee(uint8 buyFee_, uint8 sellFee_, uint8 transFee_) external onlyController {
        require(buyFee_ + sellFee_ + transFee_ <= 100, "invaild fee");
        feeSetting[0] = buyFee_;
        feeSetting[1] = sellFee_;
        feeSetting[2] = transFee_;
    }

    function transferToken(address token_, address to, uint256 amount) external onlyController {
        // send token to user
        IERC20(token_).transfer(to, amount);
    }

    function getFee() external view returns(uint8[3] memory feeArray) {
        feeArray = feeSetting;
    }

    function transfer(address to, uint256 amount) public virtual override returns (bool) {
        address owner = _msgSender();
        _transfer(owner, to, amount);
        return true;
    }

    function allowance(address owner, address spender) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public virtual override returns (bool) {
        address owner = _msgSender();
        _approve(owner, spender, amount);
        return true;
    }

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) public virtual override returns (bool) {
        address spender = _msgSender();
        _spendAllowance(from, spender, amount);
        _transfer(from, to, amount);
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        address owner = _msgSender();
        _approve(owner, spender, allowance(owner, spender) + addedValue);
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        address owner = _msgSender();
        uint256 currentAllowance = allowance(owner, spender);
        require(currentAllowance >= subtractedValue, "ERC20: decreased allowance below zero");
    unchecked {
        _approve(owner, spender, currentAllowance - subtractedValue);
    }

        return true;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal{
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(from != to, "ERC20: from = to");

        _beforeTokenTransfer(from, to, amount);

        uint256 fromBalance = _balances[from];
        require(
            fromBalance >= amount,
            "ERC20: transfer amount exceeds balance"
        );

        unchecked {
            _balances[from] = fromBalance - amount;
        }

        if (!(isExcludeFee[from] || isExcludeFee[to])) {
            uint256 fee = 0;
            // Transfer
            if (from != undPair && to != undPair) {
                fee = amount * feeSetting[2] / 100;
            }else {
                require(isOpenTrade, "Trade is Not Open.");
                // buy in
                if (from == undPair) {
                    fee = amount * feeSetting[0] / 100;
                }else{ // sell out
                    fee = amount * feeSetting[1] / 100;
                }
            }
            // fee bigger than zero
            if (fee > 0) {
                uint256 halfFee = fee / 2;
                uint256 otherHalfFee = fee - halfFee;

                _balances[fundAddr] += halfFee;
                _balances[treasury] += otherHalfFee;

                emit Transfer(from, fundAddr, halfFee);
                emit Transfer(from, treasury, otherHalfFee);
            }

            amount -= fee;
        }

        _balances[to] += amount;

        emit Transfer(from, to, amount);

        _afterTokenTransfer(from, to, amount);
    }

    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: mint to the zero address");

        _beforeTokenTransfer(address(0), account, amount);

        _totalSupply += amount;
        _balances[account] += amount;
        emit Transfer(address(0), account, amount);

        _afterTokenTransfer(address(0), account, amount);
    }

    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: burn from the zero address");

        _beforeTokenTransfer(account, address(0), amount);

        uint256 accountBalance = _balances[account];
        require(accountBalance >= amount, "ERC20: burn amount exceeds balance");
        unchecked {
            _balances[account] = accountBalance - amount;
        }
        _totalSupply -= amount;

        emit Transfer(account, address(0), amount);

        _afterTokenTransfer(account, address(0), amount);
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _spendAllowance(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        uint256 currentAllowance = allowance(owner, spender);
        if (currentAllowance != type(uint256).max) {
            require(currentAllowance >= amount, "ERC20: insufficient allowance");
            unchecked {
                _approve(owner, spender, currentAllowance - amount);
            }
        }
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}

    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}
}
