    "contracts/ROACHCOINStaking.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

abstract contract ROACHCOINContract is ERC20 {
    uint256 public _taxFee;
    uint256 public _liquidityFee;
}

/**
 * @title ROACHCOIN Staking Contract
 * @dev This contract allows users to stake ROACHCOIN tokens and earn rewards over time.
 * It also allows the contract owner to withdraw tokens and Ether from the contract,
 * while ensuring that staked amounts and rewards cannot be touched.
 */
contract ROACHCOINStaking is Ownable, ReentrancyGuard {
    ROACHCOINContract public ROACHCOIN; // ERC20 interface for ROACHCOIN tokens.
    uint256 public lockedROACHCOIN; // Amount of ROACHCOIN tokens that cannot be withdrawn by the owner.
    uint256 public constant DAYS_IN_MONTH = 30 days; // Used for time-based calculations.

    // Enumeration to represent staking durations.
    enum StakeDuration {
        OneMonth,
        TwoMonths,
        ThreeMonths,
        SixMonths,
        TwelveMonths
    }

    // Struct to represent a user's stake.
    struct Stake {
        uint256 amount;
        uint256 startTime;
        uint256 monthlyReward;
        uint256 rewardRedeemed;
        StakeDuration duration;
        bool unstaked;
    }

    // Mapping of user addresses to their stakes.
    mapping(address => Stake[]) public stakes;

    mapping(StakeDuration => uint256) public stakeDurationMonths;

    mapping(StakeDuration => uint256) public rewardPercentages;

    // Events to log various activities.
    event Staked(address indexed user, uint256 amount, StakeDuration duration);
    event RewardRedeemed(address indexed user, uint256 amount);
    event Unstaked(address indexed user, uint256 amount);

    /**
     * @dev Contract constructor.
     * @param _ROACHCOINAddress - The address of the ROACHCOIN token.
     */
    constructor(address _ROACHCOINAddress) {
        ROACHCOIN = ROACHCOINContract(_ROACHCOINAddress);

        // Initializing reward percentages for each staking duration.
        rewardPercentages[StakeDuration.OneMonth] = 300;
        rewardPercentages[StakeDuration.TwoMonths] = 400;
        rewardPercentages[StakeDuration.ThreeMonths] = 450;
        rewardPercentages[StakeDuration.SixMonths] = 500;
        rewardPercentages[StakeDuration.TwelveMonths] = 600;

        stakeDurationMonths[StakeDuration.OneMonth] = 1;
        stakeDurationMonths[StakeDuration.TwoMonths] = 2;
        stakeDurationMonths[StakeDuration.ThreeMonths] = 3;
        stakeDurationMonths[StakeDuration.SixMonths] = 6;
        stakeDurationMonths[StakeDuration.TwelveMonths] = 12;
    }

    /**
     * @dev Allows a user to stake their ROACHCOIN tokens.
     * @param amount - The amount of ROACHCOIN tokens to stake.
     * @param duration - The chosen staking duration.
     */
    function stake(
        uint256 amount,
        StakeDuration duration
    ) external nonReentrant {
        uint8 roachCoinDecimals = ROACHCOIN.decimals();
        require(
            amount >= 1 * 10 ** roachCoinDecimals,
            "Minimum staking amount is 1 ROACHCOIN"
        );
        require(
            ROACHCOIN.balanceOf(msg.sender) >= amount,
            "Insufficient ROACHCOIN balance"
        );
        require(
            ROACHCOIN.allowance(msg.sender, address(this)) >= amount,
            "The contract is not allowed to transfer ROACHCOIN on behalf of the user"
        );

        uint256 taxFeePercent = ROACHCOIN._taxFee();
        uint256 taxFee = (amount * taxFeePercent) / 10000; // Divide by 10,000 as percentage is in 100s

        uint256 liquidityFeePercent = ROACHCOIN._liquidityFee();
        uint256 liquidityFee = (amount * liquidityFeePercent) / 10000; // Divide by 10,000 as percentage is in 100s

        uint256 amountAfterFee = amount - taxFee - liquidityFee;

        uint256 totalDays = getDurationDays(duration);
        uint256 rewardPercentage = rewardPercentages[duration];

        uint256 monthlyReward = (amountAfterFee * rewardPercentage) / 10000; // Divide by 10,000 as percentage is in 100s
        uint256 totalReward = monthlyReward * (totalDays / DAYS_IN_MONTH);

        require(
            ROACHCOIN.balanceOf(address(this)) >= totalReward + lockedROACHCOIN,
            "Not enough ROACHCOIN in contract for rewards"
        );

        lockedROACHCOIN = lockedROACHCOIN + amountAfterFee + totalReward;

        stakes[msg.sender].push(
            Stake({
                amount: amountAfterFee,
                startTime: block.timestamp,
                monthlyReward: monthlyReward,
                rewardRedeemed: 0,
                duration: duration,
                unstaked: false
            })
        );

        ROACHCOIN.transferFrom(msg.sender, address(this), amount);

        emit Staked(msg.sender, amountAfterFee, duration);
    }

    /**
     * @dev Returns the stakes of a specific user.
     * @param user - Address of the user.
     * @return Array of user's stakes.
     */
    function getStakes(address user) external view returns (Stake[] memory) {
        return stakes[user];
    }

    /**
     * @dev Returns a specific stake of a user.
     * @param user - Address of the user.
     * @param index - Index of the stake in the user's stakes array.
     * @return The user's stake at the specified index.
     */
    function getStake(
        address user,
        uint256 index
    ) external view returns (Stake memory) {
        require(index < stakes[user].length, "Invalid stake index");

        return stakes[user][index];
    }

    /**
     * @dev Allows a user to redeem their earned rewards.
     * @param stakeIndex - Index of the stake in the user's stakes array.
     */
    function redeemRewards(uint256 stakeIndex) external nonReentrant {
        require(stakeIndex < stakes[msg.sender].length, "Invalid stake index");

        Stake storage userStake = stakes[msg.sender][stakeIndex];

        uint256 maxMonths = stakeDurationMonths[userStake.duration];

        uint256 monthsPassed = (block.timestamp - userStake.startTime) /
            DAYS_IN_MONTH;
        monthsPassed = monthsPassed > maxMonths ? maxMonths : monthsPassed;

        uint256 eligibleReward = userStake.monthlyReward *
            monthsPassed -
            userStake.rewardRedeemed;

        require(eligibleReward > 0, "No rewards to redeem");

        ROACHCOIN.transfer(msg.sender, eligibleReward);

        userStake.rewardRedeemed += eligibleReward;

        lockedROACHCOIN -= eligibleReward;

        emit RewardRedeemed(msg.sender, eligibleReward);
    }

    /**
     * @dev Allows a user to unstake their tokens after the staking period is over.
     * @param stakeIndex - Index of the stake in the user's stakes array.
     */
    function unstake(uint256 stakeIndex) external nonReentrant {
        require(stakeIndex < stakes[msg.sender].length, "Invalid stake index");

        Stake storage userStake = stakes[msg.sender][stakeIndex];

        require(!userStake.unstaked, "Already unstaked");

        uint256 totalDays = getDurationDays(userStake.duration);
        require(
            userStake.startTime + totalDays <= block.timestamp,
            "Tokens can only be unstaked after the chosen duration"
        );

        uint256 unstakeAmount = userStake.amount;

        userStake.unstaked = true;

        lockedROACHCOIN -= unstakeAmount;

        ROACHCOIN.transfer(msg.sender, unstakeAmount);

        emit Unstaked(msg.sender, unstakeAmount);
    }

    /**
     * @dev Allows the contract owner to withdraw any Ether held by the contract.
     */
    function withdrawEther() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }

    /**
     * @dev Allows the contract owner to withdraw tokens other than the staked ROACHCOIN.
     * @param token - Address of the token to be withdrawn.
     * @param amount - Amount of tokens to withdraw.
     */
    function withdrawToken(address token, uint256 amount) external onlyOwner {
        if (token == address(ROACHCOIN)) {
            uint256 maxWithdrawal = ROACHCOIN.balanceOf(address(this)) -
                lockedROACHCOIN;

            require(
                amount <= maxWithdrawal,
                "Cannot withdraw this amount of ROACHCOIN"
            );
        }

        IERC20 tokenInstance = IERC20(token);

        tokenInstance.transfer(owner(), amount);
    }

    // Converts the staking duration from StakeDuration enum to days.
    function getDurationDays(
        StakeDuration duration
    ) internal view returns (uint256) {
        return stakeDurationMonths[duration] * DAYS_IN_MONTH;
    }

    /**
     * @dev Fallback function to allow the contract to receive Ether.
     */
    receive() external payable {}
