    }
    struct feeTable {
        uint256 tFee;
        uint256 tLiquidity;
        uint256 tCharity;
        uint256 tMarketing;
    }
    
    function _getTxAmountMinusFee(uint256 tAmount, feeTable memory ft) private pure returns (uint256) {
        return tAmount.sub(ft.tFee).sub(ft.tLiquidity).sub(ft.tCharity).sub(ft.tMarketing);
    }
    function _getTValues(uint256 tAmount) private view returns (txAmountAndFee memory) {
        // 4 taxes
        feeTable memory ft = feeTable(
            calculateFeeTenth(_taxFee, tAmount),
            calculateFeeTenth(_liquidityFee, tAmount),
            calculateFeeTenth(_charityFee, tAmount),
            calculateFeeTenth(_devMarketingFee, tAmount)
        );

        // subtract 5 taxes from amount
        return txAmountAndFee(_getTxAmountMinusFee(tAmount, ft), ft );
        
    }

    // returns rAmount, rTransferAmount, rFee
    function _getRValues(txAmountAndFee memory txa, uint256 currentRate) private pure returns (uint256, uint256, uint256) {
        uint256 rAmount = txa.amount.mul(currentRate);
        feeTable memory rft = feeTable(
            txa.fees.tFee.mul(currentRate), 
            txa.fees.tLiquidity.mul(currentRate),
            txa.fees.tCharity.mul(currentRate),
            txa.fees.tMarketing.mul(currentRate)
        );
        return (rAmount, rAmount.sub(rft.tFee).sub(rft.tLiquidity).sub(rft.tCharity).sub(rft.tMarketing), rft.tFee);
    }

