    IERC20 private _dot;
    IERC20 private _shib;

    uint256 public tradingEnabledTimestamp;

    constructor() ERC20("Dotty Token", unicode"Dotty🎁", 22222 * 10**decimals()) {
        _mint(msg.sender, 22222 * 10**decimals());
        swapEnabled = true;
        _lpFeeRate = 400;
