    "contracts/Minter.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

interface IPandraKing {
    function mint(address user) external;
}

contract Minter is Ownable, ReentrancyGuard {
    using ECDSA for bytes32;

    event NFTClaimed(address indexed user, NFTLevel nftLevel, address pandraKingAddress);

    enum NFTLevel {
        Uncommon,
        Rare,
        Epic,
        Legendary
    }

    address public authorizedSigner;
    uint256 public mintStartTime;
    uint256 public mintEndTime;
    mapping(address => bool) public hasClaimedNFT;
    mapping(NFTLevel => address) public nftContracts;

    constructor(
        address _pandraKingUncommonAddress,
        address _pandraKingRareAddress,
        address _pandraKingEpicAddress,
        address _pandraKingLegendaryAddress,
        address _authorizedSigner,
        uint256 _mintStartTime,
        uint256 _mintEndTime
    ) {

        require(_authorizedSigner != address(0), "Authorized signer address cannot be zero");
        require(_mintEndTime >= _mintStartTime, "Invalid time range");
        require(_pandraKingUncommonAddress != address(0), "Uncommon contract address cannot be zero");
        require(_pandraKingRareAddress != address(0), "Rare contract address cannot be zero");
        require(_pandraKingEpicAddress != address(0), "Epic contract address cannot be zero");
        require(_pandraKingLegendaryAddress != address(0), "Legendary contract address cannot be zero");

        nftContracts[NFTLevel.Uncommon] = _pandraKingUncommonAddress;
        nftContracts[NFTLevel.Rare] = _pandraKingRareAddress;
        nftContracts[NFTLevel.Epic] = _pandraKingEpicAddress;
        nftContracts[NFTLevel.Legendary] = _pandraKingLegendaryAddress;

        authorizedSigner = _authorizedSigner;
        mintStartTime = _mintStartTime;
        mintEndTime = _mintEndTime;
    }

    function mintWithSignature(
        address user,
        NFTLevel nftLevel,
        bytes memory signature
    ) external nonReentrant {
        require(
            block.timestamp >= mintStartTime && block.timestamp <= mintEndTime,
            "Minting is not allowed at this time"
        );
        require(!hasClaimedNFT[user], "User has already claimed NFT");

        bytes32 messageHash = keccak256(abi.encodePacked(user, nftLevel));
        bytes32 message = ECDSA.toEthSignedMessageHash(messageHash);
        address recoveredSigner = message.recover(signature);

        require(recoveredSigner == authorizedSigner, "Invalid signature");

        address nftContractAddress = nftContracts[nftLevel];
        require(nftContractAddress != address(0), "NFT contract address not set");
        IPandraKing(nftContractAddress).mint(user);

        hasClaimedNFT[user] = true;
        emit NFTClaimed(user, nftLevel, nftContractAddress);
    }

    function updateAuthorizedSigner(
        address _newAuthorizedSigner
    ) external onlyOwner {
         require(_newAuthorizedSigner != address(0), "Authorized signer address cannot be zero");
        authorizedSigner = _newAuthorizedSigner;
    }

    function updateMintTime(
        uint256 _newStartTime,
        uint256 _newEndTime
    ) external onlyOwner {
        require(_newEndTime >= _newStartTime, "Invalid time range");
        mintStartTime = _newStartTime;
        mintEndTime = _newEndTime;
    }

    function setNFTContractAddress(NFTLevel level, address newContractAddress) external onlyOwner {
        require(newContractAddress != address(0), "New contract address cannot be zero");
        nftContracts[level] = newContractAddress;
    }
}
