contract AmazyMarketplace is AccessControl, ReentrancyGuard, ERC721Holder, Pausable {
    using Address for address;
    using EnumerableSet for EnumerableSet.AddressSet;

    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
        
    uint256 public sellId;
    uint256 public onSale;
    uint256 public fee;
    uint256 public constant denominator = 1000;
    address public feeAddr;

    EnumerableSet.AddressSet private contractsWhitelist;
    
    constructor() {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(PAUSER_ROLE, _msgSender());
        feeAddr = 0x6DF58F91F8Bdc7f5b17E0cAD3e3FC5d5ea4c92bC;
        fee = 50; // 5%, denominator is constant 1000
    }

    mapping(address => mapping(uint256 => uint256)) idByToken;

    mapping (uint256 => Sales) sale;

    struct Sales {
        address seller;
        address buyer;
        address c721;
        uint256 tokenId;
        uint256 price;
        uint256 fee;
        bool    active;
    }
  
    event Sell(uint256 indexed _id, uint256 indexed _tokenId, uint256 indexed _price, address _from, address _contract721);
    event Cancel(uint256 indexed _id);
    event Buy(uint256 indexed _id, uint256 indexed _price, address indexed _buyer);
    event ChangeContractWhiteList(address indexed _contract, bool indexed);
    event ChangeFee(uint256 indexed _feeAmount, address indexed _feeAddress);
    
    function sellIdByToken (address _contract721, uint256 _tokenId) external view returns (uint256 _sellId) {
        return (idByToken[_contract721][_tokenId]);
    }

    function sellInfo(uint256 _id) external view returns (address seller, address buyer, address contract721, uint256 tokenId, uint256 price, bool active) {
        return (sale[_id].seller, sale[_id].buyer, sale[_id].c721, sale[_id].tokenId, sale[_id].price, sale[_id].active);
    }

    function whitelistedContracts() external view returns (address[] memory) {
        return contractsWhitelist.values();
    }

    function sell(address _contract721, uint256 _tokenId, uint256 _price) external nonReentrant whenNotPaused() returns (uint256 _id) { 
        require(_price != 0, "Price cannot be zero.");
        require(_price % denominator == 0, "Price in wei must be a multiple of denominator. Remember the decimals.");
        require(contractsWhitelist.contains(_contract721), "ERC721 contract is not in whitelist.");
        IERC721(_contract721).safeTransferFrom(_msgSender(), address(this), _tokenId);
        sellId++;
        onSale++;
        idByToken[_contract721][_tokenId] = sellId;
        sale[sellId] = Sales(_msgSender(), address(0), _contract721, _tokenId, _price, fee, true);
        emit Sell(sellId, _tokenId, _price, _msgSender(), _contract721);
        return sellId;
    }
    
    function cancel(uint256 _id) external nonReentrant {
        require(sale[_id].seller == _msgSender(), "You are not a seller.");
        require(sale[_id].active, "Sell is not active.");
        sale[_id].active = false;
        onSale--;
        idByToken[sale[_id].c721][sale[_id].tokenId] = 0;
        IERC721(sale[_id].c721).safeTransferFrom(address(this), _msgSender(), sale[_id].tokenId);
        emit Cancel(_id);
    }
    
    function buy(uint256 _id) external payable nonReentrant whenNotPaused() {
        require(sale[_id].active, "Sell is not active.");
        require(msg.value == sale[_id].price, "Coins amout not right.");
        uint256 _fee = sale[_id].price * sale[_id].fee / denominator;
        sale[_id].buyer = _msgSender();
        sale[_id].active = false;
        onSale--;
        idByToken[sale[_id].c721][sale[_id].tokenId] = 0;
        (bool succ1, ) = payable(sale[_id].seller).call{value: sale[_id].price - _fee}("");
        require(succ1, "Transfer to seller not successful.");
        (bool succ2, ) = payable(feeAddr).call{value: _fee}("");
        require(succ2, "Fee transfer not successful.");
        IERC721(sale[_id].c721).safeTransferFrom(address(this), _msgSender(), sale[_id].tokenId);
        emit Buy(_id, sale[_id].price, _msgSender());
    }

    function addToWhitelist(address _contract) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "You must have admin role to add contract to whitelist.");
        require(_contract.isContract(), "Address is not a contract.");
        require(contractsWhitelist.add(_contract), "Contact is already in whitelist.");
        emit ChangeContractWhiteList(_contract, true);
    }

    function removeFromWhitelist(address _contract) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "You must have admin role to remove contract from whitelist.");
        require(contractsWhitelist.remove(_contract), "Where is no such contract in whitelist.");
        emit ChangeContractWhiteList(_contract, false);
    }
    
    function changeFee(uint256 _fee, address _feeAddr) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "You must have admin role to change fee.");
        require(_fee <= 300, "Fee cannot be more than 30%.");  // 30%, denominator is constant 1000
        require(_feeAddr != address(0), "Wrong fee address.");
        fee = _fee;
        feeAddr = _feeAddr;
        emit ChangeFee(_fee, _feeAddr);
    }

    function pause() external onlyRole(PAUSER_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(PAUSER_ROLE) {
        _unpause();
    }
}
