    "/contracts/NTT1155.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Burnable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "./INTT1155.sol";

contract NTT1155 is INTT1155, ERC1155, AccessControl {
    /**
     * @dev Event emits after token minting
     */
    event MintNTT(address indexed account, uint256 indexed tokenId, Metadata metadata);
    /**
     * @dev Event emits after user attempt to mint token
     */
    event MintNTTValidate(address indexed account, uint256 indexed tokenId, uint256 indexed mintPrice);
    /**
     * @dev Event emits after token status switched from revoke to active
     */
    event ActivatetNTT(address indexed account, uint256 indexed tokenId, Metadata metadata);
    /**
     * @dev Event emits after token status switched from active to revoke
     */
    event RevokeNTT(address indexed account, uint256 indexed tokenId, Metadata metadata);
    /**
     * @dev Event emits after updated token exiration time
     */
    event UpdateExpirationNTT(address indexed account, uint256 indexed tokenId, Metadata metadata);

    /**
     * @dev mapping to save tokens Ids by their owner adress
     */
    mapping(address => uint256[]) private _nttBalances;

    /**
     * @dev mapping to save owners adresses by tokens Id
     */
    mapping(uint256 => address) private _nttOwners;

    /**
     * @dev mapping to save tokens metadata by their owner adress
     */
    mapping (uint256 => Metadata) _nttMetadata;

    /**
     * @dev enum which contains type of contract
     */
    ContractType private _contractType;

    bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");
    bytes32 public constant MINT_ROLE = keccak256("MINT_ROLE");
    bytes32 public constant URI_SET_ROLE = keccak256("URI_SET_ROLE");
    bytes32 public constant TRANSFER_ROLE = keccak256("TRANSFER_ROLE");

    /**
     * @dev constant to explain that token mints only once
     */
    uint8 internal constant TOKEN_MINT_COUNT = 1;

    string internal _tokenName = "";
    string internal _tokenSymbol = "";
    string internal _contractUri = "";
    address payable internal _mintAddress;

    constructor(ContractType contractType, string memory contractUri, string memory tokenName, string memory tokenSymbol, address payable mintAddress) ERC1155("") {
    /**
     * @dev set contract type
     */
        _contractType = contractType;
        _tokenName = tokenName;
        _tokenSymbol = tokenSymbol;
        _contractUri = contractUri;
        _mintAddress = mintAddress;

    /**
     * @dev add all roles for Deployer
     */
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(MANAGER_ROLE, msg.sender);
        _grantRole(MINT_ROLE, mintAddress);
        _grantRole(URI_SET_ROLE, msg.sender);
        _grantRole(TRANSFER_ROLE, msg.sender);
    }

    function name() public view returns(string memory) {
        return _tokenName;
    }

    function symbol() public view returns (string memory) {
        return _tokenSymbol;
    }

    /**
     * @dev modifier which allows to work with only existed token
     */
    modifier isExistNTT(uint256 tokenId) override {
        require(
            _nttOwners[tokenId] != address(0),
            "NTT1155: Token does not exist"
        );
        _;
    }

    /**
     * @dev modifier which allows to work with when there is no token with passed id
     */
    modifier isNotExistNTT(uint256 tokenId) override {
        require(
            _nttOwners[tokenId] == address(0),
            "NTT1155: Token is already exist"
        );
        _;
    }

    /**
     * @dev Revokes token.
     *
     * Requirements:
     * - the caller must have role  MANAGER_ROLE
     *
     * Emits {ActivatetNTT} event.
     */
    function activate(uint256 tokenId) public override isExistNTT(tokenId) onlyRole(MANAGER_ROLE) {
        _nttMetadata[tokenId].isActive = true;

        emit ActivatetNTT(msg.sender, tokenId, _nttMetadata[tokenId]);
    }

    /**
     * @dev Revokes token.
     *
     * Requirements:
     * - the caller must have role  MANAGER_ROLE
     * - or to be the token owner
     *
     * Emits {RevokeNTT} event.
     */
    function revoke(uint256 tokenId) public override isExistNTT(tokenId) {
        require(
            _nttOwners[tokenId] == msg.sender || hasRole(MANAGER_ROLE, msg.sender),
            "NTT1155: Failed to revoke token"
        );
        _nttMetadata[tokenId].isActive = false;

        emit RevokeNTT(msg.sender, tokenId, _nttMetadata[tokenId]);
    }
    
    function statusActive() public pure override returns(TokenStatus) {
        return TokenStatus.ACTIVE;
    }
    
    function statusExpired() public pure override returns(TokenStatus) {
        return TokenStatus.EXPIRED;
    }
    
    function statusRevoked() public pure override returns(TokenStatus) {
        return TokenStatus.REVOKED;
    }

     /**
     * @dev Updates expiration time for token.
     *
     * Requirements:
     * - the caller must have role  MANAGER_ROLE
     *
     * Emits {UpdateExpirationNTT} event.
     */
    function updateNttExpirationPeriod(uint256 tokenId, uint expirationPeriod) public override isExistNTT(tokenId) onlyRole(MANAGER_ROLE) {
        _nttMetadata[tokenId].expirationPeriod = expirationPeriod;

        emit UpdateExpirationNTT(msg.sender, tokenId, _nttMetadata[tokenId]);
    }

    function updateMintAddress(address payable _address) public onlyRole(MANAGER_ROLE) {
        _mintAddress = _address;
    }

    function nttMintSafe(
        uint256 tokenId
    ) public payable override isNotExistNTT(tokenId) {
        bool success = payable(_mintAddress).send(msg.value);
        require(success, "Failed to transact amount for mint");
        emit MintNTTValidate(msg.sender, tokenId, msg.value);
    }

    /**
    * @dev Final token mint after token validation passed
     *
     * Requirements:
     * - the caller must have role  MINT_ROLE
     * 
     * Emits {TransferSingle} event.
     * Emits {UpdateExpirationNTT} event.
     */
    function nttMintConfirm(uint256 tokenId, address tokenReceiver, Metadata memory metadata) public override isNotExistNTT(tokenId) onlyRole(MINT_ROLE) {
        _nttBalances[tokenReceiver].push(tokenId);
        _nttOwners[tokenId] = tokenReceiver;
        _mint(tokenReceiver, tokenId, TOKEN_MINT_COUNT, "");
        _nttMetadata[tokenId] = metadata;

        emit MintNTT(tokenReceiver, tokenId, metadata);
    }

    /**
     * @dev Returns token status {TokenStatus}
     * 
     * Token could be active, revoked, expired
     *
     * Requirements:
     * - runs only for existing tokens
     */
    function nttStatus(uint256 tokenId) public override isExistNTT(tokenId) view returns (TokenStatus) {
        TokenStatus _tokenStatus = TokenStatus.REVOKED;

        if (block.timestamp > _nttMetadata[tokenId].kycTime + _nttMetadata[tokenId].expirationPeriod) {
            _tokenStatus = TokenStatus.EXPIRED;
        } else if (_nttMetadata[tokenId].isActive) {
            _tokenStatus = TokenStatus.ACTIVE;
        }

        return _tokenStatus;
    }

    /**
     * @dev Returns token owner adress
     *
     * Requirements:
     * - the caller must have role  MANAGER_ROLE
     * - runs only for existing tokens
     */
    function nttOwnerOf(uint256 tokenId) public override view  onlyRole(MANAGER_ROLE) returns (address) {
        return _nttOwners[tokenId];
    }

    /**
     * @dev Returns array of minted tokens for sender
     */
    function nttBalance() public override view returns (uint256[] memory) {
        address account = _msgSender();

        return _nttBalanceOf(account);
    }

    /**
     * @dev Returns array of minted tokens for adress passed in function
     */
    function nttBalanceOf(address account) public override view returns (uint256[] memory) {
        return _nttBalanceOf(account);
    }

    /**
     * @dev Returns computed metadata {ComputedMetadata} by passed tokenId
     * 
     * Requirements:
     * - runs only for existing tokens
     */
    function getNttMetadata(uint256 tokenId) public override isExistNTT(tokenId) view returns(ComputedMetadata memory) {
        Metadata memory m = _nttMetadata[tokenId];
        bool isValidExpirationTime = nttStatus(tokenId) != TokenStatus.EXPIRED;

        return ComputedMetadata(
            m.investorType,
            m.countryCode,
            m.isActive,
            m.kycTime,
            m.expirationPeriod,
            m.ipfsUri,
            isValidExpirationTime
        );
    }

    /**
     * @dev Returns array of minted tokens for adress passed in function
     */
    function _nttBalanceOf(address account) internal view returns (uint256[] memory) {
        return _nttBalances[account];
    }

    /**
     * @dev Returns type of contract {ContractType}
     */
    function getContractType() public override view returns (ContractType) {
        return _contractType;
    }

    /**
     * @dev Set URI for IPFS
     * @dev See {ERC1155-_setURI}.
     *
     * Requirements:
     * - the caller must have role  URI_SET_ROLE
     */
    function setURI(string memory newContractUri_) public onlyRole(URI_SET_ROLE) {
        _contractUri = newContractUri_;
    }

    function contractURI() public view returns (string memory) {
        return _contractUri;
    }

    function uri(uint256 tokenId) public view override isExistNTT(tokenId) returns (string memory) {
        return _nttMetadata[tokenId].ipfsUri;
    }

    /**
     * @dev See {ERC1155-supportsInterface}.
     */
    function supportsInterface(bytes4 interfaceId) public view override(ERC1155, AccessControl) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

     /**
     * @dev See {IERC1155-safeTransferFrom}.
     *
     * Requirements:
     * - the caller must have role  TRANSFER_ROLE
     */
    function safeTransferFrom(
        address from,
        address to,
        uint256 id,
        uint256 amount,
        bytes memory data
    ) public override(ERC1155) onlyRole(TRANSFER_ROLE) {
        super.safeTransferFrom(from, to, id, amount, data);
    }

     /**
     * @dev See {IERC1155-safeBatchTransferFrom}.
     *
     * Requirements:
     * - the caller must have role  TRANSFER_ROLE
     */
    function safeBatchTransferFrom(
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) public override(ERC1155) onlyRole(TRANSFER_ROLE) {
        super.safeBatchTransferFrom(from, to, ids, amounts, data);
    }

    /**
     * @dev See {ERC1155-setApprovalForAll}.
     *
     * Requirements:
     * - the caller must have role  TRANSFER_ROLE
     */
    function setApprovalForAll(address operator, bool approved) public override onlyRole(TRANSFER_ROLE) {
        super.setApprovalForAll(operator, approved);
    }

    /**
     * @dev See {ERC1155-isApprovedForAll}.
     *
     * Requirements:
     * - the caller must have role  TRANSFER_ROLE
     */
    function isApprovedForAll(address account, address operator) public view override onlyRole(TRANSFER_ROLE) returns (bool) {
        return super.isApprovedForAll(account, operator);
    }

    /**
     * @dev See {ERC1155-balanceOfBatch}.
     *
     * Requirements:
     * - the caller must have role  TRANSFER_ROLE
     */
    function balanceOfBatch(address[] memory accounts, uint256[] memory ids) public view override onlyRole(TRANSFER_ROLE) returns (uint256[] memory) {
        return super.balanceOfBatch(accounts, ids);
    }
}"
