    "contracts/Dispatcher.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;
import "@openzeppelin/contracts/utils/structs/EnumerableMap.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./libraries/TokenUtils.sol";

contract Dispatcher is Ownable {
    using SafeMath for uint256;
    using EnumerableMap for EnumerableMap.AddressToUintMap;
    using TokenUtils for address;
    address _token;
    string  _city;
    string  _contractType;



    constructor(string memory city_, string memory contractType_) {
        _city = city_;
        _contractType = contractType_;
    }

    function city() public view returns (string memory) {
        return _city;
    }
    function contractType() public view returns (string memory) {
        return _contractType;
    }
    function token() public view returns (address){
        return _token;
    }

    function setToken(address token_) public onlyOwner {
        _token = token_;
    }

    EnumerableMap.AddressToUintMap dispatcher;
    uint256 constant BASE = 1000000;

    function addShareHolder(address shareHolder, uint256 numerator, uint256 denominator) public onlyOwner {
        require(denominator > 0, "DZE");
        require(denominator >= numerator , "DNE");
        require(BASE >= denominator , "BDE");
        uint256 share = BASE.mul(numerator).div(denominator);
        dispatcher.set(shareHolder, share);
    }

    function batchAddShareHolder(address[] memory shareHolders, uint256[] memory numerators, uint256[] memory denominators) public onlyOwner {
        require((shareHolders.length == numerators.length) && (shareHolders.length == denominators.length), "LE");
        for (uint i =0; i < shareHolders.length; i++) {
            addShareHolder(shareHolders[i], numerators[i], denominators[i]);
        }
    }



    function is100P() public view returns (bool) {
        uint256 totalShare = 0;
        for (uint i = 0; i < dispatcher.length(); i++) {
            (, uint256 share) = dispatcher.at(i);
            totalShare += share; 
        }
        return totalShare == BASE;
    }

    function getDispatcher() public view returns (address[] memory, uint256[] memory){
        uint256 length = dispatcher.length();
        address[] memory shareHolers = new address[](length);
        uint256[] memory shares = new uint256[](length);

        for (uint i = 0; i < length; i++) {
            (address shareHoler, uint256 share) = dispatcher.at(i);
            shareHolers[i] = shareHoler;
            shares[i] = share;
        }
        return (shareHolers,shares);
    }

    function dispatch(uint256 amount) public onlyOwner {
        require(is100P(), "not 100%");
        for (uint i = 0; i < dispatcher.length(); i++) {
            (address shareHolder, uint256 share) = dispatcher.at(i);
            if (share != 0) {
                token().transfer(shareHolder,amount.mul(share).div(BASE));
            }
        }
    } 

}








