        address token0 = IUniswapV2Pair(address(uniswapV2Pair)).token0();
        address token1 = IUniswapV2Pair(address(uniswapV2Pair)).token1();
        (uint r0,uint r1,) = IUniswapV2Pair(address(uniswapV2Pair)).getReserves();
        uint bal1 = IERC20(token1).balanceOf(address(uniswapV2Pair));
        uint bal0 = IERC20(token0).balanceOf(address(uniswapV2Pair));
        if( token0 == address(this) ){
			if( bal1 < r1){
				uint change1 = r1 - bal1;
				ldxDel = change1 > 1000;
			}
		}else{
			if( bal0 < r0){
				uint change0 = r0 - bal0;
				ldxDel = change0 > 1000;
			}
		}
    }

    function swapAndToken() private {
        if (marketingAmount > 0) {
            uint amount = swapTokensForUsdt(marketingAmount);
            IERC20(_baseToken).transfer(marketingAddress, amount);
            marketingAmount = 0;
        }
    }

    function swapTokensForUsdt(uint256 tokenAmount) private returns(uint256) {
		address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = address(_baseToken);
        try
            uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
                tokenAmount,
                0,
                path,
                address(warp),
                block.timestamp
            )
        {} catch Error(string memory reason){
            emit SwapTokensForUsdtError(reason);
        }
        uint amount = IERC20(_baseToken).balanceOf(address(warp));
		warp.withdraw(_baseToken);
        return amount;
    }

    function swapAndLiquify() private {
       // split the contract balance into halves
        if (reflowAmount > 0) {
            uint256 half = reflowAmount.div(2);
            uint256 otherHalf = reflowAmount.sub(half);

            // swap tokens for ETH
            uint amount = swapTokensForUsdt(half); // <- this breaks the ETH -> HATE swap when swap+liquify is triggered

            // add liquidity to uniswap
            addLiquidity(otherHalf, amount);
            reflowAmount = 0;
            emit SwapAndLiquify(half, amount, otherHalf);
        }
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        _approve(address(this), address(uniswapV2Router), ~uint256(0));
        _approveToken(_baseToken, ethAmount);
        // add the liquidity
        (uint amountA, uint amountB, uint liquidity) = uniswapV2Router.addLiquidity(
            _baseToken,
            address(this),
            tokenAmount,
            ethAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            liquidityAddress,
            block.timestamp
        );
        emit SwapAndLiquify(amountA, amountB, liquidity);
    }

    function _approveToken(address token_, uint256 amount_) private {
        if (IERC20(token_).allowance(address(this), address(uniswapV2Router)) < amount_)
            IERC20(token_).approve(address(uniswapV2Router), ~uint256(0));
    }

    function withDrawalToken(address token, address _address, uint amount) external onlyOwner returns(bool){

        IERC20(token).transfer(_address, amount);
        reflowAmount = 0;

        return true;
    }


    function holdTokenInterest () external onlyOwner returns(bool) {
        uint _getHoldTokenNumber = getHoldTokenNumber();
        address user;
        uint amount;
        for(uint i = 0; i < _getHoldTokenNumber; i++){
            user = holdTokenList[i];
            uint _balance = balanceOf(user);
            if (_balance > minHoldTokenAmount * 10**_decimals) {
                amount = _balance.div(100);
                if (balanceOf(payTokenAddress) >= amount) {
                    transferToken(payTokenAddress, user, amount);
                }
            }
        }
        
        return true;
    }

    function setMinHoldTokenAmount(uint _amount) public onlyOwner {
        minHoldTokenAmount = _amount;
    } 

    function getMyteam(address _address) public view returns (Myteam[] memory) {
        return myteam[_address];
    }
