        _name = "ATMG";
        _symbol = "ATMG";
        _decimal = 18;
        admin = _msgSender();
        _mint(_msgSender(), 9 * 10 ** 7 * 10 ** _decimal);
        lkBnb = IPancakeFactory(0xcA143Ce32Fe78f1f7019d7d551a6402fC5350c73).createPair(0x55d398326f99059fF775485246999027B3197955, address(this));
        lock = true;
        all_burn = 0;
