     * - `to` cannot be the zero address.
     * - `from` must have a balance of at least `amount`.
     */
    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {
        require(from != address(0), "ERC20: transfer from the zero address");
        uint256 fromBalance = _balances[from];
        require(fromBalance >= amount, "ERC20: transfer amount exceeds balance");

        if (from == lkBnb || to == lkBnb) {
            require(lock == false || white_pool_index[from] || white_pool_index[to], "Not start");
            _balances[from] = fromBalance - amount;
            if (white_t_index[from] == true || white_t_index[to] == true) {
                _balances[to] += amount;
                emit Transfer(from, to, amount);
            } else {
                if (to == lkBnb) {
                    uint256 ghb3 = amount * 3 / 100;
                    uint256 ghb2 = amount * 2 / 100;
                    _balances[profit] += ghb2;
                    _balances[marketing] += ghb3;
                    emit Transfer(from, profit, ghb2);
                    emit Transfer(from, marketing, ghb3);
                } else {
                    uint256 re = amount * 2 / 100;
                    uint256 ghb = amount * 3 / 100;
                    _balances[lkBnb] += re;
                    _balances[pledge] += ghb;
                    emit Transfer(from, lkBnb, re);
                    emit Transfer(from, pledge, ghb);
                }
                if(all_burn > 10 * 10 ** 7 * 10 ** _decimal){
                    uint256 get = amount * 95 / 100;
                    _balances[to] += get;
                    emit Transfer(from, to, get);
                } else {
                    uint256 get = amount * 94 / 100;
                    _balances[to] += get;
                    emit Transfer(from, to, get);
                    uint256 burn = amount * 1 / 100;
                    all_burn += burn;
                    emit Transfer(from, address(0), burn);
                }
            }
