// SPDX-License-Identifier: MIT
pragma solidity 0.8.16;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableMap.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";


contract LiveToEarn is ReentrancyGuard, Pausable, Ownable {

    event NFT721Received(address operator, address from, uint256 erc721Id, bytes data);
    event NFT1155Received(address operator, address from, uint256 erc721Id, uint256 amount, bytes data);
    event NFT1155BatchReceived(address operator, address from, uint256[] tokenIds, uint256[] amounts, bytes data);


    event eUpdateSigner(
        address signer,
        uint256 blockNum
    );

    event eUpdateVault(
        address vault,
        uint256 blockNum
    );

    event eCreateTransferInfo(
        uint256 ud,
        address costErc20,
        uint256 costAmount,
        address createErc721,
        uint256 erc721Id,
        uint256 startTime,
        address owner
    );

    event eCost20Info(
        uint256 ud,
        address uAddress,
        address costErc20,
        uint256 costAmount,
        uint256 startTime,
        address owner
    );

    event eCost1155Info(
        uint256   ud,
        address   uAddress,
        address   costErc1155,
        uint256[] costIds,
        uint256[] costAmounts,
        uint256   startTime,
        address owner
    );

    event eStake20Info(
        uint256 ud,
        address uAddress,
        address stakeErc20,
        uint256 stakeAmount,
        uint256 startTime,
        address owner
    );

    event eStake721Info(
        uint256 ud,
        address uAddress,
        address erc721,
        uint256 stakeId,
        uint256 startTime,
        address owner
    );

    event eStake1155Info(
        uint256 ud,
        address uAddress,
        address erc1155,
        uint256[] ids,
        uint256[] amounts,
        uint256 startTime,
        address owner
    );

    event eRoleWithdraw( 
       address erc721,
       uint256 erc721Id, 
       address owner, 
       uint256 timestamp
    );

    event eWithdraw1155( 
       uint256 ud,
       address uAddress,
       address erc1155,
       uint256[] ids, 
       uint256[] amounts, 
       address owner, 
       uint256 timestamp
    );

    event eWithdraw721( 
       uint256 ud,
       address uAddress,
       address erc721,
       uint256 erc721Id, 
       address owner, 
       uint256 timestamp
    );

    event eWithdraw20( 
       uint256 ud,
       address uAddress,
       address stakeErc20, 
       uint256 stakeAmount, 
       address owner, 
       uint256 timestamp
    );

   enum LogicType {
        eStake20,
        eStake721,
        eStake1155,
        eCost20,
        eCost1155
    }

    struct RoleStakeInfo{
        address erc721;
        uint256 erc721Id;
    }

    /////////////

    struct LogicStake20Info{
        uint256 ud;
        address uAddress;
        address stakeErc20;
        uint256 stakeAmount;
        uint256 startTime;
    }

    struct LogicStake721Info{
        uint256 ud;
        address uAddress;
        address erc721;
        uint256 erc721Id;
        uint256 startTime;
    }

    struct LogicStake1155Info{
        uint256 ud;
        address uAddress;
        address erc1155;
        uint256[] ids;
        uint256[] amounts;
        uint256 startTime;
    }

   struct TransferInfo {
        uint256 ud;
        address uAddress;

        address costErc20;  
        uint256 costAmount;    

        address erc1155;
        uint256[] ids;
        uint256[] amounts;

        address stakeErc20;  
        uint256 stakeAmount;   

        address erc721; 
        uint256 erc721Id;

        address who;   
    }

    struct WithdrawInfo{
        address uAddress;
        uint256 ud;
        address who;
    }
    //type hash
    bytes32 public constant TYPE_HASH = keccak256(
        "TransferInfo(uint256 ud,address uAddress,address costErc20,uint256 costAmount,address erc1155,uint256[] ids,uint256[] amounts,address stakeErc20,uint256 stakeAmount,address erc721,uint256 erc721Id,address who)"
    );
    bytes32 public constant TYPE_WITHDRAW_HASH = keccak256(
        "WithdrawInfo(address uAddress,uint256 ud,address who)"
    );

    struct EIP712Domain {
        string  name;
        string  version;
        uint256 chainId;
        address verifyingContract;
    }

    using ECDSA for bytes32;
    using EnumerableSet for EnumerableSet.Bytes32Set;
    using EnumerableSet for EnumerableSet.UintSet;
    using EnumerableSet for EnumerableSet.AddressSet;
    using EnumerableMap for EnumerableMap.AddressToUintMap;

    using SafeERC20 for IERC20;
    using Address for address;

    bytes32 public immutable DOMAIN_SEPARATOR;
    bytes32 public constant EIP712DOMAIN_TYPEHASH = keccak256(
        "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"
    );

    address public _SIGNER;
    address public _VAULT;
    uint256 public _MAX = 0xffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff;


    // for players
    mapping( address => EnumerableSet.AddressSet ) private _roleStakeErc721s;
    mapping( address =>  mapping(address => EnumerableSet.UintSet )) private _roleStakeIDs;

    mapping( address => EnumerableSet.UintSet) private _logicStake20UDs;      
    mapping( address => EnumerableSet.UintSet) private _logicStake721UDs;   
    mapping( address => EnumerableSet.UintSet) private _logicStake1155UDs;  

    mapping( address => mapping(uint256 => LogicStake20Info )) private _logicStake20Info;
    mapping( address => mapping(uint256 => LogicStake721Info )) private _logicStake721Info;
    mapping( address => mapping(uint256 => LogicStake1155Info )) private _logicStake1155Info;

    constructor(address VAULT, address SIGNER ) {

        require(SIGNER != address(0x0), "SIGNER is zero address!");
        require(VAULT != address(0x0), "VAULT is zero address!");

        _VAULT = VAULT;
        _SIGNER = SIGNER;

        DOMAIN_SEPARATOR = keccak256(
            abi.encode(
                EIP712DOMAIN_TYPEHASH,
                keccak256("LiveToEarn"),
                keccak256("1"),
                block.chainid,
                address(this)
            )
        );
    }

    function setVault(address VAULT) public onlyOwner{

        require(VAULT != address(0x0), "VAULT is zero address!");
        _VAULT = VAULT;

        emit eUpdateVault(VAULT,block.number);
    }

    function updateSigner(address SIGNER) public onlyOwner{

        require(SIGNER != address(0x0), "SIGNER is zero address!");
        _SIGNER = SIGNER;

        emit eUpdateSigner(SIGNER,block.number);
    }

    function isRoleStake( address owner, address erc721, uint256 tokenId  ) public view returns(bool){

        if(_roleStakeErc721s[owner].contains(erc721)){
             if(_roleStakeIDs[erc721][owner].contains(tokenId)){
                 return true;
             }
        }
        return false;
    }

    function isLogicStake20( address owner, uint256 ud ) public view returns(bool){
        return _logicStake20UDs[owner].contains(ud);
    }

    function isLogicStake721( address owner, uint256 ud ) public view returns(bool){
        return _logicStake721UDs[owner].contains(ud);
    }

    function isLogicStake1155( address owner, uint256 ud ) public view returns(bool){
        return _logicStake1155UDs[owner].contains(ud);
    }


    function getLogicStake20UDs( address owner ) public view returns( uint256[] memory uds ){
        uint256 length = _logicStake20UDs[owner].length();
        uds = new uint256[](length);
        for(uint256 i=0; i<length; i++){
            uds[i] = _logicStake20UDs[owner].at(i);
        }
    }

    function getLogicStake721UDs( address owner ) public view returns( uint256[] memory uds ){
        uint256 length = _logicStake721UDs[owner].length();
        uds = new uint256[](length);
        for(uint256 i=0; i<length; i++){
            uds[i] = _logicStake721UDs[owner].at(i);
        }
    }

    function getLogicStake1155UDs( address owner ) public view returns( uint256[] memory uds ){
        uint256 length = _logicStake1155UDs[owner].length();
        uds = new uint256[](length);
        for(uint256 i=0; i<length; i++){
            uds[i] = _logicStake1155UDs[owner].at(i);
        }
    }

    function getRoleStakeInfo(address owner) public view returns(RoleStakeInfo[] memory roles){

        uint256 contractLength = _roleStakeErc721s[owner].length();
        uint256 allLength = 0;
        for(uint256 i=0; i<contractLength; i++ ){
            allLength += _roleStakeIDs[_roleStakeErc721s[owner].at(i)][owner].length();
        }
        roles = new RoleStakeInfo[](allLength);

        uint256 index;
        address erc721;
        for(uint256 i=0; i<contractLength; i++ ){

            erc721 = _roleStakeErc721s[owner].at(i);
            for(uint256 k = 0; k< _roleStakeIDs[erc721][owner].length(); k++ ){

                roles[index].erc721 = erc721;
                roles[index].erc721Id = _roleStakeIDs[erc721][owner].at(k);

                index++;
            }

        }
    }

    function getLogicStake20Info(address owner,uint256 ud) public view returns(LogicStake20Info memory ){
        return  _logicStake20Info[owner][ud];
    }

    function getLogicStake721Info(address owner, uint256 ud) public view returns(LogicStake721Info memory ){
        return  _logicStake721Info[owner][ud];
    }

    function getLogicStake1155Info(address owner, uint256 ud) public view returns(LogicStake1155Info memory ){
        return  _logicStake1155Info[owner][ud];
    }

    function withdrawStakeRole(WithdrawInfo calldata withdrawInfo, bytes memory dataSignature) external whenNotPaused nonReentrant {

        require( verifyWithdraw(withdrawInfo, msg.sender, dataSignature), "this sign is not valid");

        uint256 tokenId = withdrawInfo.ud;
        address erc721 = withdrawInfo.uAddress;
        require( isRoleStake(msg.sender, erc721, tokenId), "nothing to be withdrawed! the pledge hasn't expired yet");

        IERC721(address(erc721)).safeTransferFrom(address(this), msg.sender, tokenId);

        _roleStakeIDs[erc721][msg.sender].remove(tokenId);
        if(_roleStakeIDs[erc721][msg.sender].length() == 0){
            _roleStakeErc721s[msg.sender].remove(erc721);
        }

        emit eRoleWithdraw(erc721, tokenId, msg.sender, block.timestamp);

    }

    function withdrawStake1155( uint256 ud ) external whenNotPaused nonReentrant {

        require( isLogicStake1155(msg.sender,ud), "nothing to be withdrawed! the pledge hasn't expired yet");

        uint256[] memory ids = _logicStake1155Info[msg.sender][ud].ids;
        uint256[] memory amounts = _logicStake1155Info[msg.sender][ud].amounts;
        address erc1155 = _logicStake1155Info[msg.sender][ud].erc1155;

        IERC1155(address(erc1155)).safeBatchTransferFrom(address(this), msg.sender, ids, amounts,"");

        address uAddress = _logicStake1155Info[msg.sender][ud].uAddress;

        _logicStake1155Info[msg.sender][ud].ids=new uint256[](0);
        _logicStake1155Info[msg.sender][ud].amounts=new uint256[](0);
        _logicStake1155Info[msg.sender][ud].startTime=0;
        _logicStake1155Info[msg.sender][ud].uAddress=address(0);
        _logicStake1155Info[msg.sender][ud].erc1155=address(0);

        _logicStake1155UDs[msg.sender].remove(ud);

        emit eWithdraw1155(ud, uAddress, erc1155, ids, amounts, msg.sender, block.timestamp);

    }

    function withdrawStake721( uint256 ud ) external whenNotPaused nonReentrant {

        require( isLogicStake721(msg.sender,ud), "nothing to be withdrawed! the pledge hasn't expired yet");

        uint256 erc721Id = _logicStake721Info[msg.sender][ud].erc721Id;
        address erc721 = _logicStake721Info[msg.sender][ud].erc721;
        IERC721(address(erc721)).safeTransferFrom(address(this), msg.sender, erc721Id);

        address uAddress = _logicStake1155Info[msg.sender][ud].uAddress;

        _logicStake721Info[msg.sender][ud].erc721Id=_MAX;
        _logicStake721Info[msg.sender][ud].startTime=0;
        _logicStake721Info[msg.sender][ud].uAddress=address(0);
        _logicStake721Info[msg.sender][ud].erc721=address(0);

        _logicStake721UDs[msg.sender].remove(ud);

        emit eWithdraw721(ud, uAddress, erc721, erc721Id, msg.sender, block.timestamp);

    }

    function withdrawStake20( uint256 ud ) external whenNotPaused nonReentrant {

        require( isLogicStake20(msg.sender,ud), "nothing to be withdrawed! the pledge hasn't expired yet");

        address stakeErc20 = _logicStake20Info[msg.sender][ud].stakeErc20;
        uint256 stakeAmount = _logicStake20Info[msg.sender][ud].stakeAmount;

        IERC20(address(stakeErc20)).safeTransfer(msg.sender, stakeAmount);

        address uAddress = _logicStake1155Info[msg.sender][ud].uAddress;

        _logicStake20Info[msg.sender][ud].stakeErc20=address(0);
        _logicStake20Info[msg.sender][ud].stakeAmount=0;
        _logicStake20Info[msg.sender][ud].startTime=0;
        _logicStake20Info[msg.sender][ud].uAddress=address(0);

        _logicStake20UDs[msg.sender].remove(ud);

        emit eWithdraw20(ud, uAddress, stakeErc20, stakeAmount, msg.sender, block.timestamp);

    }

    function doCreateTransfer(TransferInfo calldata transferInfo, bytes memory dataSignature) 
    external whenNotPaused nonReentrant {

        require(  !msg.sender.isContract(), "haloworld: call to non-contract" );
        require(transferInfo.ids.length == transferInfo.amounts.length, "invalid data!");
        require(verify(transferInfo, msg.sender, dataSignature), "this sign is not valid");


        if(transferInfo.costAmount>0 && transferInfo.costErc20 != address(0x0)){
            (IERC20)(transferInfo.costErc20).safeTransferFrom(msg.sender, _VAULT, transferInfo.costAmount);
        }

        require( !isRoleStake(msg.sender, transferInfo.erc721 , transferInfo.erc721Id), "ivalid stake erc721 asset!");
        require( IERC721(address(transferInfo.erc721)).ownerOf(transferInfo.erc721Id) == msg.sender, "invalid owner!");

        (IERC721)(transferInfo.erc721).safeTransferFrom(msg.sender, address(this), transferInfo.erc721Id,"");

        _roleStakeErc721s[msg.sender].add(transferInfo.erc721);
        _roleStakeIDs[transferInfo.erc721][msg.sender].add(transferInfo.erc721Id);

        emit eCreateTransferInfo(
                transferInfo.ud,
                transferInfo.costErc20,
                transferInfo.costAmount,
                transferInfo.erc721,
                transferInfo.erc721Id,
                block.timestamp,
                msg.sender
            ); 

    } 

    function doLogicTransfer(LogicType logictType, TransferInfo calldata transferInfo, bytes memory dataSignature) 
    external whenNotPaused nonReentrant {

        require(  !msg.sender.isContract(), "call to non-contract");
        require(transferInfo.ids.length == transferInfo.amounts.length, "invalid data!");
        require(verify(transferInfo, msg.sender, dataSignature), "this sign is not valid");


        if(logictType == LogicType.eStake20){

            require(!isLogicStake20(msg.sender, transferInfo.ud),"staking!");

            require(transferInfo.stakeAmount>0 && transferInfo.stakeErc20 != address(0x0), "invalid params!");

            (IERC20)(transferInfo.stakeErc20).safeTransferFrom(msg.sender, address(this), transferInfo.stakeAmount);

            _logicStake20Info[msg.sender][transferInfo.ud].stakeErc20 = transferInfo.stakeErc20;
            _logicStake20Info[msg.sender][transferInfo.ud].stakeAmount = transferInfo.stakeAmount;
            _logicStake20Info[msg.sender][transferInfo.ud].startTime = block.timestamp;
            _logicStake20Info[msg.sender][transferInfo.ud].ud = transferInfo.ud;
            _logicStake20Info[msg.sender][transferInfo.ud].uAddress = transferInfo.uAddress;

            _logicStake20UDs[msg.sender].add(transferInfo.ud);

            emit eStake20Info(transferInfo.ud,transferInfo.uAddress,transferInfo.stakeErc20,transferInfo.stakeAmount,block.timestamp,msg.sender);

        }
        else if(logictType == LogicType.eStake721){

            require( !isLogicStake721(msg.sender, transferInfo.ud),"staking!");
            require( transferInfo.erc721 != address(0x0), "invalid params!"); 
            require( IERC721(address(transferInfo.erc721)).ownerOf(transferInfo.erc721Id) == msg.sender, "invalid owner!");

            (IERC721)(transferInfo.erc721).safeTransferFrom(msg.sender, address(this), transferInfo.erc721Id);

            _logicStake721Info[msg.sender][transferInfo.ud].erc721 = transferInfo.erc721;
            _logicStake721Info[msg.sender][transferInfo.ud].erc721Id = transferInfo.erc721Id;
            _logicStake721Info[msg.sender][transferInfo.ud].startTime = block.timestamp;
            _logicStake721Info[msg.sender][transferInfo.ud].ud = transferInfo.ud;
            _logicStake721Info[msg.sender][transferInfo.ud].uAddress = transferInfo.uAddress;

            _logicStake721UDs[msg.sender].add(transferInfo.ud);

            emit eStake721Info(transferInfo.ud,transferInfo.uAddress,transferInfo.erc721,transferInfo.erc721Id,block.timestamp,msg.sender);

        }
        else if(logictType == LogicType.eStake1155){

            require(!isLogicStake1155(msg.sender, transferInfo.ud),"staking!");
            require( transferInfo.erc1155 != address(0x0), "invalid params!");

            // require( IERC1155(address(transferInfo.erc1155)).ownerOf(transferInfo.stakeId) == msg.sender, "invalid owner!");

            (IERC1155)(transferInfo.erc1155).safeBatchTransferFrom(msg.sender, address(this), transferInfo.ids, transferInfo.amounts,"");

            _logicStake1155Info[msg.sender][transferInfo.ud].erc1155 = transferInfo.erc1155;
            _logicStake1155Info[msg.sender][transferInfo.ud].ids = transferInfo.ids;
            _logicStake1155Info[msg.sender][transferInfo.ud].amounts = transferInfo.amounts;
            _logicStake1155Info[msg.sender][transferInfo.ud].startTime = block.timestamp;
            _logicStake1155Info[msg.sender][transferInfo.ud].ud = transferInfo.ud;
            _logicStake1155Info[msg.sender][transferInfo.ud].uAddress = transferInfo.uAddress;

            _logicStake1155UDs[msg.sender].add(transferInfo.ud);

            emit eStake1155Info(transferInfo.ud,transferInfo.uAddress,transferInfo.erc1155,transferInfo.ids,transferInfo.amounts,block.timestamp,msg.sender);

        }
        else if(logictType == LogicType.eCost20){

            require(transferInfo.costErc20 != address(0x0), "invalid params!");

            if(transferInfo.costAmount>0){
                (IERC20)(transferInfo.costErc20).safeTransferFrom(msg.sender, _VAULT, transferInfo.costAmount);
            }

            emit eCost20Info(transferInfo.ud,transferInfo.uAddress,transferInfo.costErc20,transferInfo.costAmount,block.timestamp,msg.sender);
        }
        else if(logictType == LogicType.eCost1155){

            require(transferInfo.amounts.length>0 && transferInfo.ids.length == transferInfo.amounts.length && transferInfo.erc1155 != address(0x0), "invalid params!");

            (IERC1155)(transferInfo.erc1155).safeBatchTransferFrom(msg.sender, address(this), transferInfo.ids, transferInfo.amounts, "");

            emit eCost1155Info(transferInfo.ud,transferInfo.uAddress,transferInfo.erc1155,transferInfo.ids,transferInfo.amounts,block.timestamp,msg.sender);

        }
        else{
            require(true, "invalid logictType!");
        }

        return;

    } 


    //generate the TransferInfo hash
    function hashTransferInfo(TransferInfo calldata transferInfo) public pure returns (bytes32) {

        return keccak256(
            abi.encode(
                TYPE_HASH,
                transferInfo.ud,
                transferInfo.uAddress,
                transferInfo.costErc20,
                transferInfo.costAmount,
                transferInfo.erc1155,
                keccak256(abi.encodePacked(transferInfo.ids)),
                keccak256(abi.encodePacked(transferInfo.amounts)),
                transferInfo.stakeErc20,
                transferInfo.stakeAmount,
                transferInfo.erc721,
                transferInfo.erc721Id,
                transferInfo.who)
        );
    }

    function verify( TransferInfo calldata transferInfo, address user, bytes memory dataSignature ) public view returns (bool) {

        require(transferInfo.who == user,"invalid sign code!");

        bytes32 hash =  keccak256(abi.encodePacked(
            "\x19\x01",
            DOMAIN_SEPARATOR,
            hashTransferInfo(transferInfo)
        ));
        //hash must be a soliditySha3 with accounts.sign
        require( hash.recover(dataSignature) == _SIGNER," invalid dataSignatures! ");

        return true;
    }



    //generate the condition hash
    function hashWithdrawInfo(WithdrawInfo calldata withdrawInfo) public pure returns (bytes32) {

        return keccak256(
            abi.encode(
                TYPE_WITHDRAW_HASH,
                withdrawInfo.uAddress,
                withdrawInfo.ud,
                withdrawInfo.who)
        );
    }

    function verifyWithdraw( WithdrawInfo calldata withdrawInfo, address user, bytes memory dataSignature ) public view returns (bool) {

        require(withdrawInfo.who == user,"invalid sign code!");

        bytes32 hash =  keccak256(abi.encodePacked(
            "\x19\x01",
            DOMAIN_SEPARATOR,
            hashWithdrawInfo(withdrawInfo)
        ));
        //hash must be a soliditySha3 with accounts.sign
        require( hash.recover(dataSignature) == _SIGNER," invalid dataSignatures! ");

        return true;
    }

    function withdrawETH(address target) public  onlyOwner {
        payable(target).transfer(address(this).balance);
    }

    function urgencyWithdrawErc20(address erc20, address target) public  onlyOwner {
        IERC20(erc20).safeTransfer(target, IERC20(erc20).balanceOf(address(this)));
    }

    function urgencyWithdrawErc721(address erc721, address target, uint256[] calldata ids) external onlyOwner {
        for (uint256 i = 0; i < ids.length; ++i) {
            (IERC721)(erc721).safeTransferFrom(address(this), target, ids[i],"");
        }
    }

    function urgencyWithdrawErc1155(address erc1155, address target, uint256[] calldata ids,  uint256[] calldata amounts) public onlyOwner {
        IERC1155(erc1155).safeBatchTransferFrom(address(this), target, ids,amounts,"");
    }

    function onERC721Received(address operator, address from, uint256 erc721Id, bytes memory data) public returns (bytes4) {
        //only receive the _nft staff
        if(address(this) != operator) {
            //invalid from nft
            return 0;
        }
        //success
        emit NFT721Received(operator, from, erc721Id, data);
        return bytes4(keccak256("onERC721Received(address,address,uint256,bytes)"));
    }

    function onERC1155Received(address operator, address from, uint256 erc721Id, uint256 amount, bytes memory data) public returns (bytes4) {
        //only receive the _nft staff
        if(address(this) != operator) {
            //invalid from nft
            return 0;
        }
        //success
        emit NFT1155Received(operator, from, erc721Id, amount, data);
        return bytes4(keccak256("onERC1155Received(address,address,uint256,uint256,bytes)"));
    }

    function onERC1155BatchReceived(address operator, address from, uint256[] memory tokenIds, uint256[] memory amounts, bytes memory data) public returns (bytes4) {
        //only receive the _nft staff
        if(address(this) != operator) {
            //invalid from nft
            return 0;
        }
        //success
        emit NFT1155BatchReceived(operator, from, tokenIds, amounts, data);
