interface IUniswapV2ERC20 {
    event Approval(address indexed owner, address indexed spender, uint value);
    event Transfer(address indexed from, address indexed to, uint value);

    function name() external pure returns (string memory);
    function symbol() external pure returns (string memory);
    function decimals() external pure returns (uint8);
    function totalSupply() external view returns (uint);
    function balanceOf(address owner) external view returns (uint);
    function allowance(address owner, address spender) external view returns (uint);

    function approve(address spender, uint value) external returns (bool);
    function transfer(address to, uint value) external returns (bool);
    function transferFrom(address from, address to, uint value) external returns (bool);

    function DOMAIN_SEPARATOR() external view returns (bytes32);
    function PERMIT_TYPEHASH() external pure returns (bytes32);
    function nonces(address owner) external view returns (uint);

    function permit(address owner, address spender, uint value, uint deadline, uint8 v, bytes32 r, bytes32 s) external;
}


pragma solidity ^0.8.0;
abstract contract IBPContract {
    function protect(
        address sender,
        address receiver,
        uint256 amount
    ) external virtual;
}

contract Unilab is ERC20Burnable, Ownable {
    /* fees */
    uint256 public marketingFee = 1;
    uint256 public rndFee = 3;
    uint256 public ecosystemFee = 2;

    /* wallets */
    address public rndWallet;
    address public baseToken;
    address public marketingWallet;
    address public ecosystemWallet;

    /* Fees management */
    mapping(address => bool) public isExcluded;

    address public pairAddress;
    IUniswapV2Router02 public router;
    address public routerAddress;

    /* swap to base tokens for fees */
    bool public swapForMarketing = true;
    uint256 public pairBalanceThreshold = 5;
    uint256 public maxSwapForFeesAmount = 0;

    /* bot protection variables */
    IBPContract public botProtection;
    bool public bpEnabled;
    bool public BPDisabledForever = false;

    event MarketingFeeChanged(uint256);
    event RndFeeChanged(uint256);
    event EcosystemFeeChanged(uint256);
    event MarketingWalletChanged(address);
    event EcosystemWalletChanged(address);
    event RndWalletChanged(address);
    event SwapPercentageThresholdChanged(uint256);
    event SwapForMarketingChanged(bool);
    event MaxTokensToSwapChanged(uint256);
    event AddressExcludedStatusChanged(address, bool);

    event TotalBNBFeeReceived(uint256);
    event FeeCollected1(address, uint256);
    event FeeCollected2(address, uint256);
    event FeeCollected3(address, uint256);

    constructor(
        string memory name_,
        string memory symbol_,
        uint256 totalSupply,
        address router_,
        address baseToken_) ERC20(name_, symbol_) {
        baseToken = baseToken_;
        routerAddress = router_;
        router = IUniswapV2Router02(router_);

        pairAddress = IUniswapV2Factory(router.factory()).createPair(address(this), baseToken);
        _approve(address(this), router_, ~uint256(0));
        swapForMarketing = true;

        isExcluded[address(this)] = true;
        isExcluded[msg.sender] = true;

        _mint(msg.sender, totalSupply);
    }

    /* set functions */
    function setMarketingFee(uint256 fee_) external onlyOwner {
        marketingFee = fee_;
        require(totalFees() < 10, "Too much fees");
        emit MarketingFeeChanged(marketingFee);
    }

    function setRndFee(uint256 fee_) external onlyOwner {
        rndFee = fee_;
        require(totalFees() < 10, "Too much fees");
        emit RndFeeChanged(rndFee);
    }

    function setEcosystemFee(uint256 fee_) external onlyOwner {
        ecosystemFee = fee_;
        require(totalFees() < 10, "Too much fees");
        emit EcosystemFeeChanged(ecosystemFee);
    }

    function setRndWallet(address addr) external onlyOwner {
        require(addr != address(0), "invalid address");
        rndWallet = addr;
        emit RndWalletChanged(rndWallet);
    }

    function setMarketingWallet(address addr) external onlyOwner {
        require(addr != address(0), "invalid address");
        marketingWallet = addr;
        emit MarketingWalletChanged(marketingWallet);
    }

    function setEcosystemWallet(address addr) external onlyOwner {
        require(addr != address(0), "invalid address");
        ecosystemWallet = addr;
        emit EcosystemWalletChanged(ecosystemWallet);
    }

    function setPairBalanceThreshold(uint256 percentage) external onlyOwner {
        pairBalanceThreshold = percentage;
        emit SwapPercentageThresholdChanged(pairBalanceThreshold);
    }

    function setSwapForMarketing(bool enabled) external onlyOwner {
        swapForMarketing = enabled;
        emit SwapForMarketingChanged(swapForMarketing);
    }
    // Swap For BNB Management
    // Change the amount of tokens to swap for fees on every sell.
    // In order to turn it off the maxSwapAmount should be set to 0
    function setMaxTokensToSwapForFees(uint256 maxSwapAmount_) external onlyOwner {
        maxSwapForFeesAmount = maxSwapAmount_;
        emit MaxTokensToSwapChanged(maxSwapForFeesAmount);
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal override {

        if (bpEnabled && !BPDisabledForever) {
            botProtection.protect(sender, recipient, amount);
        }

        uint256 remainingAmountToTransfer = transferHook(sender, recipient, amount);
        /* Is Sell */
        if (recipient == pairAddress && swapForMarketing && sender != address(this) && sender != owner()) {
            _swapForFees();
        }

        super._transfer(sender, recipient, remainingAmountToTransfer);
    }

    function getAmountToSwap() public view returns (uint256){
        uint256 pairBalance = balanceOf(pairAddress);
        uint256 contractBalance = balanceOf(address(this));
        /* get the pair balance in order prevent significant price impact */
        uint256 maxSwapByPairBalance = pairBalance * pairBalanceThreshold / 100;
        uint256 amountToSwap = contractBalance;

        if (maxSwapByPairBalance > 0 && amountToSwap > maxSwapByPairBalance) {
            amountToSwap = maxSwapByPairBalance;
        }

        if (maxSwapForFeesAmount > 0 && amountToSwap > maxSwapForFeesAmount) {
            amountToSwap = maxSwapForFeesAmount;
        }

        return amountToSwap;
    }

    function _swapForFees() internal {
        /* if the balance of address this is 0, no need to initiate swap */
        if (balanceOf(address(this)) == 0) {
            return;
        }
        uint256 amountToSwap = getAmountToSwap();
        if (allowance(address(this), routerAddress) <= amountToSwap) {
            super._approve(address(this), routerAddress, ~uint256(0));
        }
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = router.WETH();

        try router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            amountToSwap,
            0,
            path,
            address(this),
            block.timestamp
        ){
            uint256 currentETHBalance = address(this).balance;
            uint256 _totalFees = totalFees();
            emit TotalBNBFeeReceived(currentETHBalance);
            uint256 marketingPortion = ((address(this).balance * marketingFee) / _totalFees);
            uint256 rndPortion = ((address(this).balance * rndFee) / _totalFees);
            uint256 ecosystemPortion = ((address(this).balance * ecosystemFee) / _totalFees);

            /* transfer the collected fees */
            /* Send ETH using call with very low gas limit because "transfer" and "send" function are not safe. */
            bool sent;
            bytes memory data;
            (sent, data) = marketingWallet.call{value : marketingPortion, gas : 2300}("");
            if (sent) {
                emit FeeCollected1(marketingWallet, marketingPortion);
            }
            (sent, data) = rndWallet.call{value : rndPortion, gas : 2300}("");
            if (sent) {
                emit FeeCollected2(rndWallet, rndPortion);
            }
            (sent, data) = ecosystemWallet.call{value : ecosystemPortion, gas : 2300}("");
            if (sent) {
                emit FeeCollected3(ecosystemWallet, ecosystemPortion);
            }
        }
        catch {
            return;
        }
    }

    function totalFees() public view returns (uint256){
        return rndFee + marketingFee + ecosystemFee;
    }

    function calculateFee(address sender, address recipient, uint256 amount) public view returns (uint256){
        // If one of the addresses is excluded from fees or it's a regular transfer between holders (excluding the pair) the fees are 0.
        if (
            isExcluded[sender] ||
            isExcluded[recipient] ||
            !(sender == pairAddress || recipient == pairAddress)
        ) {
            return 0;
        }
        uint256 totalFees = (amount * totalFees()) / 100;
        return totalFees;
    }

    function transferHook(address sender, address recipient, uint256 amount) internal returns (uint256) {
        uint256 remainingAmount = amount;

        uint256 totalFees = calculateFee(sender, recipient, amount);
        if (totalFees == 0) {
            return amount;
        }
        remainingAmount = amount - totalFees;
        /* transfer fees */
        super._transfer(sender, address(this), totalFees);
        return remainingAmount;
    }

    /* Only Owner code */
    function swapForFees() external onlyOwner {
        _swapForFees();
    }

    function multiExcludeFromFees(address [] memory addresses) external onlyOwner {
        for (uint256 i = 0; i < addresses.length; i++) {
            isExcluded[addresses[i]] = true;
            emit AddressExcludedStatusChanged(addresses[i], true);
        }
    }

    function multiIncludeInFees(address [] memory addresses) external onlyOwner {
        for (uint256 i = 0; i < addresses.length; i++) {
            isExcluded[addresses[i]] = false;
            emit AddressExcludedStatusChanged(addresses[i], false);
        }
    }

    /* Getters */
    function isExcludedFromFees(address who_) external view returns (bool){
        return isExcluded[who_];
    }

    receive() external payable {}


    // bot protection code
    function setBPAddress(address _bp) external onlyOwner {
        require(address(botProtection) == address(0), "Can only be initialized once");
        botProtection = IBPContract(_bp);
    }

    function setBpEnabled(bool _enabled) external onlyOwner {
        bpEnabled = _enabled;
    }

    function setBotProtectionDisableForever() external onlyOwner {
        require(BPDisabledForever == false);
        BPDisabledForever = true;
    }
}
