// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.2;

contract Panda {
    mapping(address => uint256) public balances;
    mapping(address => mapping(address => uint256)) public allowance;
    uint256 public totalSupply = 1000000000000 * 10**18; // 1,000,000,000,000 tokens
    string public name = "Panda";
    string public symbol = "PND";
    uint8 public decimals = 18;
    uint8 public buyTax = 5; // 5% buy tax
    uint8 public sellTax = 5; // 5% sell tax
    address public owner;
    address public marketingWallet = 0x55Ea2512416949b42e60c046B49f0D223d51aCE5;

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);

    modifier onlyOwner() {
        require(msg.sender == owner, "Caller is not the owner");
        _;
    }

    constructor() {
        balances[msg.sender] = totalSupply;
        owner = msg.sender;
    }

    function balanceOf(address account) public view returns (uint256) {
        return balances[account];
    }

    function transfer(address to, uint256 value) public returns (bool) {
        require(balanceOf(msg.sender) >= value, "Balance too low");
        _transfer(msg.sender, to, value);
        return true;
    }

    function transferFrom(address from, address to, uint256 value) public returns (bool) {
        require(balanceOf(from) >= value, "Balance too low");
        require(allowance[from][msg.sender] >= value, "Allowance too low");
        allowance[from][msg.sender] -= value;
        _transfer(from, to, value);
        return true;
    }

    function approve(address spender, uint256 value) public returns (bool) {
        allowance[msg.sender][spender] = value;
        emit Approval(msg.sender, spender, value);
        return true;
    }

    function _transfer(address from, address to, uint256 value) internal {
        bool isBuying = from == address(0) || to != owner;
        uint256 taxAmount = calculateTax(value, isBuying);
        uint256 taxedValue = value - taxAmount;
        uint256 burnAmount = taxedValue / 100; // 1% burn

        balances[to] += taxedValue - burnAmount;
        balances[from] -= value;
        if (burnAmount > 0) {
            totalSupply -= burnAmount; // Reduce the total supply
        }
        distributeTaxes(taxAmount);

        emit Transfer(from, to, taxedValue - burnAmount);
    }

    function calculateTax(uint256 value, bool isBuying) internal view returns (uint256) {
        uint256 taxRate = isBuying ? buyTax : sellTax;
        return (value * taxRate) / 100;
    }

    function distributeTaxes(uint256 taxAmount) internal {
        uint256 holderTax = (taxAmount * 33) / 100;
        uint256 marketingTax = taxAmount - holderTax;
        balances[owner] += holderTax;
        balances[marketingWallet] += marketingTax;
    }

    function changeOwner(address newOwner) public onlyOwner {
        require(newOwner != address(0), "Invalid address");
        owner = newOwner;
    }

    function changeMarketingWallet(address newWallet) public onlyOwner {
        require(newWallet != address(0), "Invalid address");
        marketingWallet = newWallet;
    }

    function updateTaxRates(uint8 newBuyTax, uint8 newSellTax) public onlyOwner {
        require(newBuyTax <= 100 && newSellTax <= 100, "Tax rate too high");
        buyTax = newBuyTax;
        sellTax = newSellTax;
    }
}
