pragma solidity 0.8.14;





interface INode {
    function claim() external;
}

contract NodeUpkeep is Ownable, AutomationCompatibleInterface {
    using SafeERC20 for IERC20;

    IERC20 public constant EMP = IERC20(0x3b248CEfA87F836a4e6f6d6c9b42991b88Dc1d58);
    IERC20 public constant ETH = IERC20(0x2170Ed0880ac9A755fd29B2688956BD959F933F8);
    IUniswapV2Router public constant ROUTER = IUniswapV2Router(0x10ED43C718714eb63d5aA57B78B54704E256024E);
    INode public immutable MASTERNODE;

    uint256 public constant INTERVAL = 24 hours;
    uint256 public lastTimeStamp;
    uint256 public slippageBp;

    constructor(address _masternode, uint256 _lastTimeStamp, uint256 _slippageBp) {
        MASTERNODE = INode(_masternode);
        lastTimeStamp = _lastTimeStamp;
        slippageBp = _slippageBp;
        EMP.safeApprove(address(ROUTER), type(uint256).max);
    }

    receive() external payable {}

    function condition() public view returns (bool) {
        return block.timestamp >= lastTimeStamp + INTERVAL;
    }

    function checkUpkeep(bytes calldata /* checkData */) external view override returns (bool upkeepNeeded, bytes memory /* performData */) {
        upkeepNeeded = condition();
        // We don't use the checkData in this example. The checkData is defined when the Upkeep was registered.
    }

    function performUpkeep(bytes calldata /* performData */) external override {
        if (condition()) {
            lastTimeStamp += INTERVAL;

            MASTERNODE.claim();

            uint256 empBalance = EMP.balanceOf(address(this));
            address[] memory path = new address[](2);
            path[0] = address(EMP);
            path[1] = address(ETH);

            uint256 amountOutMin = ROUTER.getAmountsOut(empBalance, path)[path.length - 1] * (10000 - slippageBp) / 10000;
            ROUTER.swapExactTokensForTokens(empBalance, amountOutMin, path, address(this), block.timestamp + 15 minutes);
        }
        // We don't use the performData in this example. The performData is generated by the Automation Node's call to your checkUpkeep function
    }

    function setLastTimeStamp(uint256 _lastTimeStamp) external onlyOwner {
        lastTimeStamp = _lastTimeStamp;
    }

    function setSlippageBp(uint256 _slippageBp) external onlyOwner {
        slippageBp = _slippageBp;
    }

    function adminWithdraw(address token, address to, uint256 amount) public onlyOwner {
        if (token == address(0)) {
            payable(to).transfer(amount);
        } else {
            IERC20(token).safeTransfer(to, amount);
        }
    }

    /**
     * @notice Performs a generic transaction.
     * @param _target The address for the transaction.
     * @param _value The value for the transaction.
     * @param _data The data of the transaction.
     */
    function invoke(address _target, uint256 _value, bytes memory _data) external onlyOwner returns (bytes memory _result) {
        bool success;
        (success, _result) = _target.call{value: _value}(_data);
        if (!success) {
            // solhint-disable-next-line no-inline-assembly
            assembly {
                returndatacopy(0, 0, returndatasize())
                revert(0, returndatasize())
            }
        }
    }
}
