pragma solidity >=0.5.0 <0.8.19;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@pancakeswap/v3-periphery/contracts/libraries/TransferHelper.sol";
import "./interface/IPancakeFactory.sol";
import "./interface/IPancakeRouter02.sol";
import "./governance/Governance.sol";

contract StoneToken is ERC20, Governance {
    using SafeMath for uint256;
    using Address for address;

    bool inSwapAndLiquify;
    bool private swapAndLiquifyEnabled = true;

    address public internalOperationAddress;
    IPancakeRouter02 public router;
    address public pancakePair;

    uint256 public buyFee;
    uint256 public sellFee;
    uint256 public numberOfTokensToSwapToLiquidity;

    mapping(address => bool) public automatedMarketMakerPairs;
    mapping(address => bool) public excludedFromFees;

    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);
    event SwapAmount(uint256 indexed amount);
    event InternalOperationAddress(
        address indexed oldAddress,
        address indexed newAddress
    );
    event AddNewAutomatedMarketMakerPair(
        address indexed newAutomatedMarketMakerPair
    );
    event ChangeRouter(
        address indexed router,
        address indexed factory,
        address indexed wbnb
    );
    event SentBNBInternalOperation(address usr, uint256 amount);

    modifier lockTheSwap() {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    constructor(
        address _pancakeRouter,
        uint256 initialSupply,
        uint256 _buyFee,
        uint256 _sellFee,
        uint256 _swapAmount
    ) ERC20("Stone Token", "STN") {
        buyFee = _buyFee;
        sellFee = _sellFee;
        numberOfTokensToSwapToLiquidity = _swapAmount;
        internalOperationAddress = _governance;
        IPancakeRouter02 pancakeRouter = IPancakeRouter02(_pancakeRouter);
        address pairCreated = IPancakeFactory(pancakeRouter.factory())
            .createPair(address(this), pancakeRouter.WETH());
        router = pancakeRouter;
        pancakePair = pairCreated;
        excludedFromFees[_governance] = true;
        excludedFromFees[address(this)] = true;
        _AutomatedMarketMakerPair(pancakePair, true);
        _approve(_governance, address(_pancakeRouter), ~uint256(0));
        _mint(_governance, initialSupply);
    }

    receive() external payable {}

    function transfer(
        address to,
        uint256 amount
    ) public override returns (bool) {
        _transfer(_msgSender(), to, amount);
        return true;
    }

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) public override returns (bool) {
        _spendAllowance(from, _msgSender(), amount);
        _transfer(from, to, amount);
        return true;
    }

    function _router(address newRouter, address factory, address weth) private {
        IPancakeRouter02 pancakeRouter = IPancakeRouter02(newRouter);
        address pairCreated = IPancakeFactory(factory).createPair(
            address(this),
            weth
        );
        router = pancakeRouter;
        pancakePair = pairCreated;
        _AutomatedMarketMakerPair(pancakePair, true);
    }

    function liquify(uint256 contractTokenBalance, address sender) private {
        if (contractTokenBalance >= numberOfTokensToSwapToLiquidity)
            contractTokenBalance = numberOfTokensToSwapToLiquidity;

        bool isOverRequiredTokenBalance = (contractTokenBalance >=
            numberOfTokensToSwapToLiquidity);

        if (
            isOverRequiredTokenBalance &&
            swapAndLiquifyEnabled &&
            !inSwapAndLiquify &&
            (!automatedMarketMakerPairs[sender])
        ) {
            uint256 toSwapBNB = contractTokenBalance;
            _sendBNBToContract(toSwapBNB);
        }
    }

    function _sendBNBToContract(uint256 tAmount) private lockTheSwap {
        _swapTokensForEth(tAmount);

        uint256 initialBalance = address(this).balance;
        if (initialBalance > 0) {
            TransferHelper.safeTransferETH(
                internalOperationAddress,
                initialBalance
            );

            emit SentBNBInternalOperation(
                internalOperationAddress,
                initialBalance
            );
        }
    }

    function _swapTokensForEth(uint256 tokenAmount) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = router.WETH();
        _approve(address(this), address(router), tokenAmount);
        router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(this),
            block.timestamp
        );
    }

    function _exchangeProcess(address from, address to) private {
        uint256 contractTokenBalance = super.balanceOf(address(this));
        if (!automatedMarketMakerPairs[from] && automatedMarketMakerPairs[to]) {
            liquify(contractTokenBalance, from);
        }
    }

    function _transferBetweenWallets(
        address from,
        address to,
        uint256 amount
    ) private {
        if (
            !automatedMarketMakerPairs[from] && !automatedMarketMakerPairs[to]
        ) {
            super._transfer(from, to, amount);
        }
    }

    function _exchangeTransferForPurchase(
        address from,
        address to,
        uint256 amount
    ) private {
        uint256 fee = amount.mul(buyFee).div(100);
        if (excludedFromFees[from] || excludedFromFees[to]) {
            fee = 0;
        }
        if (automatedMarketMakerPairs[from]) {
            uint256 restOfTokens = amount.sub(fee);
            if (fee > 0) super._transfer(from, address(this), fee);
            super._transfer(from, to, restOfTokens);
        }
    }

    function _tradeTransferForSale(
        address from,
        address to,
        uint256 amount
    ) private {
        uint256 fee = amount.mul(sellFee).div(100);
        if (excludedFromFees[from] || excludedFromFees[to]) {
            fee = 0;
        }
        if (automatedMarketMakerPairs[to]) {
            uint256 restOfTokens = amount.sub(fee);
            if (fee > 0) super._transfer(from, address(this), fee);
            super._transfer(from, to, restOfTokens);
        }
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        _exchangeProcess(from, to);
        _transferBetweenWallets(from, to, amount);
        _exchangeTransferForPurchase(from, to, amount);
        _tradeTransferForSale(from, to, amount);
    }

    function _AutomatedMarketMakerPair(address pair, bool value) private {
        require(
            automatedMarketMakerPairs[pair] != value,
            "The AutomatedMarketMakerPair pair is already set to this value"
        );
        automatedMarketMakerPairs[pair] = true;
        emit SetAutomatedMarketMakerPair(pair, value);
    }

    function addNewAutomatedMarketMakerPair(
        address pair,
        bool value
    ) external onlyGovernance {
        require(
            pair != pancakePair,
            "Pairs cannot be removed from Automated Market Maker Pair"
        );
        _AutomatedMarketMakerPair(pair, value);
        emit AddNewAutomatedMarketMakerPair(pair);
    }

    function excludedFee(address _address, bool value) external onlyGovernance {
        excludedFromFees[_address] = value;
    }

    function changeAddress(
        address _internalOperationAddress
    ) external onlyGovernance {
        address oldAddress = internalOperationAddress;
        internalOperationAddress = _internalOperationAddress;
        emit InternalOperationAddress(oldAddress, _internalOperationAddress);
    }

    function changeRouter(
        address newRouter,
        address factory,
        address wbnb
    ) external onlyGovernance {
        _router(newRouter, factory, wbnb);
        emit ChangeRouter(newRouter, factory, wbnb);
    }

    function changeSwapAmount(uint256 sAmount) external onlyGovernance {
        numberOfTokensToSwapToLiquidity = sAmount;
        emit SwapAmount(sAmount);
    }
}
