abstract contract Pausable is Context {

    event Paused(address account);

    event Unpaused(address account);

    bool private _paused;


    constructor () {
        _paused = false;
    }


    function paused() public view returns (bool) {
        return _paused;
    }

    modifier whenNotPaused() {
        require(!_paused, "Pausable: paused");
        _;
    }

    modifier whenPaused() {
        require(_paused, "Pausable: not paused");
        _;
    }

    function _pause() internal virtual whenNotPaused {
        _paused = true;
        emit Paused(_msgSender());
    }

    function _unpause() internal virtual whenPaused {
        _paused = false;
        emit Unpaused(_msgSender());
    }
}

abstract contract Lockable is Ownable {
    event Locked(address indexed account);
    event Unlock(address indexed account);

    using EnumerableSet for EnumerableSet.AddressSet;
    EnumerableSet.AddressSet private _blackList;

    string internal constant REVERT_ADDRESS_BE_LOCKED = "Blacklist: Address has been locked";
    string internal constant REVERT_ADDRESS_ALREADY_LOCKED = "Blacklist: Address already locked";
    string internal constant REVERT_ADDRESS_NOT_LOCKED = "Blacklist: Address not locked";

    modifier notLocked(address _address) {
        if (_address != address(0)) {
            require(!_blackList.contains(_address), REVERT_ADDRESS_BE_LOCKED);
        }
        _;
    }

    function inBlacklist(address _address) external view returns(bool) {
        return _blackList.contains(_address);
    }

    function _lockAcccount(address _address) internal {
        _blackList.add(_address);
        emit Locked(_address);
    }

    function _unlockAccount(address _address) internal {
        _blackList.remove(_address);
        emit Unlock(_address);
    }

    function lockAccounts(address[] memory _addresses) external onlyOwner {
        uint256 length = _addresses.length;
        for (uint256 i = 0; i < length; i++) {
            require(!_blackList.contains(_addresses[i]), REVERT_ADDRESS_ALREADY_LOCKED);
            _lockAcccount(_addresses[i]);
        }
    }

    function unlockAccounts(address[] memory _addresses) external onlyOwner {
        uint256 length = _addresses.length;
        for (uint256 i = 0; i < length; i++) {
            require(_blackList.contains(_addresses[i]), REVERT_ADDRESS_NOT_LOCKED);
            _unlockAccount(_addresses[i]);
        }
    }

    function viewBlacklistCounts() external view returns(uint256 total) {
        return _blackList.length();
    }

    function viewBlacklist(uint256 cursor, uint256 size) external view returns (address[] memory blacklistAddresses) {
        uint256 length = size;   
        if (length > _blackList.length() - cursor) length = _blackList.length() - cursor;
        blacklistAddresses = new address[](length);
        for (uint256 i = 0; i < length; i++) {
            blacklistAddresses[i] = _blackList.at(cursor + i);
        }
        return blacklistAddresses;
    }
}

