contract LGTTrader is Ownable {
    using SafeBEP20 for IBEP20;
    using SafeMath for uint256;
    IBEP20 private tokenBusd;
    IBEP20 private tokenUsdc;
    IBEP20 private tokenUsdt;
    IBEP20 private tokenLGT;

    uint256 private tokenPerStable;

    constructor(
        IBEP20 _tokenBusd,
        IBEP20 _tokenUsdc,
        IBEP20 _tokenUsdt,
        IBEP20 _tokenLGT,
        uint256 _tokenPerStable
    ) {
        tokenBusd = _tokenBusd;
        tokenUsdc = _tokenUsdc;
        tokenUsdt = _tokenUsdt;
        tokenPerStable = _tokenPerStable;
        tokenLGT = _tokenLGT;
    }

    function getAmmountPorStable() external view returns (uint256) {
        return tokenPerStable;
    }

    function updateTokenPerStable(uint256 _newtokenPerStable) external onlyOwner {
        tokenPerStable = _newtokenPerStable;
    }

    function getValues()
        public
        view
        onlyOwner
        returns (uint256 busdValue, uint256 usdcValue, uint256 usdtValue, uint256 tokenValue)
    {
         busdValue = tokenBusd.balanceOf(address(this));
         usdcValue = tokenUsdc.balanceOf(address(this));
         usdtValue = tokenUsdt.balanceOf(address(this));
         tokenValue = tokenLGT.balanceOf(address(this));
    }

    function claimStables() external onlyOwner {
        uint256 busdAmmount = tokenBusd.balanceOf(address(this));
        uint256 usdcAmmount = tokenUsdc.balanceOf(address(this));
        uint256 usdtAmmount = tokenUsdt.balanceOf(address(this));

        if (busdAmmount > 0) tokenBusd.transfer(_msgSender(), busdAmmount);
        if (usdcAmmount > 0) tokenUsdc.transfer(_msgSender(), usdcAmmount);
        if (usdtAmmount > 0) tokenUsdt.transfer(_msgSender(), usdtAmmount);
    }

    function claimToken() external onlyOwner {
        uint256 tokenAmmount = tokenLGT.balanceOf(address(this));

        if (tokenAmmount > 0) tokenLGT.transfer(_msgSender(), tokenAmmount);
       
    }
    function CalcTokenQuantity(uint256 stableQuantity) external view returns (uint256) {
        
        require(stableQuantity > 0, "The value to spend must be > 0");

        return  tokenPerStable.mul(stableQuantity);       
    }

    function buyWithStable(uint256 stableQuantity, uint256 stable)
        external
        payable
    {
        require(stableQuantity > 0, "The value to spend must be > 0");

        uint256 stableScaledAmount = stableQuantity.mul(uint256(10)**tokenBusd.decimals());

        uint256 amountToBuyScaled = tokenPerStable.mul(stableQuantity);
        
        if (stable == 1) {
            bool sentBusd = tokenBusd.transferFrom(
                _msgSender(),
                address(this),
                stableScaledAmount
            );
            require(sentBusd, "Failed to transfer busd to contract");
        } else if (stable == 2) {
            bool sentUsdc = tokenUsdc.transferFrom(
                _msgSender(),
                address(this),
                stableScaledAmount
            );
            require(sentUsdc, "Failed to transfer usdc to contract");
        } else if (stable == 3) {
            bool sentUsdt = tokenUsdt.transferFrom(
                _msgSender(),
                address(this),
                stableScaledAmount
            );
            require(sentUsdt, "Failed to transfer usdt to contract");
        } else {
            revert("Invalid Stable!");
        }

        //Transfer token to the msg.sender
        bool sentTokens = tokenLGT.transfer(_msgSender(), amountToBuyScaled);
        require(sentTokens, "Failed to transfer token to user");        
    }
