contract Degem is Context, IERC20, Ownable {
    using SafeMath for uint256;
    using Address for address;

	IERC20 private constant BUSD = IERC20(0xe9e7CEA3DedcA5984780Bafc599bD69ADd087D56);

    mapping (address => uint256) private _rOwned;
    mapping (address => uint256) private _tOwned;
    mapping (address => mapping (address => uint256)) private _allowances;

    mapping (address => bool) private _isExcludedFromFee;
    mapping (address => bool) private _isExcluded;
    address[] private _excluded;
    
    mapping (address => bool) private _isBlackListedBot;
    address[] private _blackListedBots;
    
    mapping (address => bool) private _isExcludedFromMaxWalletCheck;
   
    uint256 private constant MAX = ~uint256(0);
    uint256 private _tTotal = 100000000 * 10**6;
    uint256 private _rTotal = (MAX - (MAX % _tTotal));
    uint256 public maxWalletToken = 100000000 * 10**6; 

    string private constant _name = "Degem";
    string private constant _symbol = "DGM";
    uint8 private constant _decimals = 6;
    
    struct AddressFee {
        bool enable;
        uint256 _taxFee;
        uint256 _liquidityFee;
        uint256 _buyTaxFee;
        uint256 _buyLiquidityFee;
        uint256 _sellTaxFee;
        uint256 _sellLiquidityFee;
    }
    
    uint256 public _taxFee = 5;
    uint256 private _previousTaxFee = _taxFee;
    
    uint256 public _liquidityFee = 2;
    uint256 private _previousLiquidityFee = _liquidityFee;
    
    uint256 public _buyTaxFee = 5;
    uint256 public _buyLiquidityFee = 2;
    
    uint256 public _sellTaxFee = 5;
    uint256 public _sellLiquidityFee = 2;

	//Manual LP tax address
	address payable private _lpWallet = payable(0xa500Ebe0F177228358bd6E58e203e2F49D132b3A);
	
	//this tax is split between the tax fee
	uint256 public _marketingTax = 60;
	address payable private _marketingWallet = payable(0x9D2E8B15475075EC64B9388e3F146BF8Fbe4B769);
	
	// left over tax is for devs
	address payable private _devWallet = payable(0x4A21F7d813a5fDE68A49E25F772ACf2837978C10);
	
	// false = only enabled can trade or owner (add LP)
	// true = all can trade
	bool tradingEnabled = false;

    IUniswapV2Router02 public immutable uniswapV2Router;
    address public immutable uniswapV2Pair;
    
    bool inSwapAndLiquify;
    bool public swapAndLiquifyEnabled = true;
    
    // Fee per address
    mapping (address => AddressFee) public _addressFees;
    
    uint256 public _maxTxAmount = 1000000 * 10**6;
    uint256 public numTokensSellToAddToLiquidity = 500000 * 10**6;
    
    event FromRewardsExcluded(address account);
    event InRewardsIncluded(address account);
    event FromFeesExcluded(address account);
    event InFeesIncluded(address account);
    
    event TaxFeePercentChanged(uint256 fee);
    event LiquidityFeePercentChanged(uint256 fee);
    event MaxTxPercentChanged(uint256 percent);
    
    event BuyFeesChanged(uint256 buyTaxFee, uint256 buyLiquidityFee);
    event SellFeesChanged(uint256 buyTaxFee, uint256 buyLiquidityFee);
    
    event AddressFeesChanged(address account, bool enabled, uint256 buyTaxFee, uint256 buyLiquidityFee);
    event AddressBuyFeesChanged(address account, bool enabled, uint256 buyTaxFee, uint256 buyLiquidityFee);
    event AddressSellFeesChanged(address account, bool enabled, uint256 buyTaxFee, uint256 buyLiquidityFee);
    
    event MarketingWalletChanged(address wallet);
    event DevWalletChanged(address wallet);
    event LPWalletChanged(address wallet);
    
    event TradingEnabled(bool enabled);
    
    event MaxWalletCheckOnAccountEnabled(address account);
    event MaxWalletCheckOnAccountDisabled(address account);
    
    constructor () {
        _rOwned[_msgSender()] = _rTotal;
        
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);//IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
         // Create a uniswap pair for this new token
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), address(BUSD));

        // set the rest of the contract variables
        uniswapV2Router = _uniswapV2Router;
        
        //exclude owner and this contract from fee
        _isExcludedFromFee[owner()] = true;
        _isExcludedFromFee[address(this)] = true;
        emit Transfer(address(0), _msgSender(), _tTotal);
    }

    function name() public pure returns (string memory)  {
        return _name;
    }

    function symbol() public pure returns (string memory) {
        return _symbol;
    }

    function decimals() public pure returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _tTotal;
    }

    function balanceOf(address account) public view override returns (uint256) {
        if (_isExcluded[account]) return _tOwned[account];
        return tokenFromReflection(_rOwned[account]);
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), _allowances[sender][_msgSender()].sub(amount, "ERC20: transfer amount exceeds allowance"));
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].sub(subtractedValue, "ERC20: decreased allowance below zero"));
        return true;
    }

    function isExcludedFromReward(address account) public view returns (bool) {
        return _isExcluded[account];
    }
    
    function isExcludedFromMaxWalletCheck(address account) public view returns (bool) {
        return _isExcludedFromMaxWalletCheck[account];
    }

    function deliver(uint256 tAmount) private {
        address sender = _msgSender();
        require(!_isExcluded[sender], "Excluded addresses cannot call this function");
        (uint256 rAmount,,,,,) = _getValues(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _rTotal = _rTotal.sub(rAmount);
    }

    function reflectionFromToken(uint256 tAmount, bool deductTransferFee) public view returns(uint256) {
        require(tAmount <= _tTotal, "Amount must be less than supply");
        if (!deductTransferFee) {
            (uint256 rAmount,,,,,) = _getValues(tAmount);
            return rAmount;
        } else {
            (,uint256 rTransferAmount,,,,) = _getValues(tAmount);
            return rTransferAmount;
        }
    }

    function tokenFromReflection(uint256 rAmount) public view returns(uint256) {
        require(rAmount <= _rTotal, "Amount must be less than total reflections");
        uint256 currentRate =  _getRate();
        return rAmount.div(currentRate);
    }

    function excludeFromReward(address account) public onlyOwner() {
        // require(account != 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D, 'We can not exclude Uniswap router.');
        require(!_isExcluded[account], "Account is already excluded");
        if(_rOwned[account] > 0) {
            _tOwned[account] = tokenFromReflection(_rOwned[account]);
        }
        _isExcluded[account] = true;
        _excluded.push(account);
        
        emit FromRewardsExcluded(account);
    }

    function includeInReward(address account) external onlyOwner() {
        require(_isExcluded[account], "Account is already excluded");
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_excluded[i] == account) {
                _excluded[i] = _excluded[_excluded.length - 1];
                _tOwned[account] = 0;
                _isExcluded[account] = false;
                _excluded.pop();
                break;
            }
        }
        
        emit InRewardsIncluded(account);
    }
    function _transferBothExcluded(address sender, address recipient, uint256 tAmount) private {
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee, uint256 tTransferAmount, uint256 tFee, uint256 tLiquidity) = _getValues(tAmount);
        _tOwned[sender] = _tOwned[sender].sub(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _tOwned[recipient] = _tOwned[recipient].add(tTransferAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);        
        _takeLiquidity(tLiquidity);
        _sendTax(rFee, tFee);
        emit Transfer(sender, recipient, tTransferAmount);
    }
    
    function excludeFromFee(address account) external onlyOwner {
        require(!_isExcludedFromFee[account], "Account is already excluded");
        _isExcludedFromFee[account] = true;
        
        emit FromFeesExcluded(account);
    }
    
    function includeInFee(address account) external onlyOwner {
        require(_isExcludedFromFee[account], "Account is already included");
        _isExcludedFromFee[account] = false;
        
        emit InFeesIncluded(account);
    }
    
    function setTaxFeePercent(uint256 taxFee) external onlyOwner() {
        require(taxFee<15, "Tax higher 15 leads to several issues and is not allowed");
        _taxFee = taxFee;
        
        emit TaxFeePercentChanged(taxFee);
    }
    
    function setLiquidityFeePercent(uint256 liquidityFee) external onlyOwner() {
        require(liquidityFee<15, "Liquidity fee higher 15 leads to several issues and is not allowed");
        _liquidityFee = liquidityFee;
        
        emit LiquidityFeePercentChanged(liquidityFee);
    }
   
    function setMaxTxPercent(uint256 maxTxPercent) external onlyOwner() {
        _maxTxAmount = _tTotal.mul(maxTxPercent).div(
            10**2
        );
        
        emit MaxTxPercentChanged(maxTxPercent);
    }
    
    function setMaxWalletToken(uint256 _maxToken) external onlyOwner {
  	    maxWalletToken = _maxToken * (10**6);
  	}
    
    function addBotToBlackList(address account) external onlyOwner() {
        require(account != 0x10ED43C718714eb63d5aA57B78B54704E256024E, 'We can not blacklist Uniswap router.');
        require(!_isBlackListedBot[account], "Account is already blacklisted");
        _isBlackListedBot[account] = true;
        _blackListedBots.push(account);
    }

    function removeBotFromBlackList(address account) external onlyOwner() {
        require(_isBlackListedBot[account], "Account is not blacklisted");
        for (uint256 i = 0; i < _blackListedBots.length; i++) {
            if (_blackListedBots[i] == account) {
                _blackListedBots[i] = _blackListedBots[_blackListedBots.length - 1];
                _isBlackListedBot[account] = false;
                _blackListedBots.pop();
                break;
            }
        }
    }
    
    function excludeAddressFromMaxWalletCheck(address account) external onlyOwner() {
        require(!_isExcludedFromMaxWalletCheck[account], "Account is already excluded");
        _isExcludedFromMaxWalletCheck[account] = true;
        emit MaxWalletCheckOnAccountEnabled(account);
    }

    function includeAddressFromMaxWalletCheck(address account) external onlyOwner() {
        require(_isExcludedFromMaxWalletCheck[account], "Account is not excluded");
        _isExcludedFromMaxWalletCheck[account] = false;
        emit MaxWalletCheckOnAccountDisabled(account);
    }

    function setBuyFee(uint256 buyTaxFee, uint256 buyLiquidityFee) external onlyOwner {
        require(buyLiquidityFee+buyTaxFee<25, "Taxes/fees higher 25 in total lead to several issues and is not allowed");
        _buyTaxFee = buyTaxFee;
        _buyLiquidityFee = buyLiquidityFee;
        
        emit BuyFeesChanged(buyTaxFee, buyLiquidityFee);
    }
   
    function setSellFee(uint256 sellTaxFee, uint256 sellLiquidityFee) external onlyOwner {
        require(sellLiquidityFee+sellTaxFee<25, "Taxes/fees higher 25 in total lead to several issues and is not allowed");
        _sellTaxFee = sellTaxFee;
        _sellLiquidityFee = sellLiquidityFee;
        
        emit SellFeesChanged(sellTaxFee, sellLiquidityFee);
    }
    
    function setAddressFee(address _address, bool _enable, uint256 _addressTaxFee, uint256 _addressLiquidityFee) external onlyOwner {
        _addressFees[_address].enable = _enable;
        _addressFees[_address]._taxFee = _addressTaxFee;
