      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "../interfaces/iEGoldTokenV2.sol";


contract EGoldTokenMigrator is Pausable , AccessControl {
    using SafeMath for uint256;
    using Address for address;

    IERC20 public v1;

    iEGoldTokenV2 public v2;

    constructor( ) Pausable() AccessControl() {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);

    }

    function setTokens( address _v1 , address _v2 ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        v1 = IERC20(_v1);
        v2 = iEGoldTokenV2(_v2);

    }

    function migrateV1_to_V2( uint256 _amt ) external {
        v1.transferFrom( msg.sender , address( this ) , _amt );
        v2.mint(  msg.sender , _amt );
    }

    function migrateV2_to_V1( uint256 _amt ) external {
        v2.transferFrom( msg.sender , address(v2) , _amt);
        v2.burn( _amt );
        v1.transfer( msg.sender , _amt );
    }

}
"
