    "contracts/FairLaunchVault.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./libraries/pancakeswapV3/TransferHelper.sol";

contract FairLaunchVault is Ownable {
    receive() external payable {}

    function getContractBnbBalance() public view returns (uint256) {
        return address(this).balance;
    }

    function getContractTokenBalance(
        address tokenAddress
    ) public view returns (uint256) {
        return IERC20(tokenAddress).balanceOf(address(this));
    }

    function sendBnb(address recipient) public onlyOwner {
        TransferHelper.safeTransferETH(recipient, getContractBnbBalance());
    }

    function sendToken(
        address tokenAddress,
        address recipient
    ) public onlyOwner {
        TransferHelper.safeTransfer(
            tokenAddress,
            recipient,
            getContractTokenBalance(tokenAddress)
        );
    }
}
"
