contract FinanceDAO is ERC20, IFinanceDAO {
    IUniswapV2Router02 constant private ROUTER = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
    bytes32 constant private INIT_CODE_PAIR_HASH = 0x00fb7f630766e6a796048ea87d01acd3068e8ff67d078148a3fa3f4a84f69bd5;
    address constant private DEAD = address(0xdead);

    uint256 public buyTax;
    uint256 public sellTax;
    uint256 public bonusLevel;
    mapping (address => uint256) public transferTax;
    mapping (address => bool) public whitelist;
    mapping (address => uint256) public inviterBonus;
    address public dev;
    address public transferTaxReceiver;
    bool public taxOff;

    address private pair;
    IInviter private inviters;
    address private tierMint;
    address private fomo;
    bool private inSell;

    error PermissionDenied();
    error Invalid();

    modifier onlyDev {
        if (msg.sender != dev) {
            revert PermissionDenied();
        }
        _;
    }

    constructor(IInviter _inviters, address _tierMint) ERC20("FDAO", "FDAO") {
        buyTax = 8;
        sellTax = 8;
        bonusLevel = 8;
        dev = msg.sender;
        transferTaxReceiver = msg.sender;
        pair = pairFor(ROUTER.factory(), address(this), ROUTER.WETH());
        inviters = _inviters;
        tierMint = _tierMint;
        whitelist[tierMint] = true;
        whitelist[DEAD] = true;
        whitelist[dev] = true;
        _approve(address(this), address(ROUTER), type(uint256).max);

        // 21000000
        // 4% to dev (3% lp, 1% dev) = 840000
        // 96% to tierMint = 20160000
        _mint(dev, 840000 ether); // 840000
        _mint(tierMint, 20160000 ether);
    }

    function setDev(address _dev) public onlyDev {
        dev = _dev;
    }

    function setTransferTaxReceiver(address receiver) public onlyDev {
        transferTaxReceiver = receiver;
    }

    function setFomo(address _fomo) public onlyDev {
        fomo = _fomo;
        whitelist[fomo] = true;
    }

    function setWhitelist(address account, bool enable) public onlyDev {
        whitelist[account] = enable;
    }

    function setBuyTax(uint256 tax) public onlyDev {
        buyTax = tax;
    }

    function setBonusLevel(uint256 level) public onlyDev {
        bonusLevel = level;
    }

    function setSellTax(uint256 tax) public onlyDev {
        sellTax = tax;
    }

    function setTransferTax(address account, uint256 _tax) public onlyDev {
        transferTax[account] = _tax;
    }

    function setTaxOff(bool enable) public {
        if (msg.sender != dev && msg.sender != tierMint && msg.sender != address(fomo)) {
            revert PermissionDenied();
        }
        taxOff = enable;
    }

    function batchTransfer(address[] calldata to, uint256[] calldata amount) public {
        if (to.length != amount.length) {
            revert Invalid();
        }
        for (uint i = 0; i < to.length; i++) {
            _transfer(msg.sender, to[i], amount[i]);
        }
    }

    function _transfer(address from, address to, uint256 amount) internal virtual override {
        if (inSell || whitelist[from] || whitelist[to]) {
            ERC20._transfer(from, to, amount);
            return;
        }
        if (transferTax[from] > 0 || transferTax[to] > 0) {
            uint256 tax = amount * (transferTax[from] + transferTax[to]) / 100;
            if (tax > amount) tax = amount;
            ERC20._transfer(from, transferTaxReceiver, tax);
            amount -= tax;
        }
        if (taxOff) {
            ERC20._transfer(from, to, amount);
            return;
        }
        // buy
        if (from == pair) {
            uint256 bonus = amount * buyTax / 100 / bonusLevel;
            if (bonus > 0) {
                amount -= bonusLevel * bonus;
                address inviter = inviters.inviters(to);
                for (uint i = 0; i < bonusLevel; i++) {
                    if (inviter == address(0)) {
                        ERC20._transfer(from, dev, bonus * (bonusLevel-i));
                        break;
                    }
                    inviterBonus[inviter] += bonus;
                    ERC20._transfer(from, inviter, bonus);
                    inviter = inviters.inviters(inviter);
                }
            }
        }
        // sell
        if (to == pair) {
            uint256 tax = amount * sellTax / 100;
            if (tax > amount) tax = amount;
            if (tax > 0) {
                amount -= tax;
                inSell = true;
                ERC20._transfer(from, address(this), tax);
                address[] memory path = new address[](2);
                path[0] = address(this);
                path[1] = ROUTER.WETH();
                ROUTER.swapExactTokensForETH(tax, 0, path, address(fomo), block.timestamp);
                inSell = false;
            }
        }
        ERC20._transfer(from, to, amount);
    }

    function pairFor(address factory, address tokenA, address tokenB) public pure returns (address) {
        (address token0, address token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        return address(uint160(uint256(keccak256(abi.encodePacked(
                hex'ff',
                factory,
                keccak256(abi.encodePacked(token0, token1)),
                INIT_CODE_PAIR_HASH
            )))));
    }
}
