    mapping(address => bool) public whitelist;

    EnumerableSet.AddressSet private whitelistSet;

    bool public enableWhitelist;

    address public MarketingWallet;
    address public DevWallet;

    uint256 public _buySlippage;
    uint256 public _sellSlippage;
    uint256 public _buyDestroy;
    uint256 public _sellDestroy;

    bool public isChangeFee;
    uint256 public buyTotalFell;
    uint256 public sellTotalFell;

    address public dead = 0x000000000000000000000000000000000000dEaD;

    address public currency;
    ISwapRouter public router;
    address public pair_address;
    uint256 public constant MAX = ~uint256(0);

    error notInWhiteList(address addr);

    constructor(
        string memory name_,
        string memory symbol_,
        string memory name
    ) ERC20(name_, symbol_) ERC20Permit(name) {}

    function setCurrency(address addr) public onlyOwner {
        currency = addr;
    }

    function setRouter(address addr) public onlyOwner {
        router = ISwapRouter(addr);
    }

    function setPair() public onlyOwner {
        ISwapFactory factory = ISwapFactory(router.factory());
        address pair = factory.getPair(currency, address(this));
        if (pair == address(0)) {
            pair_address = factory.createPair(currency, address(this));
        } else {
            pair_address = pair;
        }
        _swapPairList[pair_address] = true;
    }

    function setEnableWhitelist(bool flag) public onlyOwner {
        enableWhitelist = flag;
    }

    function addWhitelist(address[] calldata addr) public onlyOwner {
        uint256 len = addr.length;
        for (uint256 i = 0; i < len; i++) {
            whitelist[addr[i]] = true;
            whitelistSet.add(addr[i]);
        }
    }

    function delWhitelist(address[] calldata addr) public onlyOwner {
        uint256 len = addr.length;
        for (uint256 i = 0; i < len; i++) {
            whitelist[addr[i]] = false;
            whitelistSet.remove(addr[i]);
        }
    }

    function setFeeWhiteList(address addr, bool flag) public onlyOwner {
        _feeWhiteList[addr] = flag;
    }

    function setIsChangeFee(bool flag) public onlyOwner {
        isChangeFee = flag;
    }

    function setMarketingWallet(address addr) public onlyOwner {
        MarketingWallet = addr;
    }

    function setDevWallet(address addr) public onlyOwner {
        DevWallet = addr;
    }

    function setBuySlippage(uint256 num) public onlyOwner {
        _buySlippage = num;
    }

    function setSellSlippage(uint256 num) public onlyOwner {
        _sellSlippage = num;
    }

    function setBuyDestroy(uint256 num) public onlyOwner {
        _buyDestroy = num;
    }

    function setSellDestroy(uint256 num) public onlyOwner {
        _sellDestroy = num;
    }

    function _beforeTransfer(address addr) internal view {
        if (enableWhitelist) {
            require(whitelist[addr], "not in whitelist");
        }
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        uint256 balance = balanceOf(from);
        require(balance >= amount, "balanceNotEnough");
        if (!_feeWhiteList[from] && !_feeWhiteList[to] && isChangeFee) {
            if (_swapPairList[from] || _swapPairList[to]) {
                if (_swapPairList[from]) {
                    _beforeTransfer(to);
                    uint256 buyFell = amount.mul(_buySlippage).div(100);
                    uint256 destroy = amount.mul(_buyDestroy).div(100);
                    amount = amount.sub(buyFell).sub(destroy);
                    super._transfer(from, address(this), buyFell);
                    super._transfer(from, dead, destroy);
                    buyTotalFell = buyTotalFell.add(buyFell);
                }
                if (_swapPairList[to]) {
                    _beforeTransfer(from);
                    uint256 sellFell = amount.mul(_sellSlippage).div(100);
                    uint256 destroy = amount.mul(_sellDestroy).div(100);
                    amount = amount.sub(sellFell).sub(destroy);
                    super._transfer(from, address(this), sellFell);
                    super._transfer(from, dead, destroy);
                    sellTotalFell = sellTotalFell.add(sellFell);
                    sendChangeFee();
                }
            }
        }
        super._transfer(from, to, amount);
        super._afterTokenTransfer(from, to, amount);
    }

    function sendChangeFee() private nonReentrant {
        if (buyTotalFell > 0) {
            tokenToUSDT(MarketingWallet, buyTotalFell);
        }
        if (sellTotalFell > 0) {
            tokenToUSDT(DevWallet, sellTotalFell);
        }
        buyTotalFell = 0;
        sellTotalFell = 0;
    }

    function tokenToUSDT(address to, uint256 amount) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = currency;
        router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            amount,
            0,
            path,
            to,
            block.timestamp + 300
        );
    }
}

contract YFIIDAO is ABS {
    constructor() ABS("DFI.Money Dao", "YFIIDAO", "DFI.Money Dao") {
        _mint(msg.sender, 40000 * 10**decimals());
        setEnableWhitelist(true);
        setMarketingWallet(0x442D8eeB11dD6E357e4e4Eb350282f018aB68158);
        setDevWallet(0x7bEfe0b3a960BEC9e843302B8F9a0B239347Cb65);
        setBuySlippage(3);
        setSellSlippage(3);
        setBuyDestroy(0);
        setSellDestroy(1);
        setCurrency(0x55d398326f99059fF775485246999027B3197955);
        setRouter(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        setPair();
        super._approve(address(this), address(router), MAX);
        super._approve(address(this), pair_address, MAX);
        setFeeWhiteList(owner(), true);
        setFeeWhiteList(address(this), true);
        setFeeWhiteList(MarketingWallet, true);
        setFeeWhiteList(DevWallet, true);
        setIsChangeFee(true);
        whitelist[owner()] = true;
    }
}
