// File: contracts/GalaxyStakeNew.sol


pragma solidity ^0.8.0;










contract GalaxyStake is Pausable, AccessControl {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using EnumerableSet for EnumerableSet.AddressSet;

    bytes32 public constant CONFIG_ROLE = keccak256("CONFIG");
    bytes32 public constant AMOUNT_ROLE = keccak256("AMOUNT");

    uint256 constant FULL_100 = 100e18; // 100 in decimals 18
    uint256 constant YEAR_IN_SECONDS = 365 * 24 * 60 * 60;

    modifier onlyConfigRole() {
        _checkRole(CONFIG_ROLE, _msgSender());
        _;
    }

    modifier onlyAmountRole() {
        _checkRole(AMOUNT_ROLE, _msgSender());
        _;
    }

    modifier onlyAdminRole() {
        _checkRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _;
    }

    modifier validId(uint256 _lid) {
        require(lockInfos[_lid].owner != address(0), "invalid id");
        _;
    }

    bool public isMigrating = false;
    IERC20 public rewardToken;
    uint256 public rewardDecimals;
    IRouter public router;
    uint256 public totalAmount;
    uint256 public id;
    uint256 public rewardStartTime;
    bool public disableDeposit;
    IGlxReferral public glxReferral;
    address public usdtAddress =
        address(0x55d398326f99059fF775485246999027B3197955);
    address public glfLPAddress =
        address(0x08262CD22B0A70C3a7803e4AfB66C03821FC1f9B);

    mapping(address => uint256[]) public userIds; // lockIds of each user
    mapping(uint256 => LockInfo) public lockInfos;
    struct LockInfo {
        address owner;
        uint256 amount; // staked amount
        uint256 rewardAmount;
        uint256 lockedAt;
        uint256 lastRewardTime; // last reward calculation
        uint256 pid;
        uint256 withdrawnAt;
    }

    PoolInfo[] public poolInfos;
    struct PoolInfo {
        uint256 duration;
        uint256 apy; // decimals 18
        uint256 directCommission;
    }

    event Deposit(address indexed user, uint256 indexed id, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed id, uint256 amount);
    event Harvest(address indexed user, uint256 indexed id, uint256 amount);

    // WARNING index from 1
    // position = 0 => not exist
    mapping(address => uint256) public addedUserPositions;

    EnumerableSet.AddressSet private users;

    constructor(address _rewardTokenAddress, address _router) {
        rewardToken = IERC20(_rewardTokenAddress);
        rewardDecimals = ERC20(_rewardTokenAddress).decimals();
        router = IRouter(_router);
        _setupRole(CONFIG_ROLE, msg.sender);
        _setupRole(AMOUNT_ROLE, msg.sender);
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        rewardStartTime = block.timestamp + 365 * 24 * 60 * 60; // default 1 year from created time
    }

    function depositBySale(
        address _user,
        uint256 _amount,
        uint256 _pid,
        address _ref
    ) external onlyAmountRole whenNotPaused returns (uint256) {
        return _deposit(_user, _amount, _pid, _ref);
    }

    function deposit(
        uint256 _amount,
        uint256 _pid,
        address _ref
    ) external whenNotPaused returns (uint256) {
        router.transferErc20(rewardToken, msg.sender, address(this), _amount);
        return _deposit(msg.sender, _amount, _pid, _ref);
    }

    function _deposit(
        address _user,
        uint256 _amount,
        uint256 _pid,
        address _ref
    ) private returns (uint256) {
        require(!disableDeposit, "disabled");
        require(!isMigrating, "migrating");
        uint256 _id = id++;

        require(_amount > 0, "invalid amount");
        require(_pid < poolInfos.length, "no tier");
        userIds[_user].push(_id);

        lockInfos[_id] = LockInfo({
            owner: _user,
            amount: _amount,
            rewardAmount: 0,
            lockedAt: block.timestamp,
            lastRewardTime: block.timestamp,
            pid: _pid,
            withdrawnAt: 0
        });

        totalAmount = totalAmount.add(_amount);

        if (
            address(glxReferral) != address(0) &&
            _ref != address(0) &&
            _ref != _user &&
            glxReferral.getReferrer(_user) == address(0)
        ) {
            glxReferral.recordReferral(_user, _ref);
        }

        if (address(glxReferral) != address(0)) {
            address referrer = glxReferral.getReferrer(_user);
            if (referrer != address(0)) {
                uint256 totalFund = _amount.mul(getGlfPrice()).div(1e2);
                glxReferral.addTotalFund(referrer, totalFund, 0);
                uint256 _directCommission = poolInfos[_pid].directCommission;
                payDirectCommission(_amount, _directCommission, referrer);
            }
        }

        if (!users.contains(_user)) {
            users.add(_user);
        }

        emit Deposit(_user, _id, _amount);

        return _id;
    }

    function withdraws(uint256[] memory _lids) external whenNotPaused {
        for (uint256 i = 0; i < _lids.length; i++) {
            withdraw(_lids[i]);
        }
    }

    function withdraw(uint256 _lid) public whenNotPaused validId(_lid) {
        LockInfo storage lockInfo = lockInfos[_lid];
        require(lockInfo.owner == msg.sender, "invalid owner");
        require(lockInfo.withdrawnAt == 0, "withdrawn");
        require(!isMigrating, "migrating");
        updateLockReward(_lid);

        uint256 lockDuration = poolInfos[lockInfo.pid].duration;

        require(
            lockInfo.lockedAt.add(lockDuration) <= block.timestamp,
            "Under locked"
        );

        uint256 _amount = lockInfo.amount;

        rewardToken.safeTransfer(lockInfo.owner, _amount);
        totalAmount = totalAmount.sub(_amount);
        lockInfo.withdrawnAt = block.timestamp;

        emit Withdraw(msg.sender, _lid, _amount);
    }

    function harvests(uint256[] memory _lids) external whenNotPaused {
        for (uint256 i = 0; i < _lids.length; i++) {
            harvest(_lids[i]);
        }
    }

    function harvest(uint256 _lid) public whenNotPaused validId(_lid) {
        require(!isMigrating, "migrating");
        LockInfo storage lockInfo = lockInfos[_lid];
        require(lockInfo.owner == msg.sender, "invalid owner");
        updateLockReward(_lid);

        uint256 rewardAmount = lockInfo.rewardAmount;
        rewardToken.safeTransfer(lockInfo.owner, rewardAmount);
        lockInfo.rewardAmount = 0;

        emit Harvest(msg.sender, _lid, rewardAmount);
    }

    function updateLockReward(uint256 _lid) public validId(_lid) {
        LockInfo storage lockInfo = lockInfos[_lid];
        if (lockInfo.withdrawnAt > 0) {
            return;
        }
        if (block.timestamp < rewardStartTime) {
            return;
        }
        if (lockInfo.lastRewardTime < rewardStartTime) {
            // calculate from START_TIME
            lockInfo.lastRewardTime = rewardStartTime;
        }
        uint256 apy = getLockApy(_lid);
        uint256 timeDiff = block.timestamp.sub(lockInfo.lastRewardTime);
        uint256 pendingReward = lockInfo
            .amount
            .mul(timeDiff)
            .mul(apy)
            .div(FULL_100)
            .div(YEAR_IN_SECONDS);
        lockInfo.rewardAmount = lockInfo.rewardAmount.add(pendingReward);
        lockInfo.lastRewardTime = block.timestamp;
    }

    // gas tracking
    function massUpdateLocks(uint256[] memory _ids) external {
        for (uint256 index = 0; index < _ids.length; index++) {
            updateLockReward(_ids[index]);
        }
    }

    function getPendingReward(uint256 _id) external view returns (uint256) {
        LockInfo memory lockInfo = lockInfos[_id];
        uint256 pendingReward = 0;
        if (block.timestamp > rewardStartTime && lockInfo.withdrawnAt == 0) {
            uint256 lastTime = lockInfo.lastRewardTime;
            if (lastTime < rewardStartTime) {
                lastTime = rewardStartTime;
            }
            uint256 apy = getLockApy(_id);
            uint256 timeDiff = block.timestamp.sub(lastTime);
            pendingReward = lockInfo
                .amount
                .mul(timeDiff)
                .mul(apy)
                .div(FULL_100)
                .div(YEAR_IN_SECONDS);
        }
        return lockInfo.rewardAmount.add(pendingReward);
    }

    function getPoolInfos() external view returns (PoolInfo[] memory) {
        return poolInfos;
    }

    function getLockApy(uint256 _id) public view returns (uint256) {
        if (lockInfos[_id].withdrawnAt > 0) return 0;
        uint256 pid = lockInfos[_id].pid;

        return poolInfos[pid].apy;
    }

    function getUserLength() external view returns (uint256) {
        return users.length();
    }

    function getUserAt(uint256 _at) external view returns (address) {
        return users.at(_at);
    }

    function getUsers() external view returns (address[] memory) {
        return users.values();
    }

    function getUserIdLength(address _user) external view returns (uint256) {
        return userIds[_user].length;
    }

    function getUserIds(address _user)
        external
        view
        returns (uint256[] memory)
    {
        return userIds[_user];
    }

    // statistical only
    function getLockInfos(uint256[] memory _ids)
        external
        view
        returns (LockInfo[] memory)
    {
        uint256 _length = _ids.length;
        LockInfo[] memory _lis = new LockInfo[](_length);
        for (uint256 index = 0; index < _length; index++) {
            _lis[index] = lockInfos[_ids[index]];
        }
        return (_lis);
    }

    // admin feature
    function setPoolInfo(
        uint256 _pid,
        uint256 _duration,
        uint256 _apy,
        uint256 _directCommission
    ) external onlyConfigRole {
        poolInfos[_pid].duration = _duration;
        poolInfos[_pid].apy = _apy;
        poolInfos[_pid].directCommission = _directCommission;
    }

    function addPoolInfo(
        uint256 _duration,
        uint256 _apy,
        uint256 _directCommission
    ) external onlyConfigRole {
        poolInfos.push(
            PoolInfo({
                duration: _duration,
                apy: _apy,
                directCommission: _directCommission
            })
        );
    }

    function setReawrdToken(address _token) external onlyConfigRole {
        rewardToken = IERC20(_token);
        rewardDecimals = ERC20(_token).decimals();
    }

    function setRewardStartTime(uint256 _time) external onlyConfigRole {
        rewardStartTime = _time;
    }

    function removeOtherTokenWithAmount(
        address _tokenAddress,
        address _to,
        uint256 _amount
    ) external onlyConfigRole {
        ERC20 erc20Token = ERC20(_tokenAddress);
        require(
            erc20Token.transfer(_to, _amount),
            "ERC20 Token transfer failed"
        );
    }

    function pause() external onlyConfigRole whenNotPaused {
        _pause();
    }

    function unpause() external onlyConfigRole whenPaused {
        _unpause();
    }

    function grantAmountRole(address _address) external onlyConfigRole {
        grantRole(AMOUNT_ROLE, _address);
    }

    function revokeAmountRole(address _address) external onlyConfigRole {
        revokeRole(AMOUNT_ROLE, _address);
    }

    function grantConfigRole(address _address) external onlyAdminRole {
        grantRole(CONFIG_ROLE, _address);
    }

    function revokeConfigRole(address _address) external onlyAdminRole {
        revokeRole(CONFIG_ROLE, _address);
    }

    function setDisable(bool val) external onlyConfigRole {
        disableDeposit = val;
    }

    function setGlxReferral(IGlxReferral _glxReferral) external onlyConfigRole {
        glxReferral = _glxReferral;
    }

    function setIsMigrating(bool _isMigrating) external onlyConfigRole {
        isMigrating = _isMigrating;
    }

    function getGlfPrice() public view returns (uint256) {
        return
            IERC20(usdtAddress).balanceOf(glfLPAddress).mul(1e2).div(
                rewardToken.balanceOf(glfLPAddress)
            );
    }

    function setUsdtAddress(address _usdtAddress) external onlyConfigRole {
        usdtAddress = _usdtAddress;
    }

    function setGlfLPAddress(address _glfLPAddress) external onlyConfigRole {
        glfLPAddress = _glfLPAddress;
    }

    function payDirectCommission(
        uint256 _amount,
        uint256 directCommission,
        address referrer
    ) internal {
        uint256 directCommissionAmount = _amount.mul(directCommission).div(1e2);
        rewardToken.safeTransfer(referrer, directCommissionAmount);
        glxReferral.recordReferralCommission(
            referrer,
            directCommissionAmount.mul(getGlfPrice()).div(1e2)
        );
    }
}
