// File: contracts/MOGCrystal.sol



pragma solidity ^0.8.0;









contract MOGCrystal is ERC721EnumerableUpgradeable, ERC721BurnableUpgradeable, ERC721PausableUpgradeable, AccessControlEnumerableUpgradeable, UUPSUpgradeable{
    using SafeMathUpgradeable for uint256;
    using StringsUpgradeable for uint256;
    
    bytes32 public constant UPGRADER_ROLE = keccak256("UPGRADER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    uint256 public lastTokenID;
    string public baseURI;

    function initialize() public initializer{
        __ERC721_init("MOG Crystal","MOGC");
        __ERC721Enumerable_init();
        __AccessControl_init();
        __Pausable_init();
        __UUPSUpgradeable_init();

        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(PAUSER_ROLE, msg.sender);
        _setupRole(UPGRADER_ROLE, msg.sender);
        _setupRole(MINTER_ROLE, msg.sender);

        baseURI = "https://apimarket.mogwar.com/crystal/";
    }

    function _authorizeUpgrade(address newImplementation) internal override onlyRole(UPGRADER_ROLE) {}
    function supportsInterface(bytes4 interfaceId) public view override(ERC721EnumerableUpgradeable,ERC721Upgradeable, AccessControlEnumerableUpgradeable) returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
    
    function pause() public onlyRole(PAUSER_ROLE) {_pause();}
    function unpause() public onlyRole(PAUSER_ROLE) {_unpause();}

    function _beforeTokenTransfer(address from,address to,uint256 tokenId) internal virtual override(ERC721EnumerableUpgradeable,ERC721PausableUpgradeable,ERC721Upgradeable) {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    function mint(address _address) external onlyRole(MINTER_ROLE) returns (uint256) {
        lastTokenID++;
        _mint(_address, lastTokenID);
        return lastTokenID;
    }
    function batchMint(address _address, uint256 amount) external onlyRole(MINTER_ROLE) {
        for (uint256 i = 0; i < amount; i++) {
            lastTokenID++;
            _mint(_address, lastTokenID);
        }
    }

    function setBaseURI(string memory uri) external onlyRole(DEFAULT_ADMIN_ROLE) {baseURI = uri;}
    function _baseURI() internal view override returns (string memory) {return baseURI;}
}
