interface IBind{
    function userMap(address account) external view returns(bool active, address referrer, uint subNum);
    function bindRelationshipExternal(address account, address referrer) external;
    function getSubordinates(address account) external view returns(address[] memory addrs);
    function getReferrer(address account)external view returns(address addr);
    function addMedal(address account,uint medalKind)external;
    function getMedal(address account,uint medalKind)external view returns(uint);
    function setOperator(address[]calldata,bool)external;
}

contract COD is Context, IERC20, IERC20Metadata,Ownable {
    using Address for address;
    mapping(address => uint256) private _balances;

    mapping(address => mapping(address => uint256)) private _allowances;

    uint256 private _totalSupply;

    string private _name;
    string private _symbol;

    address public _pool;
    address public _pay;
    address public _bind;
    mapping(address => bool) public _whitelistFrom;
    mapping(address => bool) public _whitelistTo;
    mapping(address => address) public _inviter;

    mapping(address => bool) public _markets;

    constructor() {
        _name = "CITY OF DREAM";
        _symbol = "COD";
        _mint(msg.sender,1*1e29);
    }

    function setMarket(address[] calldata addr,bool state)public onlyOwner{
        for(uint index=0;index<addr.length;index++){
            _markets[addr[index]] = state;
        }
    }

    function setPay(address addr)public onlyOwner{
        _pay = addr;
    }

    function setPool(address addr)public onlyOwner{
        _pool = addr;
    }


    function setBind(address bind)public onlyOwner{
        _bind = bind;
    }

    function setWhitelistFrom(address addr,bool state)public onlyOwner{
        _whitelistFrom[addr] = state;
    }

    function setWhitelistTo(address addr,bool state)public onlyOwner{
        _whitelistTo[addr] = state;
    }

    function setBidirectionWhitelist(address addr,bool state)public onlyOwner{
        _whitelistFrom[addr] = state;
        _whitelistTo[addr] = state;
    }

    function setBatchBidirectionWhitelist(address[] calldata addr,bool state)public onlyOwner{
        for(uint index = 0;index<addr.length;index++){
            _whitelistFrom[addr[index]] = state;
            _whitelistTo[addr[index]] = state;
        }
    }

    function name() public view virtual override returns (string memory) {
        return _name;
    }

    function symbol() public view virtual override returns (string memory) {
        return _symbol;
    }

    function decimals() public view virtual override returns (uint8) {
        return 18;
    }


    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }


    function balanceOf(address account) public view virtual override returns (uint256) {
        return _balances[account];
    }


    function transfer(address recipient, uint256 amount) public virtual override returns (bool) {
        _transfer(_msgSender(), recipient, amount,_needFee(_msgSender(),recipient));
        return true;
    }


    function allowance(address owner, address spender) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }


    function approve(address spender, uint256 amount) public virtual override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }


    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public virtual override returns (bool) {

        _transfer(sender, recipient, amount,_needFee(sender,recipient));

        uint256 currentAllowance = _allowances[sender][_msgSender()];
        require(currentAllowance >= amount, "ERC20: transfer amount exceeds allowance");
        unchecked {
            _approve(sender, _msgSender(), currentAllowance - amount);
        }

        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender] + addedValue);
        return true;
    }


    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        uint256 currentAllowance = _allowances[_msgSender()][spender];
        require(currentAllowance >= subtractedValue, "ERC20: decreased allowance below zero");
        unchecked {
            _approve(_msgSender(), spender, currentAllowance - subtractedValue);
        }

        return true;
    }


    function _transfer(
        address sender,
        address recipient,
        uint256 amount,
        bool needFee
    ) internal virtual {
        __bind(sender,recipient);
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");

        _beforeTokenTransfer(sender, recipient, amount);

        uint256 senderBalance = _balances[sender];
        require(senderBalance >= amount, "ERC20: transfer amount exceeds balance");
        unchecked {
            _balances[sender] = senderBalance - amount;
        }
        if(!needFee){
            _balances[recipient] += amount;
            emit Transfer(sender, recipient, amount);
        }else{
            uint actualAmount = amount*9/10;
            _balances[recipient] += actualAmount;
            uint feeAmount = amount-actualAmount;
            uint bonusFee = feeAmount*4/5;
            _balances[_pool] = _balances[_pool] + bonusFee;
            address referrer = IBind(_bind).getReferrer(sender);
            if(sender.isContract()){
                referrer = IBind(_bind).getReferrer(recipient);
            }
            _balances[referrer] = _balances[referrer]+feeAmount-bonusFee;
            emit Transfer(sender, recipient, actualAmount);
            emit Transfer(sender, _pool, bonusFee);
            emit Transfer(sender, referrer, feeAmount-bonusFee);
        }
        
        

        _afterTokenTransfer(sender, recipient, amount);
    }

    function _needFee(address from,address to)internal view returns(bool){
        if(_whitelistFrom[from] || _whitelistTo[to] || _markets[msg.sender]){
            return false;
        }
        return true;
    }

    function __bind(address from,address to)internal{
        bool shouldSetInviter = balanceOf(to) == 0 &&
            IBind(_bind).getReferrer(to) == address(0) &&
            !to.isContract();

        if (shouldSetInviter) {
            if(from.isContract()){
                IBind(_bind).bindRelationshipExternal(to,address(0));
            }else {
                IBind(_bind).bindRelationshipExternal(to,from);
            }
        }
    }


    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: mint to the zero address");

        _beforeTokenTransfer(address(0), account, amount);

        _totalSupply += amount;
        _balances[account] += amount;
        emit Transfer(address(0), account, amount);

        _afterTokenTransfer(address(0), account, amount);
    }

    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: burn from the zero address");

        _beforeTokenTransfer(account, address(0), amount);

        uint256 accountBalance = _balances[account];
        require(accountBalance >= amount, "ERC20: burn amount exceeds balance");
        unchecked {
            _balances[account] = accountBalance - amount;
        }
        _totalSupply -= amount;

        emit Transfer(account, address(0), amount);

        _afterTokenTransfer(account, address(0), amount);
    }


    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }


    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}

    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}
}
