contract TokenMigrationDynamicVesting is ReentrancyGuard, Initializable {
  using SafeMath for uint256;
  using SafeBEP20 for IBEP20;

  // Info of each user.
  struct UserInfo {
      uint256 amount;   // How many tokens the user has provided.
      uint256 claimedAmount;  // default 0
      bool isRefunded;  // default false
  }

  // admin address
  address public adminAddress;
  // The raising token
  IBEP20 public lpToken;
  // The offering token
  IBEP20 public offeringToken;
  // The timestamp when IHO starts
  uint256 public startTimestamp;
  // The timestamp when IHO ends
  uint256 public endTimestamp;
  // total amount of raising tokens need to be raised
  uint256 public raisingAmount;
  // total amount of offeringToken that will offer
  uint256 public offeringAmount;
  // total amount of raising tokens that have already raised
  uint256 public totalAmount;
  // address => amount
  mapping(address => UserInfo) public userInfo;
  // participators
  address[] public addressList;
  // whitelists
  mapping(address => bool) public whiteList;
  bool public isWhitelist = false;
  // The maximum rasing amount of user
  uint256 public maxUserRaisingCapacity;
  
  // withdrawal lock
  uint256[] public withdrawalLockIntervals = [1];
  uint16[] public withdrawalLockRate = [100];


  event Deposit(address indexed user, uint256 amount);
  event WithdrawRefund(address indexed user, uint256 amount);
  event Harvest(address indexed user, uint256 amount);

  constructor() public {
  }

  function initialize(
      IBEP20 _lpToken,
      IBEP20 _offeringToken,
      uint256 _startTimestamp,
      uint256 _endTimestamp,
      uint256 _offeringAmount,
      uint256 _raisingAmount,
      address _adminAddress,
      uint256 _maxUserRaisingCapacity
  ) public initializer {
      lpToken = _lpToken;
      offeringToken = _offeringToken;
      startTimestamp = _startTimestamp;
      endTimestamp = _endTimestamp;
      offeringAmount = _offeringAmount;
      raisingAmount= _raisingAmount;
      totalAmount = 0;
      adminAddress = _adminAddress;
      maxUserRaisingCapacity = _maxUserRaisingCapacity;
  }

  modifier onlyAdmin() {
    require(msg.sender == adminAddress, "admin: wut?");
    _;
  }
  
  function setWhitelist(address[] memory _whiteList) external onlyAdmin {
    for (uint16 i = 0; i < _whiteList.length; i++) {
      whiteList[_whiteList[i]] = true;
    }
    isWhitelist = (_whiteList.length > 0);
  }

  function setOfferingAmount(uint256 _offerAmount) public onlyAdmin {
    require (block.timestamp < startTimestamp, 'no');
    offeringAmount = _offerAmount;
  }

  function setRaisingAmount(uint256 _raisingAmount) public onlyAdmin {
    require (block.timestamp < startTimestamp, 'no');
    raisingAmount = _raisingAmount;
  }

  function deposit(uint256 _amount) public {
    require (block.timestamp > startTimestamp, 'not IDO time');
    require (_amount > 0, 'need _amount > 0');
    if (maxUserRaisingCapacity > 0)
      require (userInfo[msg.sender].amount.add(_amount) <= maxUserRaisingCapacity, 'Rasing amount is too much');
    if (isWhitelist)
      require (whiteList[msg.sender], 'The user is not included to white list');
    uint256 wantBalBefore = IBEP20(lpToken).balanceOf(address(this));
    lpToken.safeTransferFrom(address(msg.sender), address(this), _amount);
    uint256 wantBalAfter = IBEP20(lpToken).balanceOf(address(this));
    _amount = wantBalAfter.sub(wantBalBefore);
    
    if (userInfo[msg.sender].amount == 0) {
      addressList.push(address(msg.sender));
    }
    userInfo[msg.sender].amount = userInfo[msg.sender].amount.add(_amount);
    totalAmount = totalAmount.add(_amount);
    emit Deposit(msg.sender, _amount);
  }
  
  function withdrawRefund() public nonReentrant {
    require (block.timestamp > endTimestamp, 'not withdraw time');
    require (userInfo[msg.sender].amount > 0, 'have you participated?');
    uint256 refundingTokenAmount = getRefundingAmount(msg.sender);
    
    if (refundingTokenAmount > 0) {
      lpToken.safeTransfer(address(msg.sender), refundingTokenAmount);
      userInfo[msg.sender].isRefunded = true;
    }
    
    emit WithdrawRefund(msg.sender, refundingTokenAmount);
  }

  function harvest() public nonReentrant {
    require (block.timestamp > endTimestamp, 'not harvest time');
    require (userInfo[msg.sender].amount > 0, 'have you participated?');
    uint256 offeringTokenAmount = getClaimableOfferingAmount(msg.sender);
    
    if (offeringTokenAmount > 0) {
      offeringToken.safeTransfer(address(msg.sender), offeringTokenAmount);
      userInfo[msg.sender].claimedAmount = userInfo[msg.sender].claimedAmount.add(offeringTokenAmount);
      userInfo[msg.sender].amount = 0;
    }
    
    emit Harvest(msg.sender, offeringTokenAmount);
  }

  // allocation 100000 means 0.1(10%), 1 meanss 0.000001(0.0001%), 1000000 means 1(100%)
  function getUserAllocation(address _user) public view returns(uint256) {
    return userInfo[_user].amount.mul(1e12).div(totalAmount).div(1e6);
  }

  // get the amount of IDO token user is able to get so far
  function getClaimableOfferingAmount(address _user) public view returns(uint256) {
    if (userInfo[_user].amount == 0)
      return 0;
      
    uint256 claimableRate = getClaimableRate();
    if (claimableRate == 0)
      return 0;
    
    uint256 totalOfferingAmount = 0;
    if (totalAmount > raisingAmount) {
      uint256 allocation = getUserAllocation(_user);
      totalOfferingAmount = offeringAmount.mul(allocation).div(1e6);
    }
    else {
      // userInfo[_user] / (raisingAmount / offeringAmount)
      totalOfferingAmount = userInfo[_user].amount.mul(offeringAmount).div(raisingAmount);
    }
    uint256 totalClaimableOfferingAmount = totalOfferingAmount.mul(claimableRate).div(100);
    return totalClaimableOfferingAmount;
  }

  // get the amount of IDO token locked for user
  function getClaimLockedOfferingAmount(address _user) public view returns(uint256) {
    if (userInfo[_user].amount == 0)
      return 0;
      
    uint256 claimableRate = getClaimableRate();
      
    uint256 totalOfferingAmount = 0;
    if (totalAmount > raisingAmount) {
      uint256 allocation = getUserAllocation(_user);
      totalOfferingAmount = offeringAmount.mul(allocation).div(1e6);
    }
    else {
      // userInfo[_user] / (raisingAmount / offeringAmount)
      totalOfferingAmount = userInfo[_user].amount.mul(offeringAmount).div(raisingAmount);
    }
    uint256 totalClaimLockedOfferingAmount = totalOfferingAmount.mul(100 - claimableRate).div(100);
    return totalClaimLockedOfferingAmount;
  }
  
  // return value 100 means 100%, 1 means 1%
  function getClaimableRate() public view returns(uint256) {
    if (block.timestamp < endTimestamp) {
        return 0;
    }
      
    uint256 elapsedAfterEnd = block.timestamp.sub(endTimestamp);
    uint i = 0;
    for (; i < withdrawalLockIntervals.length; i++) {
      if (elapsedAfterEnd < withdrawalLockIntervals[i])
        break;
    }
    if (i == 0)
      return 0;
      
    if (i == withdrawalLockIntervals.length)
      return withdrawalLockRate[i - 1];
    else {
      uint256 rate = (elapsedAfterEnd - withdrawalLockIntervals[i - 1]) * 100 /
        (withdrawalLockIntervals[i] - withdrawalLockIntervals[i - 1]);
      return withdrawalLockRate[i - 1] + (withdrawalLockRate[i] - withdrawalLockRate[i - 1]) * rate / 100;
    }
  }

  // get the amount of lp token you will be refunded
  function getRefundingAmount(address _user) public view returns(uint256) {
    if (totalAmount <= raisingAmount) {
      return 0;
    }
    if (userInfo[_user].isRefunded) {
      return 0;
    }
    
    uint256 allocation = getUserAllocation(_user);
    uint256 payAmount = raisingAmount.mul(allocation).div(1e6);
    return userInfo[_user].amount.sub(payAmount);
  }

  function getAddressListLength() external view returns(uint256) {
    return addressList.length;
  }

  function finalWithdraw(uint256 _lpAmount, uint256 _offerAmount) public onlyAdmin {
    require (_lpAmount <= lpToken.balanceOf(address(this)), 'not enough raising token');
    require (_offerAmount <= offeringToken.balanceOf(address(this)), 'not enough offering token');
    if(_offerAmount > 0) {
      offeringToken.safeTransfer(address(msg.sender), _offerAmount);
    }
    if(_lpAmount > 0) {
      lpToken.safeTransfer(address(msg.sender), _lpAmount);
    }
  }
    
  function setWithdrawLock(uint256[] memory _withdrawalLockIntervals, uint16[] memory _withdrawalLockRate) public onlyAdmin {
      require (_withdrawalLockIntervals.length == _withdrawalLockRate.length, 'setWithdrawLock: _withdrawalLockRate length is should be equal to _withdrawalLockIntervals length');
      require (_withdrawalLockRate.length > 0, 'setWithdrawLock: _withdrawalLockRate length is one more than 0');
      for (uint i = 0; i < _withdrawalLockIntervals.length - 1; i++) {
          require (_withdrawalLockIntervals[i] < _withdrawalLockIntervals[i + 1], 'setWithdrawLock: The lock interval must be ascending');
          require (_withdrawalLockRate[i] < _withdrawalLockRate[i + 1], 'setWithdrawLock: The lock rate must be ascending');
      }
      require (_withdrawalLockRate[_withdrawalLockRate.length - 1] == 100, 'setWithdrawLock: 100% Withdraw should be possible');
      
      withdrawalLockIntervals = _withdrawalLockIntervals;
      withdrawalLockRate = _withdrawalLockRate;
  }
}
