      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

interface IWBNB {
    function deposit() external payable;
}

contract PRC20 is ERC20 {
    uint256 public difficulty;
    uint256 public limitPerMint;
    uint256 public challenge;
    uint256 public totalSupplyCap;
    uint256 public miningLimit;
    uint256 public mintFee;
    address public deployer;

    uint256 private currentEpoch;
    uint256 public epochs;
    uint256 public increasedDifficulty;
    bool public isHalfPerMint;

    uint256 public totalMintingTimes;
    uint256 private HALFTIME;
    uint256 public nextHalfTime;

    mapping(address => uint256) public miningTimes;
    mapping(address => mapping(uint256 => bool)) public minedNonces;

    event Halve(uint256 newLimitPerMint, uint256 newDifficulty);

    modifier checkHalve() {
        if (epochs == 0) {
            if (totalMintingTimes == nextHalfTime) {
                if (isHalfPerMint) {
                    limitPerMint = limitPerMint / 2;
                }
                difficulty = difficulty + increasedDifficulty;
                nextHalfTime = nextHalfTime + HALFTIME;
                emit Halve(limitPerMint, difficulty);
            }
        }
        if (epochs > 1) {
            if (totalMintingTimes == nextHalfTime) {
                if (isHalfPerMint) {
                    limitPerMint = limitPerMint / 2;
                }
                difficulty = difficulty + increasedDifficulty;
                currentEpoch++;
                if (currentEpoch == epochs) {
                    nextHalfTime = type(uint256).max;
                } else {
                    nextHalfTime = nextHalfTime + HALFTIME;
                }

                emit Halve(limitPerMint, difficulty);
            }
        }
        _;
    }

    modifier checkFee() {
        if (mintFee > 0) {
            require(msg.value == mintFee, "Insufficient fee");
            IWBNB(0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c).deposit{
                value: msg.value
            }();
            IERC20(0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c).approve(
                address(this),
                msg.value
            );
            IERC20(0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c).transferFrom(
                address(this),
                deployer,
                msg.value
            );
        }
        _;
    }

    constructor(
        string memory name,
        string memory symbol,
        uint256 _totalSupplyCap,
        uint256 _limitPerMint,
        uint256 _miningLimit,
        uint256 _mintFee,
        uint256 _initDifficulty,
        uint256 _epochs, // 0 Indefinite epochs 1 linear epochs
        uint256 _increasedDifficulty, // The increased difficulty in each epoch
        bool _isHalfPerMint,
        address _deployer
    ) ERC20(name, symbol) {
        difficulty = _initDifficulty;
        limitPerMint = _limitPerMint * (10 ** uint256(18));
        challenge = block.timestamp;
        totalSupplyCap = _totalSupplyCap * (10 ** uint256(18));
        miningLimit = _miningLimit;
        mintFee = _mintFee;
        deployer = _deployer;

        epochs = _epochs;
        increasedDifficulty = _increasedDifficulty;
        isHalfPerMint = _isHalfPerMint;

        currentEpoch = 1;
        HALFTIME = (_totalSupplyCap / _limitPerMint) / 2;
        nextHalfTime = HALFTIME;
        totalMintingTimes = 0;
    }

    function mine(uint256 nonce) public payable checkHalve checkFee {
        require(miningTimes[msg.sender] < miningLimit, "Mining limit reached");
        require(
            totalSupply() + limitPerMint <= totalSupplyCap,
            "Total supply cap exceeded"
        );

        require(
            !minedNonces[msg.sender][nonce],
            "Nonce already used for mining"
        );

        uint256 hash = uint256(
            keccak256(abi.encodePacked(challenge, msg.sender, nonce))
        );
        require(
            hash < ~uint256(0) >> difficulty,
            "Hash does not meet difficulty requirement"
        );

        _mint(msg.sender, limitPerMint);

        miningTimes[msg.sender]++;
        totalMintingTimes++;
        minedNonces[msg.sender][nonce] = true;
    }

    function transfer(
        address to,
        uint256 amount
    ) public override returns (bool) {
        require(
            totalSupply() >= totalSupplyCap,
            "Transfer not allowed until max supply is reached"
        );
        return super.transfer(to, amount);
    }

    function getLimitPerMint() public view returns (uint256) {
        return limitPerMint;
    }

    function getRemainingSupply() public view returns (uint256) {
        return totalSupplyCap - totalSupply();
    }
}
"
