    "contracts/AccessControlProxy/UUPSAccessControlUpgradeable.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/utils/structs/EnumerableSetUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

abstract contract UUPSAccessControlUpgradeable is UUPSUpgradeable, OwnableUpgradeable{

    using EnumerableSetUpgradeable for EnumerableSetUpgradeable.AddressSet;

    event DeployerAuthorizationUpdated(address indexed deployerAddress, bool givenAccess, address indexed byAccount);

    EnumerableSetUpgradeable.AddressSet private deployers;

    function __UUPSAccessControlUpgradeable_init() internal onlyInitializing {
        __Ownable_init();
        __UUPSUpgradeable_init();
        __UUPSAccessControlUpgradeable_init_unchained();
    }

    function __UUPSAccessControlUpgradeable_init_unchained() internal onlyInitializing {

    }

    function _authorizeUpgrade(address _newImplementation) internal override {
        if(_msgSender() != owner() && !deployers.contains(_msgSender())){
            revert("Not authorized to upgrade");
        }
    }

    function authorizeDeployer(address _deployer, bool _shouldAuthorize) external onlyOwner returns(bool success) {
        if(_shouldAuthorize){
            success = deployers.add(_deployer);
        } else {
            success = deployers.remove(_deployer);
        }
        if(success){
            emit DeployerAuthorizationUpdated(_deployer, _shouldAuthorize, _msgSender());
        }
    }

    function renounceDeployerAuthorization() external returns(bool success) {
        success = deployers.remove(_msgSender());
        if(success){
            emit DeployerAuthorizationUpdated(_msgSender(), false, _msgSender());
        } else {
            revert("Caller is not deploy authorized");
        }
    }

    function getAuthorizedDeployers() external view returns(address[] memory authorizedDeployers){
        authorizedDeployers = deployers.values();
    }

    function getAuthorizedDeployerAtIndex(uint256 _index) external view returns(address authorizedDeployer){
        authorizedDeployer = deployers.at(_index);
    }

    function getAuthorizedDeployerCount() external view returns(uint256 authorizedDeployerCount){
        authorizedDeployerCount = deployers.length();
    }
}
"
    },
    "contracts/DynamicSwapper.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "./interfaces/IDynamicSwapper.sol";
import "./Swapper.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";


contract DynamicSwapper is IDynamicSwapper, Swapper {

    using EnumerableSetUpgradeable for EnumerableSetUpgradeable.AddressSet;

    enum SwapperMode {
        STANDARD,
        DYNAMIC_BUY_ONLY,
        DYNAMIC_ADD_LIQUIDITY,
        DYNAMIC_BUY_AND_ADD_LIQUIDITY
    }

    /* @notice Always should total 100% in allocations so no divisor is needed.
     * Only allocations related to current swap mode will be used for calculations.
     */
    struct PaymentAllocation {
        uint256 vaultAllocationPercentage;
        uint256 buyBackAllocationPercentage;
        uint256 lpAllocationPercentage;
        uint256 minBNBToAddLP;
        SwapperMode swapMode;
    }

    struct DiscountSettings {
        uint256 discountPercentage;
        uint256 discountPercentageDenominator;
    }

    EnumerableSetUpgradeable.AddressSet private routers;
    EnumerableSetUpgradeable.AddressSet private liquidityPairs;

    PaymentAllocation public paymentAllocation;
    DiscountSettings public discountSettings;

    mapping(address => address) public routerForPairAddress;

    uint256 private currentRouterIndex;

    address public lpReceiver;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        string memory _contractName,
        address _ownerAddress,
        address _token,
        uint256 _swapperStandardPrice,
        uint256 _accountBuyLimit,
        address _vaultAddress,
        PaymentAllocation memory _paymentAllocation,
        DiscountSettings memory _discountSettings,
        address _lpReceiver,
        address[] memory _liquidityPairs,
        address[] memory _routers
    ) initializer public virtual {
        __DynamicSwapper_init(_contractName, _ownerAddress, _token, _swapperStandardPrice, _accountBuyLimit,
            _vaultAddress,_paymentAllocation, _discountSettings, _lpReceiver, _liquidityPairs, _routers);
    }

    function __DynamicSwapper_init(
        string memory _contractName,
        address _ownerAddress,
        address _token,
        uint256 _swapperStandardPrice,
        uint256 _accountBuyLimit,
        address _vaultAddress,
        PaymentAllocation memory _paymentAllocation,
        DiscountSettings memory _discountSettings,
        address _lpReceiver,
        address[] memory _liquidityPairs,
        address[] memory _routers
    ) onlyInitializing internal virtual {
        __Swapper_init(_contractName, _ownerAddress, _token, _swapperStandardPrice, _accountBuyLimit, _vaultAddress);
        __DynamicSwapper_init_unchained(_paymentAllocation, _discountSettings, _lpReceiver, _liquidityPairs, _routers);
    }

    function __DynamicSwapper_init_unchained(
        PaymentAllocation memory _paymentAllocation,
        DiscountSettings memory _discountSettings,
        address _lpReceiver,
        address[] memory _liquidityPairs,
        address[] memory _routers
    ) onlyInitializing internal virtual {

        paymentAllocation = _paymentAllocation;
        discountSettings = _discountSettings;

        for(uint256 i = 0; i < _liquidityPairs.length; ++i){
            if(liquidityPairs.add(_liquidityPairs[i])){
                routers.add(_routers[i]);
                routerForPairAddress[_liquidityPairs[i]] = _routers[i];
            }
        }
    }

    receive() external payable virtual override whenNotPaused {
        if(!routers.contains(_msgSender())){
            _receive(_msgSender(), msg.value);
        }
    }

    function _calculateTokenQuantity(address _account, uint256 _value) internal view virtual override returns(uint256 tokenQuantity, uint256 bnbCost){
        return paymentAllocation.swapMode == SwapperMode.STANDARD ? super._calculateTokenQuantity(_account, _value) : _calculateTokenQuantityOnly(_account, _value);
    }

    function _calculateTokenQuantityOnly(address _account, uint256 _value) internal view virtual returns(uint256 tokenQuantity, uint256 bnbCost){
        (, uint256 _pricePerToken) = _getLowestPricedPairAndPrice();
        _pricePerToken = _pricePerToken - (_pricePerToken * discountSettings.discountPercentage / discountSettings.discountPercentageDenominator);
        if(_pricePerToken == 0) {
            revert("Price per token too low");
        }
        tokenQuantity = _value * 10 ** decimals / _pricePerToken;
        uint256 availableTokens = token.balanceOf(address(this));
        tokenQuantity = tokenQuantity > availableTokens ? availableTokens : tokenQuantity;
        if(accountLimit > 0){
            tokenQuantity = tokenQuantity + received[_account] > accountLimit ? accountLimit - received[_account] : tokenQuantity;
        }
        bnbCost = tokenQuantity * _pricePerToken / 10 ** decimals;
    }

    function _getLowestPricedPairAndPrice() internal view returns(address pairAddress, uint256 pairPricePerToken) {
        address[] memory lpPairs = liquidityPairs.values();
        pairAddress = lpPairs[0];
        pairPricePerToken = 0;
        for(uint256 i = 0; i < lpPairs.length; ++i) {
            (uint112 token0, uint112 token1, ) = IUniswapV2Pair(lpPairs[i]).getReserves();

            if(pairPricePerToken > token0 * 10 ** decimals / token1 || pairPricePerToken == 0) {
                pairPricePerToken = token0 * 10 ** decimals / token1;
                pairAddress = lpPairs[i];
            }
        }
    }

    function _distributePayment(uint256 _value) internal virtual override {
        PaymentAllocation memory _paymentAllocation = paymentAllocation;

        // Remove redundant allocations based on swapper mode requirements.
        if(_paymentAllocation.swapMode == SwapperMode.DYNAMIC_BUY_ONLY) {
            _paymentAllocation.lpAllocationPercentage = 0;
        } else if(_paymentAllocation.swapMode == SwapperMode.DYNAMIC_ADD_LIQUIDITY) {
            _paymentAllocation.buyBackAllocationPercentage = 0;
        }

        if(_paymentAllocation.swapMode != SwapperMode.STANDARD &&
            _paymentAllocation.vaultAllocationPercentage +
            _paymentAllocation.buyBackAllocationPercentage +
            _paymentAllocation.lpAllocationPercentage > 0
        ) {
            uint256 originalValue = _value;
            _value = _value * _paymentAllocation.vaultAllocationPercentage /
                (_paymentAllocation.vaultAllocationPercentage +
                _paymentAllocation.lpAllocationPercentage +
                _paymentAllocation.buyBackAllocationPercentage);
            super._distributePayment(_value);

            if(_paymentAllocation.swapMode == SwapperMode.DYNAMIC_BUY_ONLY) {
                _buyTokensFromLP(originalValue - _value);
            } else {
                _value = _paymentAllocation.swapMode == SwapperMode.DYNAMIC_ADD_LIQUIDITY ?
                    (originalValue - _value) / 2 :
                    originalValue *
                    ((_paymentAllocation.lpAllocationPercentage / 2) + _paymentAllocation.buyBackAllocationPercentage) /
                    (_paymentAllocation.vaultAllocationPercentage +
                        _paymentAllocation.lpAllocationPercentage  +
                        _paymentAllocation.buyBackAllocationPercentage);
                _buyTokensFromLP(_value);
                if(address(this).balance >= _paymentAllocation.minBNBToAddLP) {
                    _addLiquidity();
                }
            }
        } else {
            super._distributePayment(_value);
        }
    }

    function _buyTokensFromLP(uint256 _value) private {
        (address pair, ) = _getLowestPricedPairAndPrice();
        IUniswapV2Router02 router = IUniswapV2Router02(routerForPairAddress[pair]);

        address[] memory path = new address[](2);
        path[0] = router.WETH();
        path[1] = address(token);
        if(_value > address(this).balance){
            _value = address(this).balance;
        }

        // make the swap
        router.swapExactETHForTokensSupportingFeeOnTransferTokens{value: _value}(
            0,
            path,
            address(this),
            block.timestamp
        );
    }

    function _addLiquidity() private {
        uint256 routerIndex = (++currentRouterIndex) % routers.length();
        currentRouterIndex = routerIndex;
        IUniswapV2Router02 router = IUniswapV2Router02(routers.at(routerIndex));
        uint256 tokenBalance = token.balanceOf(address(this));
        token.approve(address(router), tokenBalance);

        router.addLiquidityETH{value: address(this).balance}(
            address(token),
            tokenBalance,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            lpReceiver,
            block.timestamp
        );
    }

    function setDynamicBuyAndAddLP() external onlyOwner {
        paymentAllocation.swapMode = SwapperMode.DYNAMIC_BUY_AND_ADD_LIQUIDITY;
    }

    function setDynamicBuyOnly() external onlyOwner {
        paymentAllocation.swapMode = SwapperMode.DYNAMIC_BUY_ONLY;
    }

    function setDynamicAddLP() external onlyOwner {
        paymentAllocation.swapMode = SwapperMode.DYNAMIC_ADD_LIQUIDITY;
    }

    function setDynamicBuyOff() external onlyOwner {
        paymentAllocation.swapMode = SwapperMode.STANDARD;
    }

    function updatePaymentAllocations(
        uint256 _vaultAllocationPercentage,
        uint256 _buyBackAllocationPercentage,
        uint256 _lpAllocationPercentage,
        uint256 _minBNBLPTrigger
    ) external onlyOwner {
        paymentAllocation.vaultAllocationPercentage = _vaultAllocationPercentage;
        paymentAllocation.buyBackAllocationPercentage = _buyBackAllocationPercentage;
        paymentAllocation.lpAllocationPercentage = _lpAllocationPercentage;
        paymentAllocation.minBNBToAddLP = _minBNBLPTrigger;
    }

    function updateDiscountSettings(uint256 _discountPercentage, uint256 _discountPercentageDenominator) external onlyOwner {
        if(_discountPercentage > _discountPercentageDenominator || _discountPercentageDenominator == 0){
            revert("Discount cannot be greater than denominator and denominator cannot be zero");
        }
        discountSettings = DiscountSettings({
            discountPercentage : _discountPercentage,
            discountPercentageDenominator : _discountPercentageDenominator
        });
    }

    function forceRemoveRouter(address _router) external onlyOwner {
        if(!routers.remove(_router)){
            revert("Router not in list.");
        }
    }

    function addOrRemovePairAddress(address _lpAddress, address _routerForPair, bool _isValidPair) external onlyOwner {
        if(_isValidPair){
            if(liquidityPairs.add(_lpAddress)){
                routers.add(_routerForPair);
                routerForPairAddress[_lpAddress] = _routerForPair;
                return;
            }
        } else {
            if(liquidityPairs.remove(_lpAddress)) {
                routerForPairAddress[_lpAddress] = address(0);
                return;
            }
        }
        revert("Pair already set to requested state");
    }

    function updateMinBNBToAddLP(uint256 _minBNBIncDecimals) external onlyOwner {
        paymentAllocation.minBNBToAddLP = _minBNBIncDecimals;
    }

    function getRouters() external view returns(address[] memory routerAddresses) {
        routerAddresses = routers.values();
    }

    function getRouterAtIndex(uint256 _index) external view returns(address routerAddress) {
        routerAddress = routers.at(_index);
    }

    function getRouterCount() external view returns(uint256 routerCount) {
        routerCount = routers.length();
    }

    function getLiquidityPairs() external view returns(address[] memory pairAddresses) {
        pairAddresses = liquidityPairs.values();
    }

    function getLiquidityPairAtIndex(uint256 _index) external view returns(address pairAddress) {
        pairAddress = liquidityPairs.at(_index);
    }

    function getLiquidityPairCount() external view returns(uint256 pairCount) {
        pairCount = liquidityPairs.length();
    }

    function updateLPReceiver(address _lpReceiver) external onlyOwner {
        lpReceiver = _lpReceiver;
    }

    function getDynamicBuySetting() external view returns(string memory) {
        if(paymentAllocation.swapMode == SwapperMode.DYNAMIC_BUY_AND_ADD_LIQUIDITY) {
            return "Buy and add Liquidity";
        } else if(paymentAllocation.swapMode == SwapperMode.DYNAMIC_BUY_ONLY) {
            return "Buy Only";
        } else if(paymentAllocation.swapMode == SwapperMode.DYNAMIC_ADD_LIQUIDITY) {
            return "Add Liquidity Only";
        }
        return "Static Price Swap";
    }
}
